// src/components/sections/Creator.js
import React from 'react';
import styled from 'styled-components';
import Layout from '../layout/Layout';

const CreatorContainer = styled.section`
  padding: ${props => props.theme.spacing.xl} 0;
`;

const CreatorHeader = styled.div`
  margin-bottom: ${props => props.theme.spacing.xl};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -${props => props.theme.spacing.md};
    left: 0;
    width: 100px;
    height: 2px;
    background-color: ${props => props.theme.colors.primary};
  }
`;

const Title = styled.h1`
  margin-bottom: ${props => props.theme.spacing.xs};
`;

const Subtitle = styled.p`
  font-size: 1.25rem;
  color: ${props => props.theme.colors.primaryLight};
  max-width: 600px;
`;

const CreatorGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: ${props => props.theme.spacing.xl};

  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    grid-template-columns: 1fr 2fr;
  }
`;

const ImageContainer = styled.div`
  position: relative;
  overflow: hidden;
  border-radius: ${props => props.theme.borderRadius.medium};
  box-shadow: ${props => props.theme.shadows.medium};

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: 1px solid ${props => props.theme.colors.primary};
    z-index: 1;
    pointer-events: none;
  }
`;

const CreatorImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform ${props => props.theme.transitions.slow};

  &:hover {
    transform: scale(1.05);
  }
`;

const ContentContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const Bio = styled.div`
  margin-bottom: ${props => props.theme.spacing.lg};
`;

const Paragraph = styled.p`
  margin-bottom: ${props => props.theme.spacing.md};
  font-size: 1.1rem;
  line-height: 1.7;
`;

const CredentialsSection = styled.div`
  margin-top: ${props => props.theme.spacing.lg};
  padding-top: ${props => props.theme.spacing.lg};
  border-top: 1px solid rgba(160, 142, 97, 0.2);
`;

const CredentialsTitle = styled.h3`
  color: ${props => props.theme.colors.primary};
  margin-bottom: ${props => props.theme.spacing.md};
`;

const CredentialsList = styled.ul`
  margin-left: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.lg};
`;

const Credential = styled.li`
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const ContactSection = styled.div`
  margin-top: auto;
  display: flex;
  flex-wrap: wrap;
  gap: ${props => props.theme.spacing.md};
`;

const ContactLink = styled.a`
  display: inline-flex;
  align-items: center;
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  background-color: ${props => props.theme.colors.surfaceLight};
  border: 1px solid ${props => props.theme.colors.primary};
  border-radius: ${props => props.theme.borderRadius.medium};
  color: ${props => props.theme.colors.primary};
  transition: all ${props => props.theme.transitions.default};

  &:hover {
    background-color: ${props => props.theme.colors.primary};
    color: ${props => props.theme.colors.background};
  }

  svg {
    margin-right: ${props => props.theme.spacing.sm};
  }
`;

const Creator = () => {
  return (
    <Layout>
      <CreatorContainer>
        <CreatorHeader>
          <Title>About the Creator</Title>
          <Subtitle>
            The visionary mind behind Crescent's dark fantasy world
          </Subtitle>
        </CreatorHeader>

        <CreatorGrid>
          <ImageContainer>
            <CreatorImage 
              src="/assets/images/creator-portrait.jpg" 
              alt="Rosalia Fialkova"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/400x600?text=Rosalia+Fialkova';
              }}
            />
          </ImageContainer>

          <ContentContainer>
            <Bio>
              <Paragraph>
                Rosalia Fialkova is a game designer, writer, and creative director with a passion for crafting immersive dark fantasy worlds. With Crescent, she has created a unique tabletop roleplaying experience that explores themes of sacrifice, ambition, and the cyclical nature of existence.
              </Paragraph>

              <Paragraph>
                Drawing inspiration from works like Kentaro Miura's "Berserk," Akihito Tsukushi's "Made in Abyss," and FromSoftware's "Bloodborne," Rosalia's design philosophy centers on creating emotionally resonant experiences where players must navigate the tension between their dreams and the dire cost of realizing them.
              </Paragraph>

              <Paragraph>
                Her background in interactive narrative design and systems thinking has shaped Crescent's unique mechanics, where player choices have meaningful consequences and the world responds organically to their actions.
              </Paragraph>
            </Bio>

            <CredentialsSection>
              <CredentialsTitle>Game Design Experience</CredentialsTitle>
              <CredentialsList>
                <Credential>Lead Designer on Crescent, a dark fantasy tabletop RPG</Credential>
                <Credential>Graphic designer for indie projects</Credential>
                <Credential>Systems designer specializing in narrative mechanics</Credential>
                <Credential>Adoration of evolution simulators.</Credential>
              </CredentialsList>
            </CredentialsSection>

            <ContactSection>
              <ContactLink href="https://twitter.com/rosaliafialkova" target="_blank" rel="noopener noreferrer">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M23 3a10.9 10.9 0 0 1-3.14 1.53 4.48 4.48 0 0 0-7.86 3v1A10.66 10.66 0 0 1 3 4s-4 9 5 13a11.64 11.64 0 0 1-7 2c9 5 20 0 20-11.5a4.5 4.5 0 0 0-.08-.83A7.72 7.72 0 0 0 23 3z"></path>
                </svg>
                Twitter
              </ContactLink>

              <ContactLink href="https://github.com/rosaliafialkova" target="_blank" rel="noopener noreferrer">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M9 19c-5 1.5-5-2.5-7-3m14 6v-3.87a3.37 3.37 0 0 0-.94-2.61c3.14-.35 6.44-1.54 6.44-7A5.44 5.44 0 0 0 20 4.77 5.07 5.07 0 0 0 19.91 1S18.73.65 16 2.48a13.38 13.38 0 0 0-7 0C6.27.65 5.09 1 5.09 1A5.07 5.07 0 0 0 5 4.77a5.44 5.44 0 0 0-1.5 3.78c0 5.42 3.3 6.61 6.44 7A3.37 3.37 0 0 0 9 18.13V22"></path>
                </svg>
                GitHub
              </ContactLink>

              <ContactLink href="mailto:contact@rosaliafialkova.com">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
                  <polyline points="22,6 12,13 2,6"></polyline>
                </svg>
                Email
              </ContactLink>
            </ContactSection>
          </ContentContainer>
        </CreatorGrid>
      </CreatorContainer>
    </Layout>
  );
};

export default Creator;
