      ctx.fillStyle = 'rgb(15, 15, 20)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }

    // Update dimensions ref instead of state
    dimensionsRef.current = { width, height, pixelRatio };

    return true;
  }, []);

