import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';

/**
 * MeteorShower Component - Performance Optimized Version
 * 
 * Creates an animated meteor shower effect where meteors arc across the sky
 * with comet-like trails and fade away naturally.
 * Features staggered appearance and configurable journey completion.
 * 
 * @param {Object} props - Component props
 * @param {string|number} [props.height='100vh'] - Height of the container
 * @param {number} [props.zIndex=5] - Z-index for the container
 * @param {boolean} [props.active=true] - Whether the animation is active
 * @param {number} [props.meteorDensity=15] - Number of meteors to maintain on screen
 * @param {number} [props.meteorMinSize=1] - Minimum size of meteors
 * @param {number} [props.meteorMaxSize=3] - Maximum size of meteors
 * @param {number} [props.meteorSpeed=0.08] - Base speed of meteors
 * @param {number} [props.trailLength=180] - Length of meteor trails
 * @param {number} [props.trailSegments=20] - Number of segments in each trail
 * @param {string} [props.coreColor='rgba(255, 255, 255, 1)'] - Core color for meteors
 * @param {string} [props.glowColor='rgba(255, 253, 227, 0.9)'] - Glow color for meteors
 * @param {string} [props.trailColor='rgba(191, 173, 127, 0.8)'] - Trail color for meteors
 * @param {boolean} [props.enableParallax=false] - Enable parallax effect on scroll
 * @param {number} [props.parallaxIntensity=0.2] - Intensity of parallax effect
 * @param {boolean} [props.staggered=true] - Enable staggered meteor appearance
 * @param {number} [props.minStaggerDelay=200] - Minimum delay between meteor spawns (ms)
 * @param {number} [props.maxStaggerDelay=2000] - Maximum delay between meteor spawns (ms)
 * @param {number} [props.journeyCompletion=0.9] - When meteors complete their journey (0-1, where 1 is the container height)
 * @param {string} [props.mode='arc'] - Animation mode: 'arc' (curved paths) or 'linear' (straight angled paths)
 * @param {string} [props.direction='both'] - Direction for linear mode: 'left', 'right', 'both', or 'top'
 * @param {number} [props.baseAngle=30] - Base angle for linear meteors (degrees)
 * @param {number} [props.angleVariation=15] - Random variation to apply to the base angle (±degrees)
 * @param {boolean} [props.debug=false] - Enable debug visualization
 * @param {boolean} [props.adaptiveQuality=true] - Enable adaptive quality based on device performance
 * @param {boolean} [props.respectReducedMotion=true] - Respect user's reduced motion preferences
 * @param {number} [props.maxFPS=60] - Target maximum frames per second
 */
const MeteorShower = ({
  height = '100vh',
  zIndex = 5,
  active = true,
  meteorDensity = 15,
  meteorMinSize = 1,
  meteorMaxSize = 3,
  meteorSpeed = 0.08,
  trailLength = 180,
  trailSegments = 20,
  coreColor = 'rgba(255, 255, 255, 1)',
  glowColor = 'rgba(255, 253, 227, 0.9)',
  trailColor = 'rgba(191, 173, 127, 0.8)',
  enableParallax = false,
  parallaxIntensity = 0.2,
  staggered = true,
  minStaggerDelay = 200,
  maxStaggerDelay = 2000,
  journeyCompletion = 0.9,
  mode = 'arc',
  direction = 'both',
  baseAngle = 30,
  angleVariation = 15,
  debug = false,
  adaptiveQuality = true,
  respectReducedMotion = true,
  maxFPS = 60
}) => {
  // Refs for DOM elements and animation state
  const containerRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const meteorsRef = useRef([]);
  const animationFrameRef = useRef(null);
  const lastTimestampRef = useRef(0);
  const fpsTimestampRef = useRef(0);
  const frameCountRef = useRef(0);
  const currentFpsRef = useRef(60);
  const nextSpawnTimeRef = useRef(0);
  const scrollPositionRef = useRef(0);
  const resizeObserverRef = useRef(null);

  // Component state
  const [dimensions, setDimensions] = useState({ width: 0, height: 0, pixelRatio: 1 });
  const [isInitialized, setIsInitialized] = useState(false);
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [qualityFactor, setQualityFactor] = useState(1);

  // Pre-allocate objects to avoid garbage collection during animation
  const pointCache = useRef({
    current: { x: 0, y: 0 },
    segment: { x: 0, y: 0 }
  }).current;

  // Precompute color variants to avoid string operations during animation
  const colorCacheRef = useRef(new Map());

  // Get cached color with opacity
  const getCachedColor = useCallback((baseColor, opacity) => {
    // Round opacity to reduce cache size while maintaining visual quality
    const roundedOpacity = Math.round(opacity * 100) / 100;
    const key = `${baseColor}-${roundedOpacity}`;

    if (!colorCacheRef.current.has(key)) {
      const newColor = baseColor.replace(/[\d.]+\)$/, roundedOpacity + ')');
      colorCacheRef.current.set(key, newColor);
    }

    return colorCacheRef.current.get(key);
  }, []);

  // Detect device capabilities and preferences
  useEffect(() => {
    // Check for reduced motion preference
    const reducedMotionQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setPrefersReducedMotion(reducedMotionQuery.matches);

    const handleReducedMotionChange = (e) => {
      setPrefersReducedMotion(e.matches);
    };

    reducedMotionQuery.addEventListener('change', handleReducedMotionChange);

    // Detect quality level based on device
    const detectQuality = () => {
      // Use a combination of device memory, hardware concurrency, and pixel ratio to estimate performance
      const memory = navigator.deviceMemory || 4; // Default to 4GB if not available
      const cores = navigator.hardwareConcurrency || 4; // Default to 4 cores
      const pixelRatio = window.devicePixelRatio || 1;

      // Calculate performance score
      const performanceScore = (memory * cores) / pixelRatio;

      // Scale quality based on performance score
      let quality = 1;

      if (performanceScore > 16) {
        quality = 1; // High-end devices: full quality
      } else if (performanceScore > 8) {
        quality = 0.8; // Mid-range devices: 80% quality
      } else if (performanceScore > 4) {
        quality = 0.6; // Low-end devices: 60% quality
      } else {
        quality = 0.4; // Very low-end: 40% quality
      }

      // Check if it's a mobile device and reduce quality further if needed
      if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
        quality *= 0.8;
      }

      return quality;
    };

    if (adaptiveQuality) {
      setQualityFactor(detectQuality());
    }

    return () => {
      reducedMotionQuery.removeEventListener('change', handleReducedMotionChange);
    };
  }, [adaptiveQuality]);

  // Apply adaptive quality settings
  const adaptedSettings = useMemo(() => {
    if (!adaptiveQuality || qualityFactor === 1) {
      return {
        meteorDensity,
        trailSegments
      };
    }

    // Adjust quality-dependent parameters
    return {
      meteorDensity: Math.max(3, Math.floor(meteorDensity * qualityFactor)),
      trailSegments: Math.max(5, Math.floor(trailSegments * qualityFactor))
    };
  }, [adaptiveQuality, qualityFactor, meteorDensity, trailSegments]);

  // Calculate path parameters for a meteor based on selected mode
  const calculateMeteorPath = useCallback((width, height) => {
    if (mode === 'arc') {
      // Original arc path logic
      // Start point: somewhere along the top of the canvas with randomization
      const startX = Math.random() * width * 1.5 - width * 0.25;

      // Control how far meteors can start from outside the visible area
      const startY = Math.random() * -100 - 50;

      // Randomize arc curvature and direction
      const curveDirection = Math.random() > 0.5 ? 1 : -1;
      const curveIntensity = Math.random() * 0.4 + 0.2;

      // Calculate control points for quadratic path
      // End point: adjusted to complete journey according to journeyCompletion parameter
      const endX = startX + (curveDirection * width * curveIntensity);
      const endY = height * journeyCompletion;

      // Control point: creates the arc shape
      const controlX = (startX + endX) / 2 + (curveDirection * width * curveIntensity);
      const controlY = (startY + endY) * 0.5;

      return {
        pathType: 'arc',
        start: { x: startX, y: startY },
        control: { x: controlX, y: controlY },
        end: { x: endX, y: endY }
      };
    } else if (mode === 'linear') {
      // Linear path logic for traditional meteor shower

      // Determine direction (from left, right, top, or random)
      let meteorDirection = direction;
      if (direction === 'both') {
        meteorDirection = Math.random() > 0.5 ? 'left' : 'right';
      }

      // Calculate angle with variation (convert to radians)
      let angle;

      if (direction === 'top') {
        // For 'top' direction, maintain consistent 30 degree downward angle to the right
        // with minimal variation to keep them all falling in a similar direction
        angle = ((30 + (Math.random() * 2 - 1) * 5) * Math.PI) / 180;
      } else {
        angle = ((baseAngle + (Math.random() * 2 - 1) * angleVariation) * Math.PI) / 180;
      }

      // Set starting positions based on direction
      let startX;
      if (meteorDirection === 'left') {
        // Start from top-left portion of the screen
        startX = Math.random() * (width * 0.3) - (width * 0.1);
      } else if (meteorDirection === 'right') {
        // Start from top-right portion of the screen
        startX = width - Math.random() * (width * 0.3) + (width * 0.1);
      } else if (direction === 'top') {
        // For 'top' direction, distribute meteors across the top with emphasis on left and center
        const position = Math.random();
        if (position < 0.5) {
          // 50% chance: top-left
          startX = Math.random() * (width * 0.35);
        } else if (position < 0.85) {
          // 35% chance: top-center
          startX = width * 0.35 + Math.random() * (width * 0.35);
        } else {
          // 15% chance: top-right
          startX = width * 0.7 + Math.random() * (width * 0.3);
        }
      }

      // Start slightly above the screen
      const startY = Math.random() * -100 - 50;

      // Calculate distance meteor will travel based on angle and screen dimensions
      const distanceToTravel = (height * journeyCompletion) / Math.cos(angle);

      // Calculate end position using angle
      let endX, endY;
      if (meteorDirection === 'left' || direction === 'top') {
        // Meteor moves from left to right (or top to bottom-right for 'top' direction)
        endX = startX + distanceToTravel * Math.sin(angle);
        endY = startY + distanceToTravel * Math.cos(angle);
      } else {
        // Meteor moves from right to left
        endX = startX - distanceToTravel * Math.sin(angle);
        endY = startY + distanceToTravel * Math.cos(angle);
      }

      return {
        pathType: 'linear',
        start: { x: startX, y: startY },
        end: { x: endX, y: endY },
        direction: meteorDirection
      };
    }
  }, [mode, direction, baseAngle, angleVariation, journeyCompletion]);

  // Calculate position along a path - optimized implementation
  const getPathPoint = useCallback((t, path, outPoint = { x: 0, y: 0 }) => {
    if (path.pathType === 'arc') {
      // Optimized quadratic bezier calculation
      const invT = 1 - t;
      const invTSquared = invT * invT;
      const tSquared = t * t;
      const termA = invTSquared;
      const termB = 2 * invT * t;
      const termC = tSquared;

      outPoint.x = termA * path.start.x + termB * path.control.x + termC * path.end.x;
      outPoint.y = termA * path.start.y + termB * path.control.y + termC * path.end.y;
      return outPoint;
    } else {
      // Optimized linear interpolation
      outPoint.x = path.start.x + (path.end.x - path.start.x) * t;
      outPoint.y = path.start.y + (path.end.y - path.start.y) * t;
      return outPoint;
    }
  }, []);

  // Initialize canvas with proper resolution
  const setupCanvas = useCallback(() => {
    if (!canvasRef.current || !containerRef.current) return false;

    const canvas = canvasRef.current;
    const container = containerRef.current;
    const rect = container.getBoundingClientRect();
    const pixelRatio = window.devicePixelRatio || 1;

    // Adjust for pixel density (Retina/high-DPI displays)
    const displayWidth = rect.width;
    const displayHeight = typeof height === 'string' && height.endsWith('vh') 
      ? (parseInt(height, 10) / 100) * window.innerHeight
      : parseInt(height, 10) || window.innerHeight;

    // Set canvas rendering size (accounting for pixel ratio)
    canvas.width = displayWidth * pixelRatio;
    canvas.height = displayHeight * pixelRatio;

    // Set display size via CSS
    canvas.style.width = `${displayWidth}px`;
    canvas.style.height = `${displayHeight}px`;

    // Get and configure context
    const ctx = canvas.getContext('2d', {
      alpha: true,
      desynchronized: true, // Enable desynchronized context for better performance
      willReadFrequently: false
    });

    // Set high-quality rendering with proper scaling
    ctx.scale(pixelRatio, pixelRatio);

    // Enable high-quality anti-aliasing and better gamma handling
    if (ctx.imageSmoothingEnabled !== undefined) {
      ctx.imageSmoothingEnabled = true;
      ctx.imageSmoothingQuality = 'high';
    }

    // Store the context for later use
    ctxRef.current = ctx;

    setDimensions({
      width: displayWidth,
      height: displayHeight,
      pixelRatio
    });

    return true;
  }, [height]);

  // Setup canvas resize observer
  useEffect(() => {
    if (!containerRef.current) return;

    // Create a ResizeObserver to detect container size changes
    const resizeObserver = new ResizeObserver(() => {
      // Use requestAnimationFrame to avoid simultaneous layout and rendering
      requestAnimationFrame(() => {
        if (setupCanvas()) {
          setIsInitialized(true);
        }
      });
    });

    // Start observing the container
    resizeObserver.observe(containerRef.current);
    resizeObserverRef.current = resizeObserver;

    // Initial setup
    setupCanvas();
    setIsInitialized(true);

    return () => {
      if (resizeObserverRef.current) {
        resizeObserverRef.current.disconnect();
      }
    };
  }, [setupCanvas]);

  // Setup intersection observer to only animate when visible
  useEffect(() => {
    if (!containerRef.current) return;

    const observer = new IntersectionObserver(
      (entries) => {
        // Use the most recent entry
        const entry = entries[entries.length - 1];
        setIsVisible(entry.isIntersecting);
      },
      {
        threshold: 0.01,
        rootMargin: '100px'
      }
    );

    observer.observe(containerRef.current);

    return () => {
      observer.disconnect();
    };
  }, []);

  // Handle parallax effect on scroll if enabled
  useEffect(() => {
    if (!enableParallax) return;

    const handleScroll = () => {
      scrollPositionRef.current = window.scrollY;
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [enableParallax]);

  // Initialize meteor with pooling
  const initializeMeteor = useCallback(() => {
    const { width, height } = dimensions;

    if (!width || !height) return null;

    // Calculate path based on mode
    const path = calculateMeteorPath(width, height);

    // Randomize meteor properties
    const baseSpeed = meteorSpeed * (Math.random() * 0.5 + 0.75);

    // Use optimal array for storing positions
    // Pre-allocate the array with the right size to avoid resizing
    const positions = Array(adaptedSettings.trailSegments);
    for (let i = 0; i < adaptedSettings.trailSegments; i++) {
      positions[i] = { x: path.start.x, y: path.start.y };
    }

    return {
      path,
      progress: 0,
      size: Math.random() * (meteorMaxSize - meteorMinSize) + meteorMinSize,
      speed: baseSpeed,
      active: true,
      positions,
      opacity: Math.random() * 0.3 + 0.7,
      fadeThreshold: 0.7 + Math.random() * 0.2, // When to start fading (70-90% of journey)
      pulsePhase: Math.random() * Math.PI * 2,
      pulseSpeed: Math.random() * 0.01 + 0.005
    };
  }, [
    dimensions,
    meteorSpeed,
    meteorMinSize,
    meteorMaxSize,
    calculateMeteorPath,
    adaptedSettings.trailSegments
  ]);

  // FPS limiter for consistent animation speed
  const fpsLimiter = useCallback((timestamp, callback) => {
    const targetFrameTime = 1000 / maxFPS;
    const elapsed = timestamp - lastTimestampRef.current;

    if (elapsed >= targetFrameTime) {
      lastTimestampRef.current = timestamp - (elapsed % targetFrameTime);
      callback(elapsed);

      // FPS calculation for debug and adaptive quality
      frameCountRef.current++;
      if (timestamp - fpsTimestampRef.current >= 1000) {
        currentFpsRef.current = frameCountRef.current;
        frameCountRef.current = 0;
        fpsTimestampRef.current = timestamp;

        // Adjust quality based on FPS if adaptive quality is enabled
        if (adaptiveQuality && debug) {
          console.log(`Current FPS: ${currentFpsRef.current}, Quality Factor: ${qualityFactor}`);
        }
      }
    }

    animationFrameRef.current = requestAnimationFrame((nextTimestamp) => {
      fpsLimiter(nextTimestamp, callback);
    });
  }, [maxFPS, adaptiveQuality, debug, qualityFactor]);

  // Draw a meteor with optimized rendering
  const drawMeteor = useCallback((ctx, meteor, timestamp, deltaTime, parallaxOffset) => {
    // Calculate opacity based on progress
    // Start fading out after reaching the fadeThreshold
    let currentOpacity = meteor.opacity;
    if (meteor.progress > meteor.fadeThreshold) {
      // Map progress from fadeThreshold-1.0 to 1.0-0.0 for opacity
      const fadeProgress = (meteor.progress - meteor.fadeThreshold) / (1 - meteor.fadeThreshold);
      currentOpacity = meteor.opacity * (1 - fadeProgress);
    }

    // Apply pulse/flicker effect
    const timeFactor = timestamp * 0.001;
    const pulseEffect = Math.sin(timeFactor * meteor.pulseSpeed + meteor.pulsePhase) * 0.2 + 0.8;

    // Adjust for parallax if enabled
    const adjustY = enableParallax ? parallaxOffset * (meteor.size / meteorMaxSize) : 0;

    // Get starting opacity and width for optimization
    let segmentOpacity, segmentWidth;

    // Batch shadow operations to improve performance
    ctx.shadowColor = glowColor;
    ctx.shadowBlur = meteor.size * 3 * pulseEffect;
    ctx.lineCap = 'round';

    // Draw trail segments in reverse for proper layering
    // This is more efficient than changing globalCompositeOperation
    for (let i = meteor.positions.length - 2; i >= 0; i--) {
      const pos1 = meteor.positions[i];
      const pos2 = meteor.positions[i + 1];

      if (!pos1 || !pos2) continue;

      // Calculate segment opacity (decreases along the trail)
      segmentOpacity = currentOpacity * (1 - i / meteor.positions.length) * pulseEffect;

      // Skip if nearly invisible
      if (segmentOpacity < 0.02) continue;

      // Calculate segment width (decreases along the trail)
      segmentWidth = meteor.size * (1 - i / meteor.positions.length * 0.7);

      // Draw line segment
      ctx.beginPath();
      ctx.moveTo(pos1.x, pos1.y + adjustY);
      ctx.lineTo(pos2.x, pos2.y + adjustY);

      // Set line style
      ctx.lineWidth = segmentWidth;
      ctx.strokeStyle = getCachedColor(trailColor, segmentOpacity);
      ctx.stroke();
    }

    // Draw meteor head (brightest part)
    if (meteor.positions[0]) {
      const headPos = meteor.positions[0];

      // Set shadow/glow for head
      ctx.shadowColor = glowColor;
      ctx.shadowBlur = meteor.size * 5 * pulseEffect;

      // Draw outer glow
      ctx.beginPath();
      ctx.arc(headPos.x, headPos.y + adjustY, meteor.size * 1.5, 0, Math.PI * 2);
      ctx.fillStyle = getCachedColor(glowColor, currentOpacity * 0.7 * pulseEffect);
      ctx.fill();

      // Draw inner core
      ctx.beginPath();
      ctx.arc(headPos.x, headPos.y + adjustY, meteor.size * 0.7, 0, Math.PI * 2);
      ctx.fillStyle = getCachedColor(coreColor, currentOpacity * pulseEffect);
      ctx.fill();
    }
  }, [
    enableParallax, 
    meteorMaxSize, 
    glowColor, 
    trailColor, 
    coreColor,
    getCachedColor
  ]);

  // Main animation loop
  useEffect(() => {
    if (!active || !isInitialized || !isVisible || !dimensions.width || !dimensions.height) {
      return;
    }

    if (respectReducedMotion && prefersReducedMotion) {
      // Skip animation if user prefers reduced motion
      return;
    }

    const ctx = ctxRef.current;
    if (!ctx) return;

    // Animation handler function
    const handleAnimation = (deltaTime) => {
      const width = dimensions.width;
      const height = dimensions.height;

      // Initialize lastTimestamp on first run
      if (lastTimestampRef.current === 0) {
        nextSpawnTimeRef.current = performance.now();
      }

      // Apply parallax offset
      let parallaxOffset = 0;
      if (enableParallax) {
        parallaxOffset = scrollPositionRef.current * parallaxIntensity;
      }

      // Clear canvas - optimize by only clearing the used portion
      ctx.clearRect(0, 0, width, height);

      // Debug mode - draw canvas bounds
      if (debug) {
        ctx.strokeStyle = 'rgba(255, 0, 0, 0.5)';
        ctx.lineWidth = 2;
        ctx.strokeRect(0, 0, width, height);

        ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
        ctx.font = '12px monospace';
        ctx.fillText(`Canvas: ${width}×${height}`, 10, 20);
        ctx.fillText(`Meteors: ${meteorsRef.current.length}/${adaptedSettings.meteorDensity}`, 10, 40);
        ctx.fillText(`Mode: ${mode}`, 10, 60);
        ctx.fillText(`Direction: ${direction}`, 10, 80);
        ctx.fillText(`FPS: ${currentFpsRef.current}`, 10, 100);
        ctx.fillText(`Quality: ${qualityFactor.toFixed(2)}`, 10, 120);
        if (staggered) {
          ctx.fillText(`Next Spawn: ${Math.max(0, (nextSpawnTimeRef.current - performance.now()) / 1000).toFixed(2)}s`, 10, 140);
        }
      }

      // Spawn new meteors if needed, with staggering if enabled
      const now = performance.now();
      if (meteorsRef.current.length < adaptedSettings.meteorDensity) {
        const canSpawnNow = !staggered || now >= nextSpawnTimeRef.current;

        if (canSpawnNow) {
          const newMeteor = initializeMeteor();
          if (newMeteor) {
            meteorsRef.current.push(newMeteor);

            // If staggering is enabled, set the next spawn time
            if (staggered) {
              const delay = Math.random() * (maxStaggerDelay - minStaggerDelay) + minStaggerDelay;
              nextSpawnTimeRef.current = now + delay;
            }
          }
        }
      }

      // Object pool for position calculations to reduce garbage collection
      const segment = { x: 0, y: 0 };

      // Update and draw meteors
      let i = 0;
      while (i < meteorsRef.current.length) {
        const meteor = meteorsRef.current[i];

        // Skip if not active
        if (!meteor.active) {
          meteorsRef.current.splice(i, 1);
          continue;
        }

        // Update progress based on speed and delta time
        meteor.progress += meteor.speed * (deltaTime / 1000);

        // Check if meteor has completed its path
        if (meteor.progress >= 1) {
          meteorsRef.current.splice(i, 1);
          continue;
        }

        // Calculate current position along the path
        const currentPos = getPathPoint(Math.min(1, meteor.progress), meteor.path, segment);

        // Update position history (for trail) using object pooling
        // Create a new point object to store the current position
        const newPos = { x: currentPos.x, y: currentPos.y };

        // Shift positions array - this is more efficient than splice/unshift
        for (let j = meteor.positions.length - 1; j > 0; j--) {
          meteor.positions[j] = meteor.positions[j - 1];
        }
        meteor.positions[0] = newPos;

        // Draw the meteor
        drawMeteor(ctx, meteor, now, deltaTime, parallaxOffset);

        // Move to next meteor
        i++;
      }
    };

    // Start animation with FPS limiter
    fpsTimestampRef.current = performance.now();
    frameCountRef.current = 0;
    lastTimestampRef.current = 0;

    animationFrameRef.current = requestAnimationFrame((timestamp) => {
      fpsLimiter(timestamp, handleAnimation);
    });

    // Cleanup
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [
    active,
    isInitialized,
    isVisible,
    dimensions,
    mode,
    direction,
    enableParallax,
    parallaxIntensity,
    staggered,
    minStaggerDelay,
    maxStaggerDelay,
    trailSegments,
    getPathPoint,
    initializeMeteor,
    fpsLimiter,
    drawMeteor,
    debug,
    prefersReducedMotion,
    respectReducedMotion,
    adaptedSettings.meteorDensity,
    qualityFactor
  ]);

  return (
    <div 
      ref={containerRef}
      style={{ 
        position: 'absolute',
        top: 0, 
        left: 0, 
        width: '100%', 
        height: height,
        overflow: 'hidden', 
        pointerEvents: 'none', 
        zIndex: zIndex,
        // Hardware acceleration
        backfaceVisibility: 'hidden',
        transform: 'translateZ(0)',
        willChange: 'transform'
      }}
      aria-hidden="true"
    >
      <canvas 
        ref={canvasRef} 
        style={{ 
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%',
          // Additional rendering optimizations
          imageRendering: 'high-quality',
          // Hardware acceleration
          backfaceVisibility: 'hidden',
          transform: 'translateZ(0)',
          willChange: 'transform'
        }} 
      />
    </div>
  );
};

