import React, { useRef, useState, useEffect, useCallback, useMemo } from 'react';
import { motion, useInView } from 'framer-motion';

/**
 * PersonProfileCard Component - Optimized Version
 * 
 * A profile card component with simplified scrolling behavior:
 * - Uses CSS position: sticky for reliable, smooth scrolling
 * - Removed complex animation calculations that caused positioning issues
 * - Removed all image references and comet effects
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data (name, role, bio, stats, etc.)
 * @param {Object} props.animationConfig - Animation configuration
 * @param {Array} props.additionalSections - Additional content sections
 * @param {Function} props.onSectionChange - Callback when active section changes
 * @param {number} props.topOffset - Offset from top for sticky position
 * @param {boolean} props.showStats - Whether to show statistics section
 * @param {string} props.highlightColor - Color for active/hover elements
 * @param {string} props.textColor - Default text color
 * @param {number} props.minLineWidth - Minimum width for nav indicator lines
 * @param {number} props.maxLineWidth - Maximum width for nav indicator lines
 * @param {string} props.fontFamily - Font family for text elements
 * @param {Object} props.navigationItems - Custom navigation items with IDs and labels
 * @param {number} props.contentCompression - Adjusts spacing between sidebar and content (0-10)
 */
const PersonProfileCard = ({
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  additionalSections = [],
  onSectionChange = null,
  topOffset = 100,
  showStats = true,
  highlightColor = '#bfad7f',
  textColor = 'rgba(224, 224, 224, 0.7)',
  minLineWidth = 10,
  maxLineWidth = 40,
  fontFamily = '"Garamond", "Adobe Caslon Pro", serif',
  navigationItems = [
    { id: 'about', label: 'ABOUT', content: null },
    { id: 'experience', label: 'EXPERIENCE', content: null },
    { id: 'projects', label: 'PROJECTS', content: null }
  ],
  contentCompression = 0
}) => {
  // State management
  const [activeSection, setActiveSection] = useState(navigationItems[0]?.id || 'about');
  const [expandedNavItem, setExpandedNavItem] = useState(null);

  // Calculate widths based on contentCompression (0-10 scale)
  const compressionFactor = Math.min(Math.max(contentCompression, 0), 10) / 10;
  const sidebarWidth = 45 - (compressionFactor * 5); // 35-40% range
  const contentWidth = 55 - (compressionFactor * 5); // 55-60% range
  const contentPadding = 2 + (compressionFactor * 2); // 2-4rem range

  // Refs for DOM elements
  const containerRef = useRef(null);

  // Create refs for sections at the top level to follow React Hooks rules
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);
  const section3Ref = useRef(null);
  const section4Ref = useRef(null);
  const section5Ref = useRef(null);

  // Create a mapping of section IDs to refs using useMemo to prevent recreation on every render
  const sectionRefs = useMemo(() => {
    const refs = {};
    navigationItems.forEach((item, index) => {
      switch(index) {
        case 0: refs[item.id] = section1Ref; break;
        case 1: refs[item.id] = section2Ref; break;
        case 2: refs[item.id] = section3Ref; break;
        case 3: refs[item.id] = section4Ref; break;
        case 4: refs[item.id] = section5Ref; break;
        default: break;
      }
    });
    return refs;
  }, [navigationItems, section1Ref, section2Ref, section3Ref, section4Ref, section5Ref]);

  // Animation inView detection
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Handle navigation item hover
  const handleNavItemHover = useCallback((section) => {
    setExpandedNavItem(section);
  }, []);

  const handleNavItemLeave = useCallback(() => {
    setExpandedNavItem(null);
  }, []);

  // Scroll to section handler with improved scrolling behavior
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior and offset
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      const yOffset = -topOffset - 20; // Additional 20px buffer
      const element = sectionRef.current;
      const y = element.getBoundingClientRect().top + window.pageYOffset + yOffset;

      window.scrollTo({
        top: y,
        behavior: 'smooth'
      });
    }
  }, [onSectionChange, sectionRefs, topOffset]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    const sectionElements = Object.values(sectionRefs)
      .map(ref => ref.current)
      .filter(Boolean);

    if (sectionElements.length === 0) return;

    const options = {
      root: null,
      rootMargin: '-20% 0px -50% 0px', // Adjusted margins for better section detection
      threshold: [0, 0.25, 0.5]
    };

    const callback = (entries) => {
      // Find the most visible section
      const visibleEntries = entries.filter(entry => entry.isIntersecting);

      if (visibleEntries.length > 0) {
        // Sort by visibility ratio to find the most visible section
        const mostVisible = visibleEntries.reduce((prev, current) => 
          (prev.intersectionRatio > current.intersectionRatio) ? prev : current
        );

        const sectionId = mostVisible.target.getAttribute('data-section');
        if (sectionId && sectionId !== activeSection) {
          setActiveSection(sectionId);

          // Notify parent component if callback provided
          if (onSectionChange) {
            onSectionChange(sectionId);
          }
        }
      }
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    sectionElements.forEach(element => {
      if (element) observer.observe(element);
    });

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange, sectionRefs]);

  return (
    <motion.div 
      ref={containerRef}
      className="profile-container"
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
      style={{
        display: 'flex',
        flexDirection: 'row',
        width: '100%',
        maxWidth: '1300px',
        margin: '0 auto',
        minHeight: '70vh',
        position: 'relative',
      }}
    >
      {/* Left sidebar wrapper */}
      <div 
        className="sidebar-wrapper"
        style={{
          width: `${sidebarWidth}%`,
          position: 'relative',
        }}
      >
        {/* Profile sidebar - Using CSS sticky positioning instead of JS calculations */}
        <div 
          className="profile-sidebar"
          style={{
            padding: '3rem 2rem 2rem 0',
            display: 'flex',
            flexDirection: 'column',
            width: '100%',
            position: 'sticky', 
            top: `${topOffset}px`,
            maxHeight: `calc(100vh - ${topOffset}px)`,
            overflowY: 'auto',
            transition: 'top 0.3s ease'
          }}
        >
          {/* Profile information */}
          <div 
            className="profile-info"
            style={{
              marginBottom: '2rem',
              paddingLeft: '5px',
            }}
          >
            <h1 
              className="profile-name"
              style={{
                fontSize: '2.8rem',
                fontWeight: '300',
                color: highlightColor,
                letterSpacing: '0.05em',
                marginBottom: '0.5rem',
                transition: 'transform 0.3s ease',
              }}
            >
              {person.name}
            </h1>

            <h2 
              className="profile-role"
              style={{
                fontSize: '1.2rem',
                fontWeight: '300',
                marginBottom: '2rem',
                color: `${highlightColor}B3`, // 70% opacity
                letterSpacing: '0.05em',
                fontFamily: fontFamily,
                fontStyle: 'italic',
              }}
            >
              {person.role}
            </h2>

            {/* Short tagline */}
            {person.tagline && (
              <p 
                className="profile-tagline"
                style={{
                  fontSize: '1.1rem',
                  lineHeight: '1.6',
                  marginBottom: '3rem',
                  maxWidth: '90%',
                  color: textColor,
                  fontFamily: fontFamily,
                  fontWeight: '300',
                }}
              >
                {person.tagline}
              </p>
            )}
          </div>

          {/* Navigation links with fluid expansion on hover */}
          <div className="nav-links" style={{ marginTop: '3rem' }}>
            {navigationItems.map((navItem) => (
              <div 
                key={navItem.id}
                className="nav-link-container"
                style={{
                  position: 'relative',
                  marginBottom: '1rem',
                  display: 'flex',
                  alignItems: 'center',
                }}
                onMouseEnter={() => handleNavItemHover(navItem.id)}
                onMouseLeave={handleNavItemLeave}
              >
                <div
                  className="nav-line"
                  style={{
                    position: 'absolute',
                    left: '0',
                    top: '50%',
                    transform: 'translateY(-50%)',
                    width: activeSection === navItem.id || expandedNavItem === navItem.id 
                      ? `${maxLineWidth}px` 
                      : `${minLineWidth}px`,
                    height: '0.75px',
                    backgroundColor: activeSection === navItem.id || expandedNavItem === navItem.id
                      ? `${highlightColor}E6` // 90% opacity 
                      : `${highlightColor}80`, // 50% opacity
                    transition: 'width 0.3s ease, background-color 0.3s ease',
                  }}
                ></div>
                <button 
                  className="nav-link"
                  onClick={() => scrollToSection(navItem.id)}
                  style={{
                    position: 'relative',
                    display: 'block',
                    padding: '0.5rem 0 0.5rem 40px',
                    fontSize: '0.85rem',
                    letterSpacing: '0.1em',
                    background: 'transparent',
                    border: 'none',
                    textAlign: 'left',
                    outline: 'none',
                    boxShadow: 'none',
                    transition: 'color 0.5s ease, transform 0.5s ease',
                    color: activeSection === navItem.id || expandedNavItem === navItem.id 
                      ? highlightColor 
                      : textColor,
                    cursor: 'pointer',
                    transform: activeSection === navItem.id || expandedNavItem === navItem.id 
                      ? 'translateX(10px)' 
                      : 'none',
                    width: 'fit-content',
                  }}
                >
                  {navItem.label}
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Right content section */}
      <div 
        className="content-section"
        style={{
          width: `${contentWidth}%`,
          padding: `3rem 0 2rem ${contentPadding}rem`,
          marginLeft: 'auto',
        }}
      >
        {/* Dynamically render sections based on navigationItems */}
        {navigationItems.map((section) => (
          <div 
            key={section.id}
            ref={sectionRefs[section.id]}
            data-section={section.id}
            className="section"
            id={section.id}
            style={{
              marginBottom: '3rem',
              scrollMarginTop: `${topOffset + 40}px`,
            }}
          >
            {/* Default 'about' section */}
            {section.id === 'about' && (
              <>
                {person.bio && person.bio.map((paragraph, idx) => (
                  <p 
                    key={idx} 
                    className="section-content"
                    style={{
                      fontSize: '1rem',
                      lineHeight: '1.8',
                      marginBottom: '1.5rem',
                      color: textColor,
                      fontFamily: fontFamily,
                      fontWeight: '300',
                    }}
                  >
                    {paragraph}
                  </p>
                ))}

                {/* Stats display - conditionally rendered based on showStats prop */}
                {showStats && person.stats && person.stats.length > 0 && (
                  <div 
                    className="stats-container"
                    style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      flexWrap: 'wrap',
                      marginTop: '3rem',
                      width: '100%',
                    }}
                  >
                    {person.stats.map((stat, statIdx) => (
                      <div 
                        key={statIdx} 
                        className="stat"
                        style={{
                          flex: '1',
                          textAlign: 'center',
                          padding: '0 1rem',
                          minWidth: '100px',
                          transition: 'transform 0.3s ease',
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.transform = 'translateY(-5px)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.transform = 'none';
                        }}
                      >
                        <div 
                          className="stat-value"
                          style={{
                            fontSize: '2.5rem',
                            fontWeight: '100',
                            color: highlightColor,
                            marginBottom: '0.5rem'
                          }}
                        >
                          {stat.value}
                        </div>
                        <div 
                          className="stat-label"
                          style={{
                            fontSize: '0.85rem',
                            color: `${textColor}CC`, // 80% opacity
                            textTransform: 'uppercase',
                            letterSpacing: '0.1em'
                          }}
                        >
                          {stat.label}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}

            {/* Default 'experience' section */}
            {section.id === 'experience' && (
              <>
                {person.experience ? (
                  person.experience.map((item, expIdx) => (
                    <p 
                      key={expIdx} 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {item.content}
                    </p>
                  ))
                ) : (
                  <p 
                    className="section-content"
                    style={{
                      fontSize: '1rem',
                      lineHeight: '1.8',
                      marginBottom: '1.5rem',
                      color: textColor,
                      fontFamily: fontFamily,
                      fontWeight: '300',
                    }}
                  >
                    Throughout my career, I've specialized in developing software systems that seamlessly 
                    integrate front-end experiences with robust back-end architectures. My experience spans 
                    various domains, from interactive media to data visualization systems.
                  </p>
                )}
              </>
            )}

            {/* Default 'projects' section */}
            {section.id === 'projects' && (
              <>
                {person.projects ? (
                  person.projects.map((item, projIdx) => (
                    <p 
                      key={projIdx} 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {item.content}
                    </p>
                  ))
                ) : (
                  <p 
                    className="section-content"
                    style={{
                      fontSize: '1rem',
                      lineHeight: '1.8',
                      marginBottom: '1.5rem',
                      color: textColor,
                      fontFamily: fontFamily,
                      fontWeight: '300',
                    }}
                  >
                    My personal projects merge the scientific principles of evolution and adaptation with 
                    mythological themes of rebirth and renewal. I create systems that are both mechanically 
                    sound and narratively compelling, allowing users to explore complex themes through 
                    immersive experiences.
                  </p>
                )}
              </>
            )}

            {/* Custom section content */}
            {section.content && (
              typeof section.content === 'string' 
                ? <p 
                    className="section-content"
                    style={{
                      fontSize: '1rem',
                      lineHeight: '1.8',
                      marginBottom: '1.5rem',
                      color: textColor,
                      fontFamily: fontFamily,
                      fontWeight: '300',
                    }}
                  >
                    {section.content}
                  </p>
                : section.content
            )}
          </div>
        ))}

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div 
              key={index} 
              className="section"
              style={{
                marginBottom: '3rem',
                scrollMarginTop: '2rem',
              }}
            >
              {section.title && (
                <h3 style={{
                  fontSize: '1.4rem',
                  color: highlightColor,
                  marginBottom: '1rem',
                  fontWeight: '300',
                }}>
                  {section.title}
                </h3>
              )}
              {section.content && (
                typeof section.content === 'string' 
                  ? <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {section.content}
                    </p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>

      {/* Media query styles for mobile */}
      <style jsx>{`
        @media (max-width: 768px) {
          .profile-container {
            flex-direction: column;
          }

          .sidebar-wrapper {
            width: 100% !important;
          }

          .profile-sidebar {
            position: relative !important;
            padding: 2rem 1rem !important;
            align-items: center;
            text-align: center;
            top: 0 !important;
            bottom: auto !important;
          }

          .profile-name {
            font-size: 2.2rem !important;
          }

          .profile-tagline {
            max-width: 100% !important;
            text-align: center;
          }

          .content-section {
            width: 100% !important;
            padding: 2rem 1rem !important;
            margin-left: 0 !important;
          }

          .stats-container {
            justify-content: center !important;
            gap: 2rem;
          }

          .nav-links {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            width: 100%;
          }

          .nav-link-container {
            margin: 0.5rem 0 !important;
          }
        }

        @media (max-width: 480px) {
          .stats-container {
            flex-direction: column !important;
            align-items: center !important;
            gap: 1.5rem !important;
          }

          .stat {
            width: 100% !important;
            max-width: 180px !important;
          }
        }
      `}</style>
    </motion.div>
  );
};

export default PersonProfileCard;
