import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import Background from '../../../../core/Background';
import './CreatorsPage.css';

const CreatorsPage = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [activeCreator, setActiveCreator] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [userImage, setUserImage] = useState(null);
  const [showImageUpload, setShowImageUpload] = useState(false);
  const fileInputRef = useRef(null);
  const pageRef = useRef(null);
  const headerRef = useRef(null);
  const titleRef = useRef(null);

  // Parallax scroll effects
  const { scrollY } = useScroll();
  const headerY = useTransform(scrollY, [0, 500], [0, -100]);
  const parallaxY1 = useTransform(scrollY, [0, 1000], [0, -300]);
  const parallaxY2 = useTransform(scrollY, [0, 1000], [0, -150]);
  const parallaxY3 = useTransform(scrollY, [0, 1000], [0, -50]);
  const titleScale = useTransform(scrollY, [0, 300], [1, 0.8]);
  const titleOpacity = useTransform(scrollY, [0, 300], [1, 0.6]);

  // Creator data with Daniel first and prominently featured
  const creators = [
    {
      id: "daniel",
      name: "Daniel Fialkov",
      role: "Game Design, Inspiration, Writing, and Editing",
      bio: "Daniel's visionary contributions to game theory and mechanics development have shaped the core of Crescent's experience. His focus on creating balanced yet emotionally resonant gameplay experiences forms the foundation of the game's unique approach to storytelling and player agency.",
      featured: true
    },
    {
      id: "rosalia",
      name: "Rosalia Valeria Fialkova",
      role: "Game Design, Writing, Layout, and Graphic Design",
      bio: "Rosalia's approach to game design merges traditional tabletop mechanics with innovative narrative structures. Her work on Crescent brings together years of experience in world-building and system design."
    },
    {
      id: "patrick",
      name: "Patrick Conner Purciful",
      role: "Developmental and Line Editing",
      bio: "Patrick's keen editorial insight has refined the game's systems and narrative voice. His meticulous attention to detail ensures consistency and clarity throughout the rules."
    },
    {
      id: "samantha",
      name: "Samantha Keeler",
      role: "Graphic Design Consultant",
      bio: "Samantha's artistic vision brings visual cohesion to Crescent's complex world. Her design philosophy emphasizes atmosphere and immersion through careful visual storytelling."
    }
  ];

  // Track mouse movement for interactive effects
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Image upload handler
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUserImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Trigger file input click
  const triggerFileInput = () => {
    fileInputRef.current.click();
  };

  // Create meteor animations
  const createMeteors = () => {
    return Array.from({ length: 15 }).map((_, i) => (
      <div 
        key={`meteor-${i}`} 
        className="meteor"
        style={{
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          animationDuration: `${Math.random() * 3 + 2}s`,
          animationDelay: `${Math.random() * 3}s`
        }}
      />
    ));
  };

  // Letter animation variants
  const letterVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: i => ({
      opacity: 1,
      y: 0,
      transition: {
        delay: i * 0.1,
        duration: 0.8,
        ease: [0.2, 0.65, 0.3, 0.9]
      }
    })
  };

  // Set loaded state after mount
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div 
      className="creators-page"
      ref={pageRef}
      onMouseMove={handleMouseMove}
    >
      {/* Background component from core */}
      <Background 
        cursorPosition={cursorPosition} 
        setCursorPosition={setCursorPosition} 
      />

      {/* Parallax elements */}
      <div className="parallax-container">
        <motion.div className="parallax-layer layer-1" style={{ y: parallaxY1 }}>
          {createMeteors()}
        </motion.div>
        <motion.div className="parallax-layer layer-2" style={{ y: parallaxY2 }}>
          {createMeteors()}
        </motion.div>
        <motion.div className="parallax-layer layer-3" style={{ y: parallaxY3 }}>
          {createMeteors()}
        </motion.div>
      </div>

      {/* Main content */}
      <div className={`content-wrapper ${isLoaded ? 'loaded' : ''}`}>
        {/* Animated header */}
        <motion.header 
          className="creators-header" 
          ref={headerRef}
          style={{ y: headerY }}
        >
          <motion.h1 
            ref={titleRef}
            className="main-title"
            style={{ scale: titleScale, opacity: titleOpacity }}
          >
            {"VISIONARIES".split('').map((letter, i) => (
              <motion.span
                key={i}
                custom={i}
                variants={letterVariants}
                initial="hidden"
                animate="visible"
                className="title-letter"
              >
                {letter}
              </motion.span>
            ))}
          </motion.h1>
          <div className="title-divider" />
          <p className="subtitle">
            The brilliant minds behind Crescent's world of fading moonlight.
          </p>
        </motion.header>

        {/* Featured creator (Daniel) */}
        <section className="featured-creator-section">
          <div className="featured-creator-container">
            <div className="featured-creator-portrait">
              <div className="portrait-container">
                {userImage ? (
                  <div 
                    className="portrait-image"
                    style={{ backgroundImage: `url(${userImage})` }}
                  />
                ) : (
                  <div className="portrait-placeholder" />
                )}

                <div className="image-upload-controls">
                  <input 
                    type="file" 
                    ref={fileInputRef} 
                    onChange={handleImageUpload} 
                    accept="image/*" 
                    className="hidden-input"
                  />
                  <button 
                    className="upload-button"
                    onClick={triggerFileInput}
                  >
                    {userImage ? 'Change Image' : 'Add Your Portrait'}
                  </button>
                </div>
              </div>
            </div>

            <div className="featured-creator-info">
              <h2 className="featured-creator-name">{creators[0].name}</h2>
              <h3 className="featured-creator-role">{creators[0].role}</h3>
              <div className="title-divider" />
              <p className="featured-creator-bio">{creators[0].bio}</p>
            </div>
          </div>
        </section>

        {/* Creator grid for other team members */}
        <section className="creators-grid-section">
          <h2 className="section-title">The Team</h2>
          <div className="creators-grid">
            {creators.slice(1).map((creator, index) => (
              <motion.div 
                key={creator.id}
                className={`creator-card ${activeCreator === creator.id ? 'active' : ''}`}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.15 + 0.5 }}
                onMouseEnter={() => setActiveCreator(creator.id)}
                onMouseLeave={() => setActiveCreator(null)}
              >
                <div className="creator-info">
                  <h2 className="creator-name">{creator.name}</h2>
                  <h3 className="creator-role">{creator.role}</h3>
                  <p className="creator-bio">{creator.bio}</p>
                </div>
                <div className="diagonal-cut" />
              </motion.div>
            ))}
          </div>
        </section>

        {/* Inspiration section */}
        <motion.section 
          className="inspiration-section"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8, ease: "easeOut", delay: 1.2 }}
        >
          <h2 className="section-title">Inspirations</h2>
          <div className="title-divider" />
          <div className="inspiration-categories">
            <div className="inspiration-category">
              <h3>Literary & Media</h3>
              <ul className="inspiration-list">
                <li>Berserk by Kentaro Miura</li>
                <li>Made in Abyss by Akihito Tsukushi</li>
                <li>Bloodborne by FromSoft</li>
                <li>Scavenger's Reign by Joseph Bennett & Christopher Huettner</li>
              </ul>
            </div>
            <div className="inspiration-category">
              <h3>Tabletop RPG</h3>
              <ul className="inspiration-list">
                <li>Blades in the Dark by John Harper</li>
                <li>Mouse Guard by Luke Crane</li>
                <li>Ironsworn by Shaun Tomkin</li>
                <li>Heart by Grant Howitt & Christopher Taylor</li>
              </ul>
            </div>
          </div>
        </motion.section>

        {/* Credits footer */}
        <footer className="credits-section">
          <p className="image-credits">
            Artwork utilizes elements from Shutterstock.com and Adobe Stock images.
          </p>
        </footer>
      </div>
    </div>
  );
};

export default CreatorsPage;
