// src/styles/GlobalStyles.js
import { createGlobalStyle } from 'styled-components';

const GlobalStyles = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Avenir:wght@400&display=swap');

  /* You'll need to add Adobe Caslon Pro via @font-face if you have the license */
  /* Example:
  @font-face {
    font-family: 'Adobe Caslon Pro';
    src: url('/fonts/ACaslonPro-Regular.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
  }
  */

  * {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }

  html, body {
    height: 100%;
    width: 100%;
    overflow-x: hidden;
  }

  body {
    font-family: ${props => props.theme.fonts.body};
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  #root {
    display: flex;
    flex-direction: column;
    min-height: 100vh;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: ${props => props.theme.fonts.heading};
    font-weight: 400;
    line-height: 1.3;
    color: ${props => props.theme.colors.text};
    margin-bottom: ${props => props.theme.spacing.md};
  }

  h1 {
    font-size: 2.5rem;
    letter-spacing: 0.04em;

    @media (min-width: ${props => props.theme.breakpoints.medium}) {
      font-size: 3.5rem;
    }
  }

  h2 {
    font-size: 2rem;
    letter-spacing: 0.03em;

    @media (min-width: ${props => props.theme.breakpoints.medium}) {
      font-size: 2.5rem;
    }
  }

  h3 {
    font-size: 1.5rem;
    letter-spacing: 0.02em;
  }

  p {
    margin-bottom: ${props => props.theme.spacing.md};
  }

  a {
    color: ${props => props.theme.colors.primary};
    text-decoration: none;
    transition: color ${props => props.theme.transitions.default};

    &:hover {
      color: ${props => props.theme.colors.primaryLight};
    }
