import React, { useState, useEffect, useRef } from 'react';
import './introSequence.css';

const IntroSequence = ({ showIntro, setShowIntro, setIsLoaded }) => {
  const moonCanvasRef = useRef(null);
  const moonAnimationRef = useRef(null);
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [moonDrawn, setMoonDrawn] = useState(0); // 0: not started, 1: drawing, 2: complete

  // Moved quotes from LandingPage to IntroSequence
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend â€”\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ];

  // Setup intro sequence
  useEffect(() => {
    if (showIntro) {
      // Select a random quote
      const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
      setCurrentQuote(randomQuote);

      // Coordinated intro sequence timing
      setTimeout(() => setMoonDrawn(1), 150); // Start animation quickly
      setTimeout(() => setQuoteVisible(true), 350); // Show quote almost immediately

      // Initiate fade-out process
      setTimeout(() => {
        setQuoteVisible(false); // Hide quote first
      }, 2000);

      setTimeout(() => {
        setMoonDrawn(2); // Signal animation to begin fade-out
      }, 2100);

      setTimeout(() => {
        // Start fade out of entire intro sequence
        const introElement = document.querySelector('.intro-sequence');
        if (introElement) introElement.classList.add('fade-out');
      }, 2400);

      setTimeout(() => {
        setShowIntro(false);
        setIsLoaded(true);
      }, 2900); // Complete sequence with enough time for animation to finish fading
    }
  }, [showIntro, setShowIntro, setIsLoaded]);

  // Comet/shooting star animation with trailing glow
  useEffect(() => {
    if (!moonCanvasRef.current || moonDrawn === 0) return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 70;

    // Animation parameters
    let rotation = 0;
    let lastTimestamp = 0;
    let isFadingOut = false;
    let fadeOpacity = 1;
    let twinkleIntensity = 1;
    let twinkleDirection = -0.02;

    // Trail parameters
    const trailLength = 120; // Length of trail in degrees
    const trailSegments = 20; // Number of segments in trail

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Function to draw the comet and trail
    const drawComet = (opacity = 1, twinkle = 1) => {
      // Calculate comet head position
      const headAngle = rotation * Math.PI / 180;
      const headX = centerX + radius * Math.cos(headAngle);
      const headY = centerY + radius * Math.sin(headAngle);

      // Draw trail segments with decreasing opacity
      for (let i = 0; i < trailSegments; i++) {
        // Calculate position for this segment
        const segmentAngle = ((rotation - (i * (trailLength / trailSegments))) % 360) * Math.PI / 180;
        const segmentX = centerX + radius * Math.cos(segmentAngle);
        const segmentY = centerY + radius * Math.sin(segmentAngle);

        // Calculate next segment position (for line drawing)
        const nextSegmentAngle = ((rotation - ((i + 1) * (trailLength / trailSegments))) % 360) * Math.PI / 180;
        const nextSegmentX = centerX + radius * Math.cos(nextSegmentAngle);
        const nextSegmentY = centerY + radius * Math.sin(nextSegmentAngle);

        // Calculate opacity based on position in trail and overall opacity
        const baseOpacity = opacity * 0.8 * (1 - (i / trailSegments));

        // Draw trail segment
        ctx.beginPath();
        ctx.moveTo(segmentX, segmentY);
        ctx.lineTo(nextSegmentX, nextSegmentY);

        // Vary line width from head to tail
        const segmentWidth = 2.5 * (1 - (i / trailSegments)) + 0.5;

        // Set shadow/glow for trail
        ctx.shadowColor = 'rgba(255, 253, 227, ' + baseOpacity + ')';
        ctx.shadowBlur = 10 * (1 - (i / trailSegments)) + 5;

        // Set line style and draw
        ctx.strokeStyle = 'rgba(191, 173, 127, ' + baseOpacity + ')';
        ctx.lineWidth = segmentWidth;
        ctx.lineCap = 'round';
        ctx.stroke();
      }

      // Draw comet head with twinkle effect
      ctx.shadowColor = `rgba(255, 253, 227, ${0.9 * opacity * twinkle})`;
      ctx.shadowBlur = 20 * twinkle;

      // Larger outer glow
      ctx.beginPath();
      ctx.arc(headX, headY, 4 * twinkle, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${0.7 * opacity * twinkle})`;
      ctx.fill();

      // Brighter inner core
      ctx.beginPath();
      ctx.arc(headX, headY, 2.5 * twinkle, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${0.9 * opacity * twinkle})`;
      ctx.fill();

      // Brightest center point
      ctx.beginPath();
      ctx.arc(headX, headY, 1 * twinkle, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 255, 255, ${1 * opacity * twinkle})`;
      ctx.fill();
    };

    const animate = (timestamp) => {
      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Handle twinkle effect
      twinkleIntensity += twinkleDirection;
      if (twinkleIntensity <= 0.85) {
        twinkleDirection = 0.02;
      } else if (twinkleIntensity >= 1.15) {
        twinkleDirection = -0.02;
      }

      if (isFadingOut) {
        // Continue the twinkle fade-out effect
        fadeOpacity -= 0.02; // Gradual fade

        if (fadeOpacity <= 0) {
          cancelAnimationFrame(moonAnimationRef.current);
          return;
        }

        // Add extra twinkle during fade out
        const fadeTwinkle = 1 + (Math.sin(fadeOpacity * 12) * 0.3);

        // Draw with fading opacity and enhanced twinkle
        ctx.globalAlpha = fadeOpacity;
        drawComet(fadeOpacity, fadeTwinkle);
      } else {
        // Normal animation - update rotation
        const speed = 0.12 * deltaTime; // Adjust for desired speed
        rotation = (rotation + speed) % 360;

        if (moonDrawn === 2) {
          // Start the fade-out process
          isFadingOut = true;
        } else {
          // Draw with normal settings
          drawComet(1, twinkleIntensity);
        }
      }

      moonAnimationRef.current = requestAnimationFrame(animate);
    };

    if (moonDrawn === 1) {
      moonAnimationRef.current = requestAnimationFrame(animate);
    }

    return () => {
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, [moonDrawn]);

  // Skip intro
  const handleSkipIntro = () => {
    setShowIntro(false);
    setIsLoaded(true);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  if (!showIntro) return null;

  return (
    <div className="intro-sequence" onClick={handleSkipIntro}>
      <div className="moon-animation">
        <canvas 
          ref={moonCanvasRef} 
          width="200" 
          height="200"
          className="moon-canvas"
        />
      </div>

      <div className={`quote-container ${quoteVisible ? 'visible' : ''}`}>
        <div className="quote-text">
          {currentQuote && formatQuote(currentQuote.text)}
        </div>
        <div className="quote-author">
          {currentQuote && currentQuote.author}
        </div>
      </div>

      <div className="skip-intro">tap to skip</div>
    </div>
  );
};

export default IntroSequence;
