    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d', { alpha: false });

    if (!ctx) return;

    const { width, height, pixelRatio } = dimensionsRef.current;

    // Clear canvas
    ctx.fillStyle = COLORS.background.topColor;
    ctx.fillRect(0, 0, width * pixelRatio, height * pixelRatio);

    // Apply device pixel ratio scaling only once
    ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);

    // Enable global composition for better glow effect
    ctx.globalCompositeOperation = 'lighter';

    // Draw stars
    for (let i = 0; i < starsRef.current.length; i++) {
      drawStar(ctx, starsRef.current[i]);
    }

    // Reset global composition
    ctx.globalCompositeOperation = 'source-over';
  }, [COLORS.background.topColor, drawStar]);

  // Apply spring physics to star offsets
  const applySpringPhysics = useCallback((star, deltaTime) => {
    // Spring physics constants
    const stiffness = CONFIG.springStiffness; 
    const damping = CONFIG.springDamping;

    // Calculate spring force for X axis
    let springForceX = (star.targetOffsetX - star.springOffsetX) * stiffness;
    let dampingForceX = -star.velocityX * damping;
    star.accelerationX = springForceX + dampingForceX;
    star.velocityX += star.accelerationX * deltaTime;
    star.springOffsetX += star.velocityX * deltaTime;

    // Calculate spring force for Y axis
    let springForceY = (star.targetOffsetY - star.springOffsetY) * stiffness;
    let dampingForceY = -star.velocityY * damping;
    star.accelerationY = springForceY + dampingForceY;
    star.velocityY += star.accelerationY * deltaTime;
    star.springOffsetY += star.velocityY * deltaTime;
  }, [CONFIG.springStiffness, CONFIG.springDamping]);

  // Update animation state - optimized for performance
  const updateAnimation = useCallback((deltaTime, timestamp) => {
    const { width, height } = dimensionsRef.current;

    // Global time factor for animations
    const timeFactor = timestamp * 0.001;

    // Get current scroll position and calculate scroll velocity
    const currentScrollY = window.scrollY;
    const scrollDelta = currentScrollY - lastScrollYRef.current;

    // Update scroll velocity with smoothing
    scrollVelocityRef.current = scrollVelocityRef.current * 0.8 + scrollDelta * 0.2;

    // Store current scroll position for next frame
    lastScrollYRef.current = currentScrollY;
    scrollYRef.current = currentScrollY;

    // Maximum displacement based on config
    const maxDisplacement = CONFIG.parallaxMaxDisplacement;

    // Update stars
    for (let i = 0; i < starsRef.current.length; i++) {
      const star = starsRef.current[i];

      // Initialize velocity and acceleration if not present
      if (star.velocityX === undefined) {
        star.velocityX = 0;
        star.velocityY = 0;
        star.accelerationX = 0;
        star.accelerationY = 0;
      }

      // Basic movement based on time
      star.x = star.baseX + Math.sin(timeFactor * 0.2 + star.pulsePhase) * 2 * star.directionX;
      star.y = star.baseY + Math.cos(timeFactor * 0.3 + star.pulsePhase * 1.3) * 2 * star.directionY;

      // Parallax effect based on scroll - each star moves differently based on its layer
      if (CONFIG.parallaxEnabled) {
        // Calculate target offset based on scroll position
        // Stars in deeper layers (higher parallaxFactor) move more
        const scrollInfluence = (scrollYRef.current / height) * maxDisplacement;
        const layerFactor = star.parallaxFactor;

        // Add velocity-based effects for a more dynamic feel
        const velocityInfluence = scrollVelocityRef.current * 0.1 * layerFactor;

        // Set target offsets - notice how deeper layers move more
        star.targetOffsetX = Math.sin(timeFactor * 0.1 + star.pulsePhase) * velocityInfluence * star.directionX;
        star.targetOffsetY = (scrollInfluence * layerFactor) + (Math.cos(timeFactor * 0.15 + star.pulsePhase) * velocityInfluence * star.directionY);

        // Apply spring physics for smooth transitions
        applySpringPhysics(star, deltaTime);
      }

      // Wrap around screen edges
      const effectiveX = star.x + star.springOffsetX;
      const effectiveY = star.y + star.springOffsetY;

      if (effectiveX < 0) star.baseX = width;
      if (effectiveX > width) star.baseX = 0;
      if (effectiveY < 0) star.baseY = height;
      if (effectiveY > height) star.baseY = 0;

      // Simplified pulsing effect
      const pulseFactor = Math.sin(timeFactor * star.pulseSpeed + star.pulsePhase) * 0.15 + 0.85;
      star.opacity = star.baseOpacity * pulseFactor;

      // Subtle size variation based on parallax movement
      const sizeVariation = 1 + (Math.abs(star.springOffsetX) + Math.abs(star.springOffsetY)) / (maxDisplacement * 10);
      star.currentSize = star.size * sizeVariation;
    }
  }, [CONFIG.parallaxEnabled, CONFIG.parallaxMaxDisplacement, applySpringPhysics]);

  // Define the animation loop function with FPS limiting
  const animate = useCallback(function animationLoop(timestamp) {
    if (!isInitializedRef.current) {
      animationFrameRef.current = requestAnimationFrame(animationLoop);
      return;
    }

    // Limit FPS
    const minFrameTime = 1000 / CONFIG.maxFPS;
    const elapsed = timestamp - (lastTimeRef.current || 0);

    if (elapsed < minFrameTime) {
      animationFrameRef.current = requestAnimationFrame(animationLoop);
      return;
    }

    // Calculate delta time with cap to prevent large jumps
    const deltaTime = lastTimeRef.current ? Math.min(elapsed, 50) : 16;
    lastTimeRef.current = timestamp;

    // Update animation state
    updateAnimation(deltaTime, timestamp);

    // Render scene
    renderScene();

    // Schedule next frame
    animationFrameRef.current = requestAnimationFrame(animationLoop);
  }, [CONFIG.maxFPS, updateAnimation, renderScene]);

  // Track scroll position
  useEffect(() => {
    const handleScroll = () => {
      scrollYRef.current = window.scrollY;
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Comprehensive initialization and resize handling
  useEffect(() => {
    // One-time initialization function
    const initialize = () => {
      if (setupCanvas()) {
        // Create stars only after canvas is ready
        initializeStars();
        // Mark as initialized but don't trigger re-render
        isInitializedRef.current = true;
        // Start animation only after everything is ready
        animationFrameRef.current = requestAnimationFrame(animate);
      }
    };

    // Handle resize events
    const handleResize = () => {
      // Only re-initialize if already initialized
      if (isInitializedRef.current) {
        setupCanvas();
        initializeStars();
        // No need to change initialization state
      }
    };

    // Throttle resize events
    let resizeTimer;
    const throttledResize = () => {
      if (!resizeTimer) {
        resizeTimer = setTimeout(() => {
          resizeTimer = null;
          handleResize();
        }, 200); // 200ms throttle
      }
    };

    // Add resize listener
    window.addEventListener('resize', throttledResize);

    // Run initialization after first render is complete
    // This prevents the "double load" effect
    requestAnimationFrame(() => {
      firstRenderCompleteRef.current = true;
      initialize();
    });

    // Cleanup
    return () => {
      window.removeEventListener('resize', throttledResize);
      if (resizeTimer) clearTimeout(resizeTimer);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [setupCanvas, initializeStars, animate]);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        overflow: 'hidden',
        zIndex: -1,
        pointerEvents: 'none'
      }}
      aria-hidden="true"
    >
      <canvas
        ref={canvasRef}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%'
        }}
      />
    </div>
  );
};

export default Background;
