import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring, useInView } from 'framer-motion';
import Background from '../../../core/Background'; // Adjust import path as needed
import rosaliaImage from '../../../assets/images/creators/rosalia.jpg'; // Adjust path as needed
import './creatorsPage.css';

// Custom Meteor component
const Meteor = ({ 
  width = 150, 
  height = 150, 
  speed = 0.12,
  delay = 0,
  direction = 1, // 1 for left to right, -1 for right to left
  size = 3,
  opacity = 0.8,
  top = "50%",
  left = "0%",
}) => {
  const meteorRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setTimeout(() => setIsVisible(true), delay);

    return () => setIsVisible(false);
  }, [delay]);

  return (
    <div 
      className={`meteor ${isVisible ? 'visible' : ''}`} 
      style={{
        width: `${width}px`,
        height: `${height}px`,
        top,
        left,
        opacity: 0, // Start invisible, will be animated
      }}
      ref={meteorRef}
    >
      <div 
        className="meteor-body"
        style={{
          animationDuration: `${8 / speed}s`,
          animationDelay: `${delay}ms`,
          animationDirection: direction === 1 ? 'normal' : 'reverse',
        }}
      >
        <div 
          className="meteor-head"
          style={{ 
            width: `${size}px`, 
            height: `${size}px`,
          }}
        ></div>
        <div 
          className="meteor-tail"
          style={{ opacity }}
        ></div>
      </div>
    </div>
  );
};

// Text reveal animation component
const RevealText = ({ children, threshold = 0.5, delay = 0 }) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: false, threshold });

  return (
    <div ref={ref} className="reveal-text-container">
      <div
        className={`reveal-text ${isInView ? 'revealed' : ''}`}
        style={{ transitionDelay: `${delay}ms` }}
      >
        {children}
      </div>
    </div>
  );
};

// Project card component
const ProjectCard = ({ project, index }) => {
  const cardRef = useRef(null);
  const isInView = useInView(cardRef, { once: true, threshold: 0.2 });

  return (
    <motion.div 
      ref={cardRef}
      className="project-card"
      initial={{ opacity: 0, y: 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
      transition={{ 
        duration: 0.8, 
        ease: "easeOut",
        delay: index * 0.15
      }}
    >
      <div className="project-image">
        <img src={project.image} alt={project.title} />
      </div>
      <div className="project-details">
        <h3 className="project-title">{project.title}</h3>
        <p className="project-description">{project.description}</p>
        <div className="project-tags">
          {project.tags.map((tag, i) => (
            <span key={i} className="project-tag">{tag}</span>
          ))}
        </div>
      </div>
    </motion.div>
  );
};

// Main component
const CreatorsPage = () => {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);

  // Parallax scrolling setup
  const { scrollY } = useScroll();
  const mainContainerRef = useRef(null);
  const creatorProfileRef = useRef(null);

  // Transform values for parallax elements with extended fade range
  const headerY = useTransform(scrollY, [0, 1000], [0, -150]);
  const headerOpacity = useTransform(scrollY, [0, 400, 1000], [1, 0.9, 0]); // Extended fade range
  const meteorsY = useTransform(scrollY, [0, 1000], [0, -250]);
  const titleScale = useTransform(scrollY, [0, 300], [1, 1.2]);
  const titleY = useTransform(scrollY, [0, 500], [0, 100]);

  // Spring physics for smoother parallax
  const springHeaderY = useSpring(headerY, { stiffness: 100, damping: 30 });
  const springMeteorsY = useSpring(meteorsY, { stiffness: 50, damping: 30 });
  const springTitleY = useSpring(titleY, { stiffness: 100, damping: 30 });

  // Track mouse movement for Background component
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Check if creator profile is in view
  const isProfileInView = useInView(creatorProfileRef, { once: true, threshold: 0.2 });

  // Meteor field configuration with enhanced top shower effect
  const meteors = [
    // Top meteor shower (concentrated in top third of the screen)
    { id: 1, top: "5%", left: "-5%", width: 180, height: 180, speed: 0.08, direction: 1, size: 4, delay: 100 },
    { id: 2, top: "8%", left: "100%", width: 160, height: 160, speed: 0.14, direction: -1, size: 3.5, delay: 200 },
    { id: 3, top: "12%", left: "-8%", width: 140, height: 140, speed: 0.12, direction: 1, size: 3, delay: 300 },
    { id: 4, top: "18%", left: "105%", width: 200, height: 200, speed: 0.09, direction: -1, size: 4.5, delay: 450 },
    { id: 5, top: "25%", left: "-10%", width: 150, height: 150, speed: 0.11, direction: 1, size: 3.2, delay: 600 },
    { id: 6, top: "20%", left: "108%", width: 180, height: 180, speed: 0.13, direction: -1, size: 3.8, delay: 750 },
    { id: 7, top: "15%", left: "-12%", width: 170, height: 170, speed: 0.10, direction: 1, size: 4.2, delay: 900 },
    { id: 8, top: "10%", left: "110%", width: 190, height: 190, speed: 0.07, direction: -1, size: 4.8, delay: 1050 },
    { id: 9, top: "22%", left: "-7%", width: 140, height: 140, speed: 0.15, direction: 1, size: 2.8, delay: 1200 },
    { id: 10, top: "27%", left: "103%", width: 160, height: 160, speed: 0.09, direction: -1, size: 3.5, delay: 1350 },

    // Additional meteors throughout the page
    { id: 11, top: "35%", left: "100%", width: 150, height: 150, speed: 0.12, direction: -1, size: 3, delay: 1800 },
    { id: 12, top: "45%", left: "-8%", width: 140, height: 140, speed: 0.11, direction: 1, size: 3, delay: 2100 },
    { id: 13, top: "55%", left: "105%", width: 170, height: 170, speed: 0.08, direction: -1, size: 3.8, delay: 2400 },
    { id: 14, top: "65%", left: "-10%", width: 200, height: 200, speed: 0.06, direction: 1, size: 5, delay: 2700 },
    { id: 15, top: "75%", left: "100%", width: 170, height: 170, speed: 0.07, direction: -1, size: 4, delay: 3000 },
    { id: 16, top: "85%", left: "-6%", width: 160, height: 160, speed: 0.09, direction: 1, size: 3.5, delay: 3300 },
  ];

  // Project data
  const projects = [
    {
      id: "crescent",
      title: "Crescent TTRPG",
      description: "A dark fantasy tabletop roleplaying game centered around cycles of creation, evolution, and destruction.",
      image: "/images/projects/crescent.jpg",
      tags: ["Game Design", "World Building", "System Design"]
    },
    {
      id: "crucible",
      title: "Crucible Platform",
      description: "A web platform for managing and visualizing game worlds, character creation, and rule systems.",
      image: "/images/projects/crucible.jpg",
      tags: ["Web Development", "UI/UX", "Interactive Design"]
    },
    {
      id: "evolution-sim",
      title: "Evolution Simulator",
      description: "A sophisticated simulation system modeling environment formation and creature adaptation within dark fantasy settings.",
      image: "/images/projects/evolution.jpg",
      tags: ["Simulation", "Procedural Generation", "Complex Systems"]
    }
  ];

  // Simulate initial loading and create staggered meteor effects
  useEffect(() => {
    // Set loaded state after a brief delay
    const loadTimer = setTimeout(() => {
      setIsLoaded(true);
    }, 300);

    // Function to create additional random meteors periodically
    const createRandomMeteors = () => {
      // This is just a placeholder - the actual meteor creation is handled through the meteors array
      // but we're simulating ongoing activity by forcing re-renders periodically
      const refreshTimer = setTimeout(() => {
        setCursorPosition(prev => ({...prev, timestamp: Date.now()}));
        createRandomMeteors();
      }, 2000 + Math.random() * 3000); // Random interval between 2-5 seconds

      return refreshTimer;
    };

    // Start the meteor creation cycle after initial load
    const meteorTimer = setTimeout(createRandomMeteors, 1500);

    return () => {
      clearTimeout(loadTimer);
      clearTimeout(meteorTimer);
    };
  }, []);

  return (
    <div 
      className="creators-container"
      onMouseMove={handleMouseMove}
      ref={mainContainerRef}
    >
      {/* Background component with stars */}
      <Background 
        cursorPosition={cursorPosition} 
        setCursorPosition={setCursorPosition} 
      />

      {/* Meteor field with parallax */}
      <motion.div 
        className="meteor-field"
        style={{ y: springMeteorsY }}
      >
        {meteors.map((meteor) => (
          <Meteor 
            key={meteor.id}
            width={meteor.width}
            height={meteor.height}
            speed={meteor.speed}
            direction={meteor.direction}
            size={meteor.size}
            delay={meteor.delay}
            top={meteor.top}
            left={meteor.left}
          />
        ))}
      </motion.div>

      {/* Parallax header section */}
      <motion.div 
        className="parallax-header"
        style={{ 
          y: springHeaderY,
          opacity: headerOpacity,
          zIndex: 5 /* Ensure proper layer ordering */
        }}
      >
        <motion.div 
          className="title-container"
          style={{ 
            y: springTitleY,
            scale: titleScale
          }}
        >
          <h1 className="main-title">CELESTIAL CREATOR</h1>
          <div className="title-divider"></div>
        </motion.div>
      </motion.div>

      <div className={`scrollable-content ${isLoaded ? 'loaded' : ''}`}>
        {/* Introduction section with reveal animations */}
        <section className="intro-section">
          <RevealText threshold={0.2}>
            <p className="intro-quote">
              "Though my soul may set in darkness,<br />
              it will rise in perfect light;<br />
              I have loved the stars too fondly<br />
              to be fearful of the night."
            </p>
            <p className="quote-author">— Sarah Williams, "The Old Astronomer to His Pupil"</p>
          </RevealText>

          <RevealText delay={300}>
            <p className="creators-subtitle">
              Crescent was born from a fascination with cycles of rebirth, evolutionary pressure, and the eternal dance between light and darkness. As its creator, I've woven together systems design, narrative structure, and visual aesthetics to craft an immersive dark fantasy world.
            </p>
          </RevealText>
        </section>

        {/* Creator profile section */}
        <section>
          <motion.div 
            ref={creatorProfileRef}
            className={`creator-profile ${isProfileInView ? 'in-view' : ''}`}
            initial={{ opacity: 0, y: 50 }}
            animate={isProfileInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
          >
<div className="creator-image">
  <img src={rosaliaImage} alt="Rosalia Valeria Fialkova" />
</div>

            <div className="creator-details">
              <h2 className="creator-name">Rosalia Valeria Fialkova</h2>
              <p className="creator-role">Game Designer & World Builder</p>

              <p className="creator-bio">
                Architect of the Crescent world, where cycles of creation and destruction
                shape endless possibilities. With a passion for dark fantasy and intricate
                systems, I craft universes where player choices and environmental
                pressures create unique, evolving narratives.
              </p>

              <p className="creator-bio">
                My work merges scientific principles of evolution and adaptation
                with mythological themes of rebirth and renewal. I create systems
                that are both mechanically sound and narratively compelling, 
                allowing players to explore complex themes through immersive gameplay.
              </p>

              <div className="creator-stats">
                <div className="creator-stat">
                  <div className="stat-value">8</div>
                  <div className="stat-label">Environment Types</div>
                </div>
                <div className="creator-stat">
                  <div className="stat-value">3</div>
                  <div className="stat-label">Metropoles</div>
                </div>
                <div className="creator-stat">
                  <div className="stat-value">∞</div>
                  <div className="stat-label">Cycles</div>
                </div>
              </div>
            </div>
          </motion.div>
        </section>

        {/* Projects section */}
        <section className="projects-section">
          <RevealText>
            <h2 className="section-title">Celestial Works</h2>
            <div className="title-divider"></div>
          </RevealText>

          <div className="projects-grid">
            {projects.map((project, index) => (
              <ProjectCard key={project.id} project={project} index={index} />
            ))}
          </div>
        </section>

        {/* Inspiration section with parallax */}
        <section className="inspiration-section">
          <div className="parallax-inspiration-bg"></div>

          <RevealText>
            <h2 className="section-title">Celestial Inspirations</h2>
            <div className="title-divider"></div>
          </RevealText>

          <div className="inspiration-categories">
            <div className="inspiration-category">
              <RevealText delay={200}>
                <h3>Literary & Media</h3>
                <ul className="inspiration-list">
                  <li>Berserk by Kentaro Miura</li>
                  <li>Made in Abyss by Akihito Tsukushi</li>
                  <li>Bloodborne by FromSoft</li>
                  <li>Scavenger's Reign by Joseph Bennett & Christopher Huettner</li>
                </ul>
              </RevealText>
            </div>

            <div className="inspiration-category">
              <RevealText delay={400}>
                <h3>Tabletop RPG</h3>
                <ul className="inspiration-list">
                  <li>Blades in the Dark by John Harper</li>
                  <li>Mouse Guard by Luke Crane</li>
                  <li>Ironsworn by Shaun Tomkin</li>
                  <li>Heart by Grant Howitt & Christopher Taylor</li>
                </ul>
              </RevealText>
            </div>
          </div>
        </section>

        {/* Final cosmic message */}
        <section className="final-message">
          <RevealText>
            <p className="final-text">
              In each beginning, the Moon gives birth to the skies and earth. 
              Ether, the life-sustaining substance of the Moon, swells across the newborn world.
              With ether comes life. With that life comes profound light and shadow.
            </p>
          </RevealText>
        </section>
      </div>
    </div>
  );
};

export default CreatorsPage;
