import React, { useState, useEffect, useRef, useMemo } from 'react';
import './introSequence.css';

const IntroSequence = ({ showIntro, setShowIntro, setIsLoaded }) => {
  const moonCanvasRef = useRef(null);
  const animationFrameRef = useRef(null);
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [animationActive, setAnimationActive] = useState(false);

  // Use useMemo for quotes to prevent dependency issues
  const quotes = useMemo(() => [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend â€”\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ], []);

  // Initialize quote and trigger sequence
  useEffect(() => {
    if (showIntro && !animationActive) {
      // Select a random quote
      const randomIndex = Math.floor(Math.random() * quotes.length);
      setCurrentQuote(quotes[randomIndex]);

      // Show quote after a short delay
      const quoteTimer = setTimeout(() => {
        setQuoteVisible(true);
      }, 350);

      // Set flag that animation is active
      setAnimationActive(true);

      // Schedule the end of the sequence
      const hideQuoteTimer = setTimeout(() => {
        setQuoteVisible(false);
      }, 2400);

      const fadeOutTimer = setTimeout(() => {
        const introElement = document.querySelector('.intro-sequence');
        if (introElement) {
          introElement.classList.add('fade-out');
        }
      }, 2600);

      const endSequenceTimer = setTimeout(() => {
        setShowIntro(false);
        setIsLoaded(true);
        setAnimationActive(false);
      }, 3000);

      // Cleanup timers
      return () => {
        clearTimeout(quoteTimer);
        clearTimeout(hideQuoteTimer);
        clearTimeout(fadeOutTimer);
        clearTimeout(endSequenceTimer);
      };
    }
  }, [showIntro, quotes, setShowIntro, setIsLoaded, animationActive]);

  // Comet animation
  useEffect(() => {
    if (!showIntro || !moonCanvasRef.current || !animationActive) return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 70;

    let rotation = 0;
    let brightness = 1.0;
    let lastTimestamp = 0;
    let startTime = Date.now();

    // Comet trail parameters
    const trailLength = 120; // degrees
    const trailSegments = 20;

    // Animation function
    const animate = (timestamp) => {
      if (!moonCanvasRef.current) return;

      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Calculate elapsed time for dimming effect
      const elapsed = Date.now() - startTime;

      // Update rotation
      const speed = 0.12 * deltaTime;
      rotation = (rotation + speed) % 360;

      // Calculate brightness - dim after 2 seconds
      if (elapsed >= 2000) {
        const dimDuration = 600; // 600ms to dim
        const dimProgress = Math.min(1, (elapsed - 2000) / dimDuration);
        brightness = Math.max(0, 1 - dimProgress);
      }

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Calculate head position
      const headAngle = rotation * Math.PI / 180;
      const headX = centerX + radius * Math.cos(headAngle);
      const headY = centerY + radius * Math.sin(headAngle);

      // Draw trail segments
      for (let i = 0; i < trailSegments; i++) {
        // Calculate segment positions
        const segmentAngle = ((rotation - (i * (trailLength / trailSegments))) % 360) * Math.PI / 180;
        const segmentX = centerX + radius * Math.cos(segmentAngle);
        const segmentY = centerY + radius * Math.sin(segmentAngle);

        const nextSegmentAngle = ((rotation - ((i + 1) * (trailLength / trailSegments))) % 360) * Math.PI / 180;
        const nextSegmentX = centerX + radius * Math.cos(nextSegmentAngle);
        const nextSegmentY = centerY + radius * Math.sin(nextSegmentAngle);

        // Calculate segment opacity
        const segmentOpacity = 0.8 * (1 - (i / trailSegments)) * brightness;

        // Draw trail segment
        ctx.beginPath();
        ctx.moveTo(segmentX, segmentY);
        ctx.lineTo(nextSegmentX, nextSegmentY);

        // Width variation
        const segmentWidth = 2.5 * (1 - (i / trailSegments)) + 0.5;

        // Shadow/glow effect
        ctx.shadowColor = `rgba(255, 253, 227, ${segmentOpacity})`;
        ctx.shadowBlur = (10 * (1 - (i / trailSegments)) + 5) * brightness;

        // Line style
        ctx.strokeStyle = `rgba(191, 173, 127, ${segmentOpacity})`;
        ctx.lineWidth = segmentWidth;
        ctx.lineCap = 'round';
        ctx.stroke();
      }

      // Draw comet head
      ctx.shadowColor = `rgba(255, 253, 227, ${0.9 * brightness})`;
      ctx.shadowBlur = 20 * brightness;

      // Outer glow
      ctx.beginPath();
      ctx.arc(headX, headY, 4, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${0.7 * brightness})`;
      ctx.fill();

      // Middle layer
      ctx.beginPath();
      ctx.arc(headX, headY, 2.5, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${0.9 * brightness})`;
      ctx.fill();

      // Bright center
      ctx.beginPath();
      ctx.arc(headX, headY, 1, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 255, 255, ${brightness})`;
      ctx.fill();

      // Continue animation if not fully dimmed
      if (brightness > 0) {
        animationFrameRef.current = requestAnimationFrame(animate);
      }
    };

    // Start animation
    animationFrameRef.current = requestAnimationFrame(animate);

    // Cleanup
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [showIntro, animationActive]);

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  // Skip intro handler
  const handleSkipIntro = () => {
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }
    setShowIntro(false);
    setIsLoaded(true);
  };

  if (!showIntro) return null;

  return (
    <div className="intro-sequence" onClick={handleSkipIntro}>
      <div className="moon-animation">
        <canvas 
          ref={moonCanvasRef} 
          width="200" 
          height="200"
          className="moon-canvas"
        />
      </div>

      <div className={`quote-container ${quoteVisible ? 'visible' : ''}`}>
        <div className="quote-text">
          {currentQuote && formatQuote(currentQuote.text)}
        </div>
        <div className="quote-author">
          {currentQuote && currentQuote.author}
        </div>
      </div>

      <div className="skip-intro">tap to skip</div>
    </div>
  );
};

export default IntroSequence;
