import React, { useState, useEffect, useRef } from 'react';

const EnhancedLandingPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [showIntro, setShowIntro] = useState(true);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [quoteVisible, setQuoteVisible] = useState(false);

  const canvasRef = useRef(null);
  const moonRef = useRef(null);
  const particlesRef = useRef([]);
  const requestRef = useRef(null);

  // Collection of poetic quotes by women
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend —\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ];

  const sections = [
    {
      id: "creator",
      title: "Creator",
      description: "The mind behind the mysteries, shaping worlds from shadow and light.",
      icon: "◉", // Circle symbol
      path: "/creator"
    },
    {
      id: "creativity",
      title: "Creativity",
      description: "Visualizations of complex systems, revealing patterns beneath the chaos.",
      icon: "△", // Triangle symbol
      path: "/creativity"
    },
    {
      id: "cycles",
      title: "Cycles",
      description: "An intricate framework modeling the evolution of worlds through endless creation and destruction.",
      icon: "⌁", // Lightning symbol
      path: "/cycles"
    },
    {
      id: "codex",
      title: "Codex",
      description: "Repository of arcane knowledge, rules, and lore from across the multiverse.",
      icon: "⊕", // Circled plus symbol
      path: "/codex"
    },
    {
      id: "characters",
      title: "Characters",
      description: "Beings shaped by their environments, each with a unique story to tell.",
      icon: "✧", // Star symbol
      path: "/characters"
    }
  ];

  // Initialize moon animation and particles
  useEffect(() => {
    // Select a random quote
    const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
    setCurrentQuote(randomQuote);

    // Initialize particles
    particlesRef.current = Array(40).fill().map(() => ({
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 2 + 1,
      speedX: (Math.random() - 0.5) * 0.2,
      speedY: (Math.random() - 0.5) * 0.2,
      opacity: Math.random() * 0.4 + 0.1
    }));

    // Intro sequence
    setTimeout(() => setQuoteVisible(true), 500);
    setTimeout(() => setQuoteVisible(false), 5000);
    setTimeout(() => setShowIntro(false), 6000);
    setTimeout(() => setIsLoaded(true), 6500);

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, []);

  // Canvas animation
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles
      particlesRef.current.forEach(particle => {
        // Update position
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // Wrap around screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${particle.opacity})`;
        ctx.fill();
      });

      // Draw cursor influence - reduced intensity
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 100
        );
        gradient.addColorStop(0, 'rgba(160, 142, 97, 0.05)');
        gradient.addColorStop(1, 'rgba(160, 142, 97, 0)');

        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 100, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [cursorPosition]);

  // Moon animation for the footer
  useEffect(() => {
    if (!moonRef.current) return;

    const drawMoon = () => {
      const moon = moonRef.current;
      const ctx = moon.getContext('2d');

      moon.width = 300;
      moon.height = 300;

      // Clear canvas
      ctx.clearRect(0, 0, moon.width, moon.height);

      // Draw moon glow
      const gradient = ctx.createRadialGradient(
        150, 150, 60,
        150, 150, 130
      );
      gradient.addColorStop(0, 'rgba(191, 173, 127, 0.4)');
      gradient.addColorStop(1, 'rgba(191, 173, 127, 0)');

      ctx.beginPath();
      ctx.arc(150, 150, 130, 0, Math.PI * 2);
      ctx.fillStyle = gradient;
      ctx.fill();

      // Draw moon
      ctx.beginPath();
      ctx.arc(150, 150, 65, 0, Math.PI * 2);
      ctx.fillStyle = '#bfad7f';
      ctx.fill();

      // Draw moon craters
      const craters = [
        { x: 125, y: 130, r: 8 },
        { x: 170, y: 150, r: 12 },
        { x: 140, y: 180, r: 10 },
        { x: 160, y: 110, r: 7 },
        { x: 110, y: 160, r: 9 }
      ];

      craters.forEach(crater => {
        ctx.beginPath();
        ctx.arc(crater.x, crater.y, crater.r, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(134, 120, 87, 0.7)';
        ctx.fill();
      });
    };

    drawMoon();

    window.addEventListener('resize', drawMoon);
    return () => {
      window.removeEventListener('resize', drawMoon);
    };
  }, []);

  // Track mouse movement
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Skip intro
  const handleSkipIntro = () => {
    setShowIntro(false);
    setIsLoaded(true);
  };

  // Navigate to section
  const handleNavigate = (path) => {
    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  return (
    <div 
      className="landing-container"
      onMouseMove={handleMouseMove}
      onClick={showIntro ? handleSkipIntro : undefined}
    >
      <canvas ref={canvasRef} className="background-canvas" />

      {/* Intro Sequence */}
      {showIntro && (
        <div className="intro-sequence">
          <div className="moon-animation">
            <div className="moon">
              <div className="moon-crater moon-crater-1"></div>
              <div className="moon-crater moon-crater-2"></div>
              <div className="moon-crater moon-crater-3"></div>
            </div>
          </div>

          <div className={`quote-container ${quoteVisible ? 'visible' : ''}`}>
            <div className="quote-text">
              {currentQuote && formatQuote(currentQuote.text)}
            </div>
            <div className="quote-author">
              {currentQuote && currentQuote.author}
            </div>
          </div>

          <div className="skip-intro">Tap anywhere to skip</div>
        </div>
      )}

      <div className={`content-wrapper ${isLoaded ? 'loaded' : ''}`}>
        <div className="content-container">
          <div className="title-container">
            <h1 className="main-title">CRESCENT</h1>
            <div className="title-divider" />
            <p className="subtitle">A Dark Fantasy Experience</p>
          </div>

          <div className="sections-container">
            {sections.map((section, index) => (
              <div 
                key={section.id}
                className={`section-row ${activeSection === section.id ? 'active' : ''}`}
                onMouseEnter={() => setActiveSection(section.id)}
                onMouseLeave={() => setActiveSection(null)}
                onClick={() => handleNavigate(section.path)}
                style={{ animationDelay: `${index * 0.15 + 0.3}s` }}
              >
                <div className="section-content">
                  <div className="section-icon-wrapper">
                    <div className="section-icon">{section.icon}</div>
                  </div>
                  <div className="section-text">
                    <h2 className="section-title">{section.title}</h2>
                    <p className="section-description">{section.description}</p>
                  </div>
                </div>
                <div className="diagonal-cut" />
              </div>
            ))}
          </div>
        </div>

        {/* Footer Moon */}
        <div className="footer-section">
          <canvas ref={moonRef} className="footer-moon" width="300" height="300"></canvas>
          <div className="brand-mark">
            <span className="symbol">◊</span>
            <span className="year">2025</span>
          </div>
        </div>
      </div>

      <style jsx>{`
        .landing-container {
          position: relative;
          width: 100vw;
          min-height: 100vh;
          overflow-x: hidden;
          background-color: #080808;
          display: flex;
          justify-content: center;
          align-items: flex-start;
          font-family: 'Avenir', 'Avenir Next', sans-serif;
        }

        .background-canvas {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 1;
        }

        /* Intro Sequence Styles */
        .intro-sequence {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 1000;
          background-color: #080808;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          cursor: pointer;
        }

        .moon-animation {
          position: relative;
          width: 150px;
          height: 150px;
          margin-bottom: 4rem;
        }

        .moon {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background-color: #bfad7f;
          border-radius: 50%;
          box-shadow: 0 0 40px rgba(191, 173, 127, 0.5);
          animation: moonGlow 4s ease-in-out infinite alternate;
        }

        .moon-crater {
          position: absolute;
          background-color: rgba(134, 120, 87, 0.7);
          border-radius: 50%;
        }

        .moon-crater-1 {
          width: 20px;
          height: 20px;
          top: 30px;
          left: 40px;
        }

        .moon-crater-2 {
          width: 30px;
          height: 30px;
          bottom: 40px;
          right: 30px;
        }

        .moon-crater-3 {
          width: 15px;
          height: 15px;
          top: 70px;
          right: 50px;
        }

        .quote-container {
          max-width: 500px;
          text-align: center;
          opacity: 0;
          transform: translateY(20px);
          transition: opacity 2s ease, transform 2s ease;
          padding: 0 2rem;
        }

        .quote-container.visible {
          opacity: 1;
          transform: translateY(0);
        }

        .quote-text {
          font-family: 'Adobe Caslon Pro', 'Caslon', serif;
          font-size: 1.2rem;
          line-height: 1.6;
          color: rgba(191, 173, 127, 0.8);
          margin-bottom: 1rem;
          font-style: italic;
        }

        .quote-line {
          display: inline-block;
          animation: fadeTextIn 3s ease-out;
        }

        .quote-author {
          font-size: 0.9rem;
          color: rgba(224, 224, 224, 0.6);
          letter-spacing: 0.1em;
          text-align: right;
          padding-right: 2rem;
          animation: fadeTextIn 3s ease-out 0.5s backwards;
        }

        .skip-intro {
          position: absolute;
          bottom: 2rem;
          font-size: 0.8rem;
          color: rgba(224, 224, 224, 0.4);
          letter-spacing: 0.1em;
          animation: pulse 2s infinite;
        }

        /* Main Content Styles */
        .content-wrapper {
          z-index: 2;
          width: 100%;
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          opacity: 0;
          transition: opacity 1.2s ease;
        }

        .content-wrapper.loaded {
          opacity: 1;
        }

        .content-container {
          width: 100%;
          max-width: 1000px;
          margin: 0 auto;
          padding: 4rem 2rem 2rem;
        }

        .title-container {
          text-align: center;
          margin-bottom: 3rem;
        }

        .main-title {
          font-size: 3.5rem;
          letter-spacing: 0.2em;
          margin-bottom: 0.5rem;
          color: #bfad7f;
          font-weight: 300;
          text-transform: uppercase;
        }

        .title-divider {
          height: 1px;
          width: 120px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0), rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0));
          margin: 0.5rem auto;
        }

        .subtitle {
          font-size: 1.1rem;
          color: rgba(224, 224, 224, 0.7);
          font-family: 'Adobe Caslon Pro', 'Caslon', serif;
          font-style: italic;
        }

        .sections-container {
          display: flex;
          flex-direction: column;
          gap: 0.75rem;
          margin-bottom: 3rem;
        }

        .section-row {
          position: relative;
          height: 5rem;
          background-color: rgba(15, 15, 15, 0.5);
          border: 1px solid rgba(160, 142, 97, 0.3);
          border-radius: 4px;
          cursor: pointer;
          transition: all 0.3s ease;
          overflow: hidden;
          animation: fadeIn 0.8s ease backwards;
          display: flex;
          align-items: center;
          padding-right: 3rem; /* Space for diagonal cut */
        }

        .section-row::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 0;
          height: 2px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0));
          transition: width 0.6s ease;
        }

        .section-row.active {
          background-color: rgba(20, 20, 20, 0.7);
          transform: translateX(10px);
          box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), 0 0 15px rgba(160, 142, 97, 0.2);
          border-color: rgba(160, 142, 97, 0.5);
          height: 5.5rem;
        }

        .section-row.active::before {
          width: 100%;
        }

        .section-content {
          display: flex;
          align-items: center;
          width: 100%;
          height: 100%;
          position: relative;
          z-index: 2;
        }

        .section-icon-wrapper {
          width: 80px;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .section-icon {
          font-size: 1.5rem;
          color: #a08e61;
          opacity: 0.9;
          transition: all 0.3s ease;
        }

        .section-row.active .section-icon {
          transform: scale(1.2);
          color: #bfad7f;
        }

        .section-text {
          flex: 1;
        }

        .section-title {
          font-size: 1.25rem;
          margin-bottom: 0.25rem;
          color: #bfad7f;
          font-weight: 500;
          letter-spacing: 0.05em;
          transition: all 0.3s ease;
        }

        .section-row.active .section-title {
          transform: translateY(-2px);
        }

