          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 3rem 0;
          position: relative;
        }

        .footer-moon {
          margin-bottom: 2rem;
        }

        .brand-mark {
          display: flex;
          flex-direction: column;
          align-items: center;
          opacity: 0.5;
          transition: opacity 0.3s ease;
        }

        .brand-mark:hover {
          opacity: 0.8;
        }

        .symbol {
          font-size: 1.5rem;
          color: #a08e61;
          margin-bottom: 0.25rem;
        }

        .year {
          font-size: 0.75rem;
          color: rgba(224, 224, 224, 0.5);
          letter-spacing: 0.2em;
        }

        /* Animations */
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateX(-20px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes fadeTextIn {
          0% {
            opacity: 0;
          }
          20% {
            opacity: 0.7;
          }
          80% {
            opacity: 0.7;
          }
          100% {
            opacity: 1;
          }
        }

        @keyframes moonGlow {
          0% {
            box-shadow: 0 0 20px rgba(191, 173, 127, 0.3);
          }
          100% {
            box-shadow: 0 0 40px rgba(191, 173, 127, 0.6);
          }
        }

        @keyframes pulse {
          0% {
            opacity: 0.2;
          }
          50% {
            opacity: 0.5;
          }
          100% {
            opacity: 0.2;
          }
        }

        /* Responsive Adjustments */
        @media (max-width: 768px) {
          .main-title {
            font-size: 2.5rem;
          }

          .section-row {
            height: 4.5rem;
          }

          .section-row.active {
            height: 5rem;
          }

          .section-icon-wrapper {
            width: 60px;
          }

          .section-description {
            font-size: 0.75rem;
            max-width: 95%;
          }

          .quote-text {
            font-size: 1rem;
          }
        }

        @media (max-width: 480px) {
          .content-container {
            padding: 2rem 1rem 1rem;
          }

          .main-title {
            font-size: 2rem;
            letter-spacing: 0.15em;
          }

          .subtitle {
            font-size: 0.9rem;
          }

          .section-row {
            height: 4rem;
          }

          .section-icon-wrapper {
            width: 50px;
          }

          .section-icon {
            font-size: 1.2rem;
          }

          .section-title {
            font-size: 1rem;
          }

          .moon-animation {
            width: 120px;
            height: 120px;
          }

          .footer-moon {
            width: 250px;
            height: 250px;
          }
        }
      `}</style>
    </div>
  );
};

export default EnhancedLandingPage;
