import React, { useRef, useState, useEffect, useCallback, useMemo } from 'react';
import { motion, useInView, useAnimation } from 'framer-motion';

/**
 * PersonProfileCard Component
 * 
 * A profile card with a three-phase scroll behavior on desktop:
 * 1. Normal Flow: Initially scrolls with the page
 * 2. Fixed Position: Sticks to viewport when scrolling through content
 * 3. Release: Returns to normal flow after scrolling past component
 * 
 * On mobile:
 * - Navigation moves to top with toggle functionality
 * - Content expands full-width for better readability
 */
const PersonProfileCard = ({
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  additionalSections = [],
  onSectionChange = null,
  topOffset = 100,
  showStats = true,
  highlightColor = '#bfad7f',
  textColor = 'rgba(224, 224, 224, 0.7)',
  minLineWidth = 10,
  maxLineWidth = 40,
  fontFamily = '"Garamond", "Adobe Caslon Pro", serif',
  navigationItems = [
    { id: 'about', label: 'ABOUT', content: null },
    { id: 'experience', label: 'EXPERIENCE', content: null },
    { id: 'projects', label: 'PROJECTS', content: null }
  ],
  contentCompression = 0
}) => {
  // State management
  const [activeSection, setActiveSection] = useState(navigationItems[0]?.id || 'about');
  const [expandedNavItem, setExpandedNavItem] = useState(null);
  const [sidebarMode, setSidebarMode] = useState('normal'); // 'normal', 'fixed', or 'end'
  const [isInitialized, setIsInitialized] = useState(false);
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Calculate widths based on contentCompression (0-10 scale)
  const compressionFactor = Math.min(Math.max(contentCompression, 0), 10) / 10;
  const sidebarWidth = 45 - (compressionFactor * 5); // 35-40% range
  const contentWidth = 55 - (compressionFactor * 5); // 55-60% range
  const contentPadding = 2 + (compressionFactor * 2); // 2-4rem range

  // Refs for DOM elements
  const containerRef = useRef(null);
  const sidebarRef = useRef(null);
  const contentRef = useRef(null);
  const sidebarWrapperRef = useRef(null);
  const mobileNavRef = useRef(null);

  // Animation controls
  const controls = useAnimation();

  // Create individual section refs at the top level
  const section0Ref = useRef(null);
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);
  const section3Ref = useRef(null);
  const section4Ref = useRef(null);
  const section5Ref = useRef(null);
  const section6Ref = useRef(null);
  const section7Ref = useRef(null);

  // Memoize the array of refs so it's stable across renders
  const sectionRefs = useMemo(() => [
    section0Ref, section1Ref, section2Ref, section3Ref,
    section4Ref, section5Ref, section6Ref, section7Ref
  ], []);

  // Map section IDs to refs
  const sectionRefsMap = useMemo(() => {
    const refsMap = {};
    navigationItems.forEach((item, index) => {
      if (index < sectionRefs.length) {
        refsMap[item.id] = sectionRefs[index];
      }
    });
    return refsMap;
  }, [navigationItems, sectionRefs]);

  // Animation inView detection
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Check if mobile on mount and window resize
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    // Initial check
    checkMobile();

    // Add resize listener
    window.addEventListener('resize', checkMobile);

    return () => {
      window.removeEventListener('resize', checkMobile);
    };
  }, []);

  // Close mobile nav when changing sections on mobile
  useEffect(() => {
    if (isMobile) {
      setIsMobileNavOpen(false);
    }
  }, [activeSection, isMobile]);

  // Navigation item hover handlers
  const handleNavItemHover = useCallback((section) => {
    if (!isMobile) {
      setExpandedNavItem(section);
    }
  }, [isMobile]);

  const handleNavItemLeave = useCallback(() => {
    setExpandedNavItem(null);
  }, []);

  // Toggle mobile navigation
  const toggleMobileNav = useCallback(() => {
    setIsMobileNavOpen(prev => !prev);
  }, []);

  // Scroll to section handler with memoization
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    const sectionRef = sectionRefsMap[sectionId];
    if (sectionRef?.current) {
      // Adjust scroll position for mobile to account for the nav bar
      const yOffset = isMobile ? -60 : 0; 
      const y = sectionRef.current.getBoundingClientRect().top + window.pageYOffset + yOffset;

      window.scrollTo({
        top: y,
        behavior: 'smooth'
      });
    }
  }, [onSectionChange, sectionRefsMap, isMobile]);

  // Initial animation
  useEffect(() => {
    if (isInView) {
      controls.start({ 
        opacity: 1, 
        y: 0,
        transition: { 
          duration: animationConfig?.duration ?? 0.8,
          ease: "easeOut",
          delay: 0.1 // Small delay to ensure DOM is settled
        }
      });

      // Mark as initialized after animation completes
      const timeout = setTimeout(() => {
        setIsInitialized(true);
      }, (animationConfig?.duration ?? 0.8) * 1000 + 100);

      return () => clearTimeout(timeout);
    }
  }, [isInView, controls, animationConfig?.duration]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    const sectionElements = Object.values(sectionRefsMap)
      .map(ref => ref.current)
      .filter(Boolean);

    if (sectionElements.length === 0) return;

    const options = {
      root: null,
      rootMargin: isMobile ? '-80px 0px -70% 0px' : '-10% 0px -70% 0px',
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);
    sectionElements.forEach(element => observer.observe(element));

    return () => observer.disconnect();
  }, [activeSection, onSectionChange, sectionRefsMap, isMobile]);

  // Implement scroll behavior with debouncing for performance
  useEffect(() => {
    if (!isInitialized || !containerRef.current || !sidebarRef.current || !sidebarWrapperRef.current) {
      return;
    }

    // Skip on mobile
    if (isMobile) {
      return;
    }

    const container = containerRef.current;
    const sidebar = sidebarRef.current;
    const sidebarWrapper = sidebarWrapperRef.current;

    let containerRect;
    let wrapperRect;
    let sidebarHeight;

    // Update measurements
    const updateMeasurements = () => {
      containerRect = container.getBoundingClientRect();
      wrapperRect = sidebarWrapper.getBoundingClientRect();
      sidebarHeight = sidebar.offsetHeight;
    };

    // First, get accurate measurements
    updateMeasurements();

    const handleScroll = () => {
      // Skip on mobile
      if (isMobile) return;

      // Update measurements if necessary
      updateMeasurements();

      // Calculate phase transition points
      const startFixPoint = containerRect.top <= topOffset;
      const endFixPoint = containerRect.bottom <= (sidebarHeight + topOffset);

      // Determine the current scroll phase
      let newMode;
      if (!startFixPoint) {
        newMode = 'normal';
      } else if (startFixPoint && !endFixPoint) {
        newMode = 'fixed';
      } else {
        newMode = 'end';
      }

      // Only update DOM if the mode changes
      if (sidebarMode !== newMode) {
        setSidebarMode(newMode);

        if (newMode === 'normal') {
          // Phase 1: Normal flow
          sidebar.style.position = 'relative';
          sidebar.style.top = '0';
          sidebar.style.left = '0';
          sidebar.style.width = '';
          sidebar.style.bottom = '';
        } 
        else if (newMode === 'fixed') {
          // Phase 2: Fixed position
          sidebar.style.position = 'fixed';
          sidebar.style.top = `${topOffset}px`;
          sidebar.style.width = `${wrapperRect.width}px`;
          sidebar.style.left = `${wrapperRect.left}px`;
          sidebar.style.bottom = '';
        } 
        else if (newMode === 'end') {
          // Phase 3: End position
          sidebar.style.position = 'absolute';
          sidebar.style.top = 'auto';
          sidebar.style.bottom = '0';
          sidebar.style.left = '0';
          sidebar.style.width = '';
        }
      }
    };

    // Add scroll event listener with throttling
    let ticking = false;
    const scrollListener = () => {
      if (!ticking) {
        window.requestAnimationFrame(() => {
          handleScroll();
          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', scrollListener, { passive: true });

    // Handle window resize
    const handleResize = () => {
      // Skip if mobile
      if (isMobile) return;

      // Update measurements
      updateMeasurements();

      // Apply current mode
      handleScroll();
    };

    window.addEventListener('resize', handleResize);

    // Initial setup
    handleResize();
    handleScroll();

    return () => {
      window.removeEventListener('scroll', scrollListener);
      window.removeEventListener('resize', handleResize);
    };
  }, [isInitialized, topOffset, sidebarMode, isMobile]);

  // Render mobile navigation toggle button
  const renderMobileNavToggle = () => (
    <button
      onClick={toggleMobileNav}
      className="mobile-nav-toggle"
      aria-label={isMobileNavOpen ? "Close navigation" : "Open navigation"}
      style={{
        background: 'transparent',
        border: 'none',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        alignItems: 'center',
        width: '30px',
        height: '24px',
        padding: '0',
        cursor: 'pointer',
        position: 'absolute',
        right: '20px',
        top: '20px',
        zIndex: 1000,
      }}
    >
      <span
        style={{
          display: 'block',
          width: '100%',
          height: '2px',
          backgroundColor: highlightColor,
          transition: 'transform 0.3s ease, opacity 0.3s ease',
          transform: isMobileNavOpen ? 'translateY(10px) rotate(45deg)' : 'none',
        }}
      />
      <span
        style={{
          display: 'block',
          width: '100%',
          height: '2px',
          backgroundColor: highlightColor,
          opacity: isMobileNavOpen ? 0 : 1,
          transition: 'opacity 0.3s ease',
        }}
      />
      <span
        style={{
          display: 'block',
          width: '100%',
          height: '2px',
          backgroundColor: highlightColor,
          transition: 'transform 0.3s ease',
          transform: isMobileNavOpen ? 'translateY(-10px) rotate(-45deg)' : 'none',
        }}
      />
    </button>
  );

  // Render profile info section
  const renderProfileInfo = () => (
    <div 
      className="profile-info"
      style={{
        marginBottom: '2rem',
        paddingLeft: '5px',
      }}
    >
      <h1 
        className="profile-name"
        style={{
          fontSize: isMobile ? '2.2rem' : '2.8rem',
          fontWeight: '300',
          color: highlightColor,
          letterSpacing: '0.05em',
          marginBottom: '0.5rem',
        }}
      >
        {person.name}
      </h1>

      <h2 
        className="profile-role"
        style={{
          fontSize: isMobile ? '1.1rem' : '1.2rem',
          fontWeight: '300',
          marginBottom: '2rem',
          color: `${highlightColor}B3`, // 70% opacity
          letterSpacing: '0.05em',
          fontFamily: fontFamily,
          fontStyle: 'italic',
        }}
      >
        {person.role}
      </h2>

      {person.tagline && (
        <p 
          className="profile-tagline"
          style={{
            fontSize: '1.1rem',
            lineHeight: '1.6',
            marginBottom: '3rem',
            maxWidth: isMobile ? '100%' : '90%',
            color: textColor,
            fontFamily: fontFamily,
            fontWeight: '300',
          }}
        >
          {person.tagline}
        </p>
      )}
    </div>
  );

  // Render navigation links
  const renderNavLinks = () => (
    <div 
      className="nav-links" 
      style={{ 
        marginTop: '3rem',
        display: isMobile && !isMobileNavOpen ? 'none' : 'block',
      }}
    >
      {navigationItems.map((navItem) => (
        <div 
          key={navItem.id}
          className="nav-link-container"
          style={{
            position: 'relative',
            marginBottom: '1rem',
            display: 'flex',
            alignItems: 'center',
          }}
          onMouseEnter={() => handleNavItemHover(navItem.id)}
          onMouseLeave={handleNavItemLeave}
        >
          <div
            className="nav-line"
            style={{
              position: 'absolute',
              left: '0',
              top: '50%',
              transform: 'translateY(-50%)',
              width: activeSection === navItem.id || expandedNavItem === navItem.id 
                ? `${maxLineWidth}px` 
                : `${minLineWidth}px`,
              height: '0.75px',
              backgroundColor: activeSection === navItem.id || expandedNavItem === navItem.id
                ? `${highlightColor}E6` // 90% opacity 
                : `${highlightColor}80`, // 50% opacity
              transition: 'width 0.3s ease, background-color 0.3s ease',
            }}
          ></div>
          <button 
            className="nav-link"
            onClick={() => scrollToSection(navItem.id)}
            style={{
              position: 'relative',
              display: 'block',
              padding: '0.5rem 0 0.5rem 40px',
              fontSize: '0.85rem',
              letterSpacing: '0.1em',
              background: 'transparent',
              border: 'none',
              textAlign: 'left',
              outline: 'none',
              boxShadow: 'none',
              transition: 'color 0.5s ease, transform 0.5s ease',
              color: activeSection === navItem.id || expandedNavItem === navItem.id 
                ? highlightColor 
                : textColor,
              cursor: 'pointer',
              transform: activeSection === navItem.id || expandedNavItem === navItem.id 
                ? 'translateX(10px)' 
                : 'none',
              width: 'fit-content',
            }}
          >
            {navItem.label}
          </button>
        </div>
      ))}
    </div>
  );

  // Render mobile navigation
  const renderMobileNav = () => {
    if (!isMobile) return null;

    return (
      <div 
        ref={mobileNavRef}
        className="mobile-nav"
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          zIndex: 100,
          backgroundColor: 'rgba(25, 25, 25, 0.95)',
          boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
          transition: 'transform 0.3s ease, opacity 0.3s ease',
          padding: '15px 20px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <div className="mobile-nav-title">
          <h3 
            style={{
              margin: 0,
              fontSize: '1.2rem',
              color: highlightColor,
              fontWeight: '300',
            }}
          >
            {person.name}
          </h3>
          <h4
            style={{
              margin: 0,
              fontSize: '0.85rem',
              fontWeight: '300',
              color: `${highlightColor}B3`,
              marginTop: '2px',
            }}
          >
            {activeSection ? navigationItems.find(item => item.id === activeSection)?.label : ''}
          </h4>
        </div>

        {renderMobileNavToggle()}

        <div 
          className="mobile-nav-menu"
          style={{
            position: 'absolute',
            top: '100%',
            left: 0,
            width: '100%',
            backgroundColor: 'rgba(25, 25, 25, 0.98)',
            padding: '1rem 0',
            display: isMobileNavOpen ? 'block' : 'none',
            boxShadow: '0 4px 8px rgba(0,0,0,0.3)',
            zIndex: 90,
            maxHeight: 'calc(100vh - 60px)',
            overflowY: 'auto',
          }}
        >
          {navigationItems.map((navItem) => (
            <button 
              key={navItem.id}
              onClick={() => scrollToSection(navItem.id)}
              style={{
                display: 'block',
                width: '100%',
                padding: '0.8rem 2rem',
                textAlign: 'left',
                background: 'transparent',
                border: 'none',
                color: activeSection === navItem.id ? highlightColor : textColor,
                fontSize: '1rem',
                cursor: 'pointer',
                borderLeft: activeSection === navItem.id 
                  ? `3px solid ${highlightColor}` 
                  : '3px solid transparent',
                transition: 'background-color 0.2s ease, color 0.2s ease',
              }}
            >
              {navItem.label}
            </button>
          ))}
        </div>
      </div>
    );
  };

  // Render stats section
  const renderStats = () => {
    if (!showStats || !person.stats || person.stats.length === 0) return null;

    return (
      <div 
        className="stats-container"
        style={{
          display: 'flex',
          justifyContent: isMobile ? 'center' : 'space-between',
          flexWrap: 'wrap',
          marginTop: '3rem',
          width: '100%',
          gap: isMobile ? '2rem' : '0',
        }}
      >
        {person.stats.map((stat, statIdx) => (
          <div 
            key={statIdx} 
            className="stat"
            style={{
              flex: '1',
              textAlign: 'center',
              padding: '0 1rem',
              minWidth: isMobile ? '85px' : '100px',
              transition: 'transform 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-5px)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'none';
            }}
          >
            <div 
              className="stat-value"
              style={{
                fontSize: isMobile ? '2.2rem' : '2.5rem',
                fontWeight: '100',
                color: highlightColor,
                marginBottom: '0.5rem'
              }}
            >
              {stat.value}
            </div>
            <div 
              className="stat-label"
              style={{
                fontSize: '0.85rem',
                color: `${textColor}CC`, // 80% opacity
                textTransform: 'uppercase',
                letterSpacing: '0.1em'
              }}
            >
              {stat.label}
            </div>
          </div>
        ))}
      </div>
    );
  };

  // Render section content
  const renderSectionContent = (content) => {
    if (!content) return null;

    if (typeof content === 'string') {
      return (
        <p 
          className="section-content"
          style={{
            fontSize: '1rem',
            lineHeight: '1.8',
            marginBottom: '1.5rem',
            color: textColor,
            fontFamily: fontFamily,
            fontWeight: '300',
          }}
        >
          {content}
        </p>
      );
    }

    return content;
  };

  // Render about section
  const renderAboutSection = (section) => {
    return (
      <>
        {person.bio && person.bio.map((paragraph, idx) => (
          <p 
            key={idx} 
            className="section-content"
            style={{
              fontSize: '1rem',
              lineHeight: '1.8',
              marginBottom: '1.5rem',
              color: textColor,
              fontFamily: fontFamily,
              fontWeight: '300',
            }}
          >
            {paragraph}
          </p>
        ))}

        {renderStats()}
      </>
    );
  };

  // Render experience section
  const renderExperienceSection = (section) => {
    if (person.experience) {
      return person.experience.map((item, expIdx) => (
        renderSectionContent(item.content)
      ));
    }

    return renderSectionContent(
      "Throughout my career, I've specialized in developing software systems that seamlessly " +
      "integrate front-end experiences with robust back-end architectures. My experience spans " +
      "various domains, from interactive media to data visualization systems."
    );
  };

  // Render projects section
  const renderProjectsSection = (section) => {
    if (person.projects) {
      return person.projects.map((item, projIdx) => (
        renderSectionContent(item.content)
      ));
    }

    return null;
  };

  // Render sections based on section ID
  const renderSection = (section) => {
    if (section.content) {
      return renderSectionContent(section.content);
    }

    switch (section.id) {
      case 'about':
        return renderAboutSection(section);
      case 'experience':
        return renderExperienceSection(section);
      case 'projects':
        return renderProjectsSection(section);
      default:
        return null;
    }
  };

  return (
    <motion.div 
      ref={containerRef}
      className="profile-container"
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={controls}
      style={{
        display: 'flex',
        flexDirection: isMobile ? 'column' : 'row',
        width: '100%',
        maxWidth: '1300px',
        margin: '0 auto',
        minHeight: '70vh',
        position: 'relative',
        paddingTop: isMobile ? '60px' : '0', // Space for fixed mobile nav
      }}
    >
      {/* Mobile navigation (fixed at top) */}
      {renderMobileNav()}

      {/* Left sidebar wrapper - hidden on mobile */}
      <div 
        ref={sidebarWrapperRef}
        className="sidebar-wrapper"
        style={{
          width: isMobile ? '100%' : `${sidebarWidth}%`,
          position: 'relative',
          display: isMobile ? 'none' : 'block', // Hide on mobile
        }}
      >
        {/* Profile sidebar */}
        <div 
          ref={sidebarRef}
          className="profile-sidebar"
          style={{
            padding: '3rem 2rem 2rem 0',
            display: 'flex',
            flexDirection: 'column',
            width: '100%',
            position: 'relative',
            top: 0,
          }}
        >
          {renderProfileInfo()}
          {renderNavLinks()}
        </div>
      </div>

      {/* Mobile profile info (only shown on mobile) */}
      {isMobile && (
        <div 
          className="mobile-profile-info"
          style={{
            width: '100%',
            padding: '1rem 1.5rem 2rem',
            textAlign: 'center',
          }}
        >
          {renderProfileInfo()}
        </div>
      )}

      {/* Right content section */}
      <div 
        ref={contentRef}
        className="content-section"
        style={{
          width: isMobile ? '100%' : `${contentWidth}%`,
          padding: isMobile 
            ? '0 1.5rem 2rem' 
            : `3rem 0 2rem ${contentPadding}rem`,
          marginLeft: isMobile ? '0' : 'auto',
        }}
      >
        {/* Render sections based on navigationItems */}
        {navigationItems.map((section, index) => {
          if (index >= sectionRefs.length) return null;

          return (
            <div 
              key={section.id}
              ref={sectionRefs[index]}
              data-section={section.id}
              className="section"
              id={section.id}
              style={{
                marginBottom: '3rem',
                scrollMarginTop: isMobile ? '65px' : '2rem',
              }}
            >
              {/* Section title for mobile */}
              {isMobile && (
                <h3 
                  style={{
                    fontSize: '1.4rem',
                    color: highlightColor,
                    marginBottom: '1.5rem',
                    fontWeight: '300',
                    borderBottom: `1px solid ${highlightColor}40`,
                    paddingBottom: '0.5rem',
                  }}
                >
                  {section.label}
                </h3>
              )}

              {renderSection(section)}
            </div>
          );
        })}

        {/* Additional sections */}
        {additionalSections.map((section, index) => (
          <div 
            key={`additional-${index}`}
            className="section"
            style={{
              marginBottom: '3rem',
              scrollMarginTop: isMobile ? '65px' : '2rem',
            }}
          >
            {section.title && (
              <h3 style={{
                fontSize: '1.4rem',
                color: highlightColor,
                marginBottom: '1rem',
                fontWeight: '300',
                ...(isMobile && {
                  borderBottom: `1px solid ${highlightColor}40`,
                  paddingBottom: '0.5rem',
                }),
              }}>
                {section.title}
              </h3>
            )}
            {renderSectionContent(section.content)}
          </div>
        ))}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
