import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { createUseStyles } from 'react-jss';

// Styles for the showcase navigation bar
const useStyles = createUseStyles({
  // Main container
  navContainer: {
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
    backgroundColor: 'rgba(10, 10, 15, 0.9)',
    zIndex: 100,
  },

  // Top navigation bar
  topNav: {
    display: 'flex',
    padding: '0.75rem 1.5rem',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottom: '1px solid rgba(191, 173, 127, 0.2)',
  },

  // Logo and title
  titleSection: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
  },
  logoIcon: {
    width: '24px',
    height: '24px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #bfad7f 0%, #7D6B9E 100%)',
    boxShadow: '0 0 10px rgba(191, 173, 127, 0.5)',
  },
  title: {
    color: '#bfad7f',
    fontSize: '1.5rem',
    margin: 0,
    fontWeight: 300,
    letterSpacing: '0.05em',
    textShadow: '0 2px 5px rgba(0, 0, 0, 0.5)',
  },

  // Main selector area
  selectorArea: {
    display: 'flex',
    gap: '1rem',
    alignItems: 'center',
  },

  // Dropdown selectors
  selector: {
    position: 'relative',
    minWidth: '220px',
  },
  selectorButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    width: '100%',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)',
    },
  },
  selectorLabel: {
    fontWeight: 'normal',
    marginRight: '0.5rem',
  },
  selectorValue: {
    fontWeight: 'bold',
  },
  dropdownIcon: {
    fontSize: '0.7rem',
    marginLeft: '0.25rem',
  },

  // Dropdown menu
  dropdownMenu: {
    position: 'absolute',
    top: '100%',
    left: 0,
    width: '100%',
    backgroundColor: 'rgba(15, 15, 20, 0.95)',
    borderRadius: '4px',
    padding: '0.5rem',
    marginTop: '0.25rem',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)',
    zIndex: 101,
    border: '1px solid rgba(191, 173, 127, 0.3)',
    maxHeight: '60vh',
    overflowY: 'auto',
  },
  menuItem: {
    padding: '0.75rem',
    cursor: 'pointer',
    borderRadius: '3px',
    transition: 'all 0.15s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  menuItemActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
  },
  menuItemName: {
    fontSize: '0.95rem',
    color: '#bfad7f',
    fontWeight: 500,
    marginBottom: '0.25rem',
  },
  menuItemDesc: {
    fontSize: '0.8rem',
    color: 'rgba(224, 224, 224, 0.7)',
  },

  // Main tabs
  tabsSection: {
    display: 'flex',
    margin: '0 auto',
  },
  tab: {
    padding: '0.75rem 1.25rem',
    backgroundColor: 'transparent',
    border: 'none',
    borderBottom: '2px solid transparent',
    color: 'rgba(224, 224, 224, 0.7)',
    cursor: 'pointer',
    fontSize: '0.95rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      color: '#bfad7f',
      borderBottomColor: 'rgba(191, 173, 127, 0.3)',
    },
  },
  tabActive: {
    color: '#bfad7f',
    borderBottomColor: '#bfad7f',
  },

  // Zoom controls
  zoomControls: {
    display: 'flex',
    gap: '0.5rem',
    marginLeft: '1rem',
  },
  zoomButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    width: '32px',
    height: '32px',
    borderRadius: '4px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)',
    },
  },

  // Visualization controls section
  controlsSection: {
    display: 'flex',
    padding: '1rem 1.5rem',
    borderBottom: '1px solid rgba(191, 173, 127, 0.2)',
    backgroundColor: 'rgba(15, 15, 20, 0.5)',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexWrap: 'wrap',
  },

  // Visualization mode controls
  visualModeSection: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem',
  },
  sectionTitle: {
    color: 'rgba(191, 173, 127, 0.8)',
    fontSize: '0.9rem',
    margin: 0,
  },
  visualButtons: {
    display: 'flex',
    gap: '0.5rem',
  },
  visualButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  visualButtonActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    borderColor: 'rgba(191, 173, 127, 0.7)',
  },

  // Trait probabilities section
  probabilitiesSection: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem',
    minWidth: '250px',
  },
  probabilityLabel: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  probabilityName: {
    color: '#e0e0e0',
    fontSize: '0.9rem',
  },
  probabilityValue: {
    backgroundColor: 'rgba(125, 107, 158, 0.3)',
    color: '#e0e0e0',
    padding: '0.1rem 0.5rem',
    borderRadius: '2px',
    fontSize: '0.8rem',
  },
  probabilityBar: {
    width: '100%',
    height: '8px',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    borderRadius: '4px',
    overflow: 'hidden',
    position: 'relative',
  },
  probabilityFill: {
    position: 'absolute',
    height: '100%',
    backgroundColor: '#7D6B9E',
    borderRadius: '4px',
    transition: 'width 0.3s ease',
  },

  // Filter pill buttons
  filterPills: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    padding: '0.5rem 1rem',
    borderBottom: '1px solid rgba(191, 173, 127, 0.2)',
  },
  filterPill: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.35rem 0.75rem',
    borderRadius: '20px',
    fontSize: '0.85rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  filterPillActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    borderColor: 'rgba(191, 173, 127, 0.6)',
  },

  // Main content area
  contentArea: {
    padding: '1.5rem',
    overflowY: 'auto',
    transition: 'all 0.3s ease',
  },

  // Traits panel
  traitsPanel: {
    display: 'flex',
    flexDirection: 'column',
    gap: '2rem',
  },

  // Category sections
  categorySection: {
    marginBottom: '1.5rem',
  },
  categoryHeader: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
    marginBottom: '1rem',
  },
  categoryIcon: {
    width: '18px',
    height: '18px',
    borderRadius: '50%',
  },
  categoryName: {
    color: '#e0e0e0',
    fontSize: '1.1rem',
    margin: 0,
  },

  // Trait cards grid
  traitGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '1rem',
  },
  traitCard: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    borderRadius: '4px',
    padding: '1rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.15)',
      borderColor: 'rgba(191, 173, 127, 0.5)',
      transform: 'translateY(-2px)',
    },
  },
  traitCardActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.2)',
    borderColor: 'rgba(191, 173, 127, 0.6)',
    boxShadow: '0 0 15px rgba(191, 173, 127, 0.2)',
  },
  traitHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '0.75rem',
  },
  traitName: {
    color: '#bfad7f',
    fontSize: '1rem',
    margin: 0,
    fontWeight: 500,
  },
  traitTier: {
    backgroundColor: 'rgba(191, 173, 127, 0.2)',
    color: '#bfad7f',
    padding: '0.1rem 0.5rem',
    borderRadius: '2px',
    fontSize: '0.8rem',
  },
  traitDesc: {
    color: 'rgba(224, 224, 224, 0.8)',
    fontSize: '0.9rem',
    margin: 0,
    lineHeight: 1.5,
  },

  // Environment panel
  environmentPanel: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '2rem',
  },

  // Pressure slider
  sliderContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem',
  },
  sliderHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  sliderLabel: {
    color: '#e0e0e0',
    fontSize: '1rem',
  },
  sliderValue: {
    color: '#bfad7f',
    fontWeight: 'bold',
    fontSize: '1rem',
  },
  sliderTrack: {
    width: '100%',
    height: '8px',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    borderRadius: '4px',
    position: 'relative',
    margin: '0.5rem 0',
  },
  sliderFill: {
    position: 'absolute',
    height: '100%',
