import React, { useState, useEffect, useRef } from 'react';
import './introSequence.css';

const IntroSequence = ({ showIntro, setShowIntro, setIsLoaded }) => {
  const moonCanvasRef = useRef(null);
  const moonAnimationRef = useRef(null);
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [moonDrawn, setMoonDrawn] = useState(0); // 0: not started, 1: drawing, 2: complete

  // Moved quotes from LandingPage to IntroSequence
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend â€”\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ];

  // Setup intro sequence
  useEffect(() => {
    if (showIntro) {
      // Select a random quote
      const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
      setCurrentQuote(randomQuote);

      // Fast intro sequence (slightly quicker than before)
      setTimeout(() => setMoonDrawn(1), 150); // Start drawing animation quickly
      setTimeout(() => setQuoteVisible(true), 350); // Show quote almost immediately
      setTimeout(() => setMoonDrawn(2), 2000); // Complete animation faster (was 2500ms)
      setTimeout(() => {
        setQuoteVisible(false);
        // Start fade out of intro
        const introElement = document.querySelector('.intro-sequence');
        if (introElement) introElement.classList.add('fade-out');
      }, 2100);
      setTimeout(() => {
        setShowIntro(false);
        setIsLoaded(true);
      }, 2500); // Complete entire sequence faster (was 3000ms)
    }
  }, [showIntro, setShowIntro, setIsLoaded]);

  // Circular loading animation with painterly effect
  useEffect(() => {
    if (!moonCanvasRef.current || moonDrawn === 0) return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 70;

    // Animation parameters
    let rotation = 0;
    let lastTimestamp = 0;
    let twinkleOpacity = 0.8;
    let twinkleDirection = -1; // Start by fading out

    // Arc parameters
    const arcLength = 240; // Length of arc in degrees (leaving a gap)

    // Painterly effect parameters
    const baseWidth = 2;
    const widthVariation = 0.5;
    const brushTexture = 6; // Number of brush texture points

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const drawLoadingAnimation = (timestamp) => {
      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Rotation speed - consistent circular motion
      const speed = 0.15 * deltaTime; // Adjust for desired speed
      rotation = (rotation + speed) % 360;

      // Handle twinkling effect
      twinkleOpacity += 0.04 * twinkleDirection;
      if (twinkleOpacity <= 0.4) {
        twinkleDirection = 1; // Start brightening
      } else if (twinkleOpacity >= 0.9) {
        twinkleDirection = -1; // Start dimming
      }

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw background circle (very subtle)
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.1)';
      ctx.lineWidth = 1;
      ctx.stroke();

      // Draw the main arc with painterly effect
      const startAngle = rotation * Math.PI / 180;
      const endAngle = (rotation + arcLength) * Math.PI / 180;

      // Drawing multiple slightly offset arcs for a painterly brush effect
      for (let i = 0; i < brushTexture; i++) {
        const randomOffset = (Math.random() * 2 - 1) * 0.02; // Small random offset
        const randomWidth = baseWidth + (Math.random() * widthVariation);
        const randomAlpha = 0.2 + (Math.random() * 0.5);

        ctx.beginPath();
        ctx.arc(
          centerX, 
          centerY, 
          radius + (Math.random() * 1.5 - 0.75), // Slight radius variation
          startAngle + randomOffset, 
          endAngle + randomOffset
        );
        ctx.strokeStyle = `rgba(191, 173, 127, ${randomAlpha})`;
        ctx.lineWidth = randomWidth;
        ctx.stroke();
      }

      // Main arc (more visible)
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, startAngle, endAngle);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = baseWidth;
      ctx.stroke();

      // Draw twinkling start point
      const startX = centerX + radius * Math.cos(startAngle);
      const startY = centerY + radius * Math.sin(startAngle);

      // Glow effect
      ctx.shadowColor = `rgba(255, 253, 227, ${twinkleOpacity})`;
      ctx.shadowBlur = 15;

      // Larger inner glow
      ctx.beginPath();
      ctx.arc(startX, startY, 4, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${twinkleOpacity * 0.7})`;
      ctx.fill();

      // Brighter center point
      ctx.beginPath();
      ctx.arc(startX, startY, 2, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${twinkleOpacity})`;
      ctx.fill();

      // Stop animation if needed
      if (moonDrawn === 2) {
        cancelAnimationFrame(moonAnimationRef.current);

        // Fade out effect when stopping
        let fadeOpacity = 1;
        const fadeOut = () => {
          fadeOpacity -= 0.05;
          if (fadeOpacity <= 0) {
            return;
          }

          ctx.clearRect(0, 0, canvas.width, canvas.height);
          ctx.globalAlpha = fadeOpacity;

          // Redraw the arc
          ctx.beginPath();
          ctx.arc(centerX, centerY, radius, startAngle, endAngle);
          ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
          ctx.lineWidth = baseWidth;
          ctx.stroke();

          requestAnimationFrame(fadeOut);
        };

        requestAnimationFrame(fadeOut);
        return;
      }

      moonAnimationRef.current = requestAnimationFrame(drawLoadingAnimation);
    };

    if (moonDrawn === 1) {
      moonAnimationRef.current = requestAnimationFrame(drawLoadingAnimation);
    }

    return () => {
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, [moonDrawn]);

  // Skip intro
  const handleSkipIntro = () => {
    setShowIntro(false);
    setIsLoaded(true);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  if (!showIntro) return null;

  return (
    <div className="intro-sequence" onClick={handleSkipIntro}>
      <div className="moon-animation">
        <canvas 
          ref={moonCanvasRef} 
          width="200" 
          height="200"
          className="moon-canvas"
        />
      </div>

      <div className={`quote-container ${quoteVisible ? 'visible' : ''}`}>
        <div className="quote-text">
          {currentQuote && formatQuote(currentQuote.text)}
        </div>
        <div className="quote-author">
          {currentQuote && currentQuote.author}
        </div>
      </div>

      <div className="skip-intro">tap to skip</div>
    </div>
  );
};

export default IntroSequence;
