import React from 'react';
import { createUseStyles } from 'react-jss';

// Simplified classnames utility
const cx = (...classes) => classes.filter(Boolean).join(' ');

// Optimized styles without animations and transitions
const useStyles = createUseStyles({
  // Root container with essential styling only
  card: {
    position: 'relative',
    height: '100%',
    borderRadius: '8px',
    overflow: 'hidden',
    backgroundColor: 'rgba(15, 15, 15, 0.7)',
    boxShadow: '0 8px 25px rgba(0, 0, 0, 0.25)',
    border: '1px solid rgba(160, 142, 97, 0.2)',
    display: 'flex',
    flexDirection: 'column'
  },
  // Image container
  imageContainer: {
    height: '220px',
    position: 'relative',
    overflow: 'hidden'
  },
  // Image with minimal styling
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover'
  },
  // Overlay with fixed opacity
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'linear-gradient(to bottom, rgba(15, 15, 15, 0.1), rgba(15, 15, 15, 0.3))',
    opacity: 0.5
  },
  // Border glow effect (static)
  glow: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
    borderRadius: '8px',
    boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0.2), 0 0 30px rgba(191, 173, 127, 0.15)',
    zIndex: 1
  },
  // Content area
  content: {
    padding: '1.5rem 1.8rem',
    display: 'flex',
    flexDirection: 'column',
    flex: 1
  },
  // Title section
  titleArea: {
    marginBottom: '0.75rem'
  },
  // Title
  title: {
    fontSize: '1.6rem',
    color: '#bfad7f',
    letterSpacing: '0.04em',
    margin: 0,
    fontWeight: 300,
    lineHeight: 1.2
  },
  // Category
  category: {
    fontSize: '0.95rem',
    color: 'rgba(191, 173, 127, 0.7)',
    letterSpacing: '0.05em',
    fontWeight: 300,
    fontStyle: 'italic',
    margin: '0.4rem 0 0 0'
  },
  // Description
  description: {
    fontSize: '0.90rem',
    color: 'rgba(224, 224, 224, 0.9)',
    lineHeight: 1.6,
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300,
    margin: '0.5rem 0 1.5rem 0',
    flex: 1
  },
  // Tags container
  tagsContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.5rem',
    marginTop: 'auto',
    paddingTop: '1rem',
    borderTop: '1px solid rgba(160, 142, 97, 0.15)'
  },
  // Tag
  tag: {
    display: 'inline-flex',
    alignItems: 'center',
    padding: '0.3rem 0.7rem',
    borderRadius: '4px',
    backgroundColor: 'rgba(191, 173, 127, 0.1)',
    border: '1px solid rgba(191, 173, 127, 0.2)',
    color: 'rgba(191, 173, 127, 0.9)',
    fontSize: '0.75rem',
    fontWeight: 400,
    letterSpacing: '0.02em'
  },
  // Clickable cursor
  clickable: {
    cursor: 'pointer'
  }
});

/**
 * OptimizedProjectCard
 * 
 * An optimized project card component:
 * - No animations or transitions
 * - Minimal DOM nodes
 * - No state management or hooks except for styles
 * - Static rendering with all elements present from the start
 * 
 * @param {Object} props - Component props
 * @param {Object} props.project - Project data object
 * @param {Function} props.onClick - Click handler
 */
const ProjectCard = ({ project, onClick }) => {
  const classes = useStyles();

  // Build class names
  const cardClassName = cx(
    classes.card,
    onClick && classes.clickable
  );

  // Render with minimal DOM nodes
  return (
    <div 
      className={cardClassName}
      onClick={onClick ? () => onClick(project) : undefined}
      role={onClick ? 'button' : undefined}
      tabIndex={onClick ? 0 : undefined}
    >
      {/* Static border glow effect */}
      <div className={classes.glow} aria-hidden="true" />

      {/* Image section */}
      <div className={classes.imageContainer}>
        <img
          src={project.image}
          alt=""
          className={classes.image}
          loading="lazy"
          decoding="async"
        />
        <div className={classes.overlay} aria-hidden="true" />
      </div>

      {/* Content section */}
      <div className={classes.content}>
        <div className={classes.titleArea}>
          <h2 className={classes.title}>{project.title}</h2>
          <p className={classes.category}>{project.category}</p>
        </div>

        <p className={classes.description}>{project.description}</p>

        {project.tags && project.tags.length > 0 && (
          <div className={classes.tagsContainer}>
            {project.tags.map((tag, index) => (
              <span key={index} className={classes.tag}>
                {tag}
              </span>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

// Add display name for better debugging
ProjectCard.displayName = 'OptimizedProjectCard';

export default ProjectCard;
