import React, { useRef, useState, useCallback, useMemo, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';
import CometBorderEffect from '../bordereffects/CometBorderEffect';

// Enhanced styles with optimized layout and scroll behavior
const useStyles = createUseStyles({
  container: {
    display: 'flex',
    flexDirection: 'row',
    width: '100%',
    maxWidth: '1300px',
    margin: '0 auto',
    minHeight: '70vh',
    position: 'relative',
    overflow: 'visible',
    '@media (max-width: 768px)': {
      flexDirection: 'column',
    }
  },

  // Left sidebar section with sticky positioning
  profileSidebar: {
    width: '40%',
    padding: '3rem 1rem 2rem 0',
    display: 'flex',
    flexDirection: 'column',
    position: 'sticky',
    top: '2rem',
    height: 'fit-content',
    maxHeight: 'calc(100vh - 4rem)',
    '@media (max-width: 768px)': {
      width: '100%',
      padding: '2rem 1rem',
      alignItems: 'center',
      textAlign: 'center',
      position: 'relative',
      top: 0
    }
  },

  // Right content section
  contentSection: {
    width: '60%',
    padding: '3rem 0 2rem 2rem',
    '@media (max-width: 768px)': {
      width: '100%',
      padding: '2rem 1rem'
    }
  },

  // Profile image container with circular shape
  profileImageContainer: {
    position: 'relative',
    width: '240px',
    height: '240px',
    borderRadius: '50%',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    transition: 'box-shadow 0.3s ease',
    overflow: 'hidden',
    boxSizing: 'border-box',
    marginBottom: '2rem',
    border: '1px solid rgba(191, 173, 127, 0.2)',
    '&:hover': {
      boxShadow: '0 5px 20px rgba(191, 173, 127, 0.4)'
    },
    '@media (max-width: 768px)': {
      width: '200px',
      height: '200px',
    },
  },

  cometCanvasContainer: {
    position: 'absolute',
    top: -20,
    left: -20,
    right: -20,
    bottom: -20,
    pointerEvents: 'none',
    zIndex: 10,
    transform: 'translateZ(0)',
    willChange: 'transform',
