// src/components/sections/ComingSoon.js
import React from 'react';
import styled from 'styled-components';
import Layout from '../layout/Layout';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 70vh;
  text-align: center;
  padding: ${props => props.theme.spacing.xl} 0;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: ${props => props.theme.spacing.md};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -${props => props.theme.spacing.sm};
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 2px;
    background-color: ${props => props.theme.colors.primary};
  }

  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    font-size: 4rem;
  }
`;

const Description = styled.p`
  font-size: 1.25rem;
  max-width: 600px;
  margin: ${props => props.theme.spacing.lg} auto;
  color: ${props => props.theme.colors.text};
`;

const Illustration = styled.div`
  margin-top: ${props => props.theme.spacing.xl};
  position: relative;
  width: 200px;
  height: 200px;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: radial-gradient(circle, ${props => props.theme.colors.primary}33 0%, transparent 70%);
    animation: pulse 4s infinite ease-in-out;
  }

  @keyframes pulse {
    0%, 100% {
      transform: scale(0.9);
      opacity: 0.5;
    }
    50% {
      transform: scale(1.1);
      opacity: 0.8;
    }
  }
`;

const MoonIcon = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100px;
  height: 100px;
  border-radius: 50%;
  background-color: ${props => props.theme.colors.primary};
  box-shadow: 0 0 30px ${props => props.theme.colors.primary}66;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: -25px;
    width: 100px;
    height: 100px;
    border-radius: 50%;
    background-color: rgba(0, 0, 0, 0.8);
    mix-blend-mode: darken;
  }
`;

const ComingSoon = ({ title, description }) => {
  return (
    <Layout>
      <Container>
        <Title>{title}</Title>
        <Description>
          {description || "This feature is currently in development. Check back soon for updates."}
        </Description>

        <Illustration>
          <MoonIcon />
        </Illustration>
      </Container>
    </Layout>
  );
};

export default ComingSoon;
