      jsx: true,
      children: `
        .landing-container {
          position: relative;
          width: 100vw;
          min-height: 100vh;
          overflow-x: hidden;
          background-color: #080808;
          display: flex;
          justify-content: center;
          align-items: flex-start;
          font-family: 'Avenir', 'Avenir Next', sans-serif;
          -webkit-font-smoothing: antialiased;
        }

        .background-canvas {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 1;
        }

        /* Intro Sequence Styles */
        .intro-sequence {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 1000;
          background-color: #080808;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          cursor: pointer;
          transition: opacity 0.4s ease;
        }

        .intro-sequence.fade-out {
          opacity: 0;
        }

        .moon-animation {
          position: relative;
          margin-bottom: 3rem;
        }

        .moon-canvas {
          display: block;
        }

        .quote-container {
          max-width: 500px;
          text-align: center;
          opacity: 0;
          transform: translateY(10px);
          transition: opacity 0.6s ease, transform 0.6s ease;
          padding: 0 2rem;
        }

        .quote-container.visible {
          opacity: 1;
          transform: translateY(0);
        }

        .quote-text {
          font-family: 'Garamond', 'Adobe Caslon Pro', serif;
          font-size: 1.2rem;
          line-height: 1.7;
          color: rgba(191, 173, 127, 0.7);
          margin-bottom: 1.5rem;
          font-weight: 300;
          letter-spacing: 0.02em;
        }

        .quote-line {
          display: inline-block;
        }

        .quote-author {
          font-size: 0.9rem;
          color: rgba(224, 224, 224, 0.5);
          letter-spacing: 0.1em;
          text-align: right;
          padding-right: 2rem;
          font-weight: 300;
        }

        .skip-intro {
          position: absolute;
          bottom: 2rem;
          font-size: 0.7rem;
          color: rgba(224, 224, 224, 0.3);
          letter-spacing: 0.15em;
          animation: pulse 2s infinite;
          font-weight: 300;
          text-transform: uppercase;
        }

        /* Main Content Styles */
        .content-wrapper {
          z-index: 2;
          width: 100%;
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          opacity: 0;
          transition: opacity 0.8s ease;
        }

        .content-wrapper.loaded {
          opacity: 1;
        }

        .content-container {
          width: 100%;
          max-width: 900px;
          margin: 0 auto;
          padding: 4rem 2rem 2rem;
          display: flex;
          flex-direction: column;
          align-items: center; /* Center all content */
        }

        .title-container {
          text-align: center;
          margin-bottom: 5rem; /* Increased spacing significantly */
          width: 100%;
        }

        .main-title {
          font-size: 3.5rem; /* Slightly larger */
          letter-spacing: 0.25em;
          margin-bottom: 0.5rem;
          color: #bfad7f;
          font-weight: 300;
          text-transform: uppercase;
        }

        .title-divider {
          height: 1px;
          width: 150px; /* Wider divider */
          background: linear-gradient(to right, rgba(160, 142, 97, 0), rgba(160, 142, 97, 0.6), rgba(160, 142, 97, 0));
          margin: 0.5rem auto;
        }

        .subtitle {
          font-size: 1.1rem; /* Slightly larger */
          color: rgba(224, 224, 224, 0.6);
          font-family: 'Garamond', 'Adobe Caslon Pro', serif;
          font-style: italic;
          font-weight: 300;
          margin-top: 0.75rem;
          letter-spacing: 0.05em;
        }

        /* Sections Container - Added transitions for all children */
        .sections-container {
          display: flex;
          flex-direction: column;
          gap: 2.5rem; /* Significantly increased spacing */
          margin-bottom: 5rem; /* More space at bottom */
          width: 100%;
          max-width: 800px; /* Control maximum width */
          position: relative; /* For absolute positioning of children if needed */
        }

        /* Add transition during layout changes */
        .sections-container.animating .section-wrapper {
          transition: all 0.4s ease;
        }

        /* Ensure smooth transitions when sections expand/collapse */
        .section-wrapper {
          position: relative;
          width: 100%;
          transition: height 0.4s ease, transform 0.4s ease, margin 0.4s ease;
        }

        /* Add transitions for expanded section */
        .section-wrapper.expanded-section {
          z-index: 10; /* Ensure expanded section is on top */
        }

        .section-row {
          position: relative;
          height: 6rem; /* Taller rows for main sections */
          background-color: rgba(15, 15, 15, 0.5);
          border: 1px solid rgba(160, 142, 97, 0.2);
          border-radius: 3px;
          cursor: pointer;
          transition: all 0.4s ease;
          overflow: hidden;
          animation: fadeIn 0.8s ease backwards;
          display: flex;
          align-items: center;
          padding-right: 3rem; /* Space for diagonal cut */
          width: 100%;
          will-change: transform, background-color, border-color, box-shadow;
        }

        .section-row::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 0;
          height: 1px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0));
          transition: width 0.6s ease;
        }

        .section-row.active {
          background-color: rgba(20, 20, 20, 0.7);
          transform: translateX(10px);
          box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), 0 0 10px rgba(160, 142, 97, 0.15);
          border-color: rgba(160, 142, 97, 0.4);
        }

        .section-row.expanded {
          background-color: rgba(25, 25, 30, 0.7);
          border-color: rgba(160, 142, 97, 0.5);
          box-shadow: 0 5px 25px rgba(0, 0, 0, 0.4), 0 0 15px rgba(160, 142, 97, 0.2);
        }

        .section-row.active::before,
        .section-row.expanded::before {
          width: 100%;
        }

        .section-content {
          display: flex;
          align-items: center;
          width: 100%;
          height: 100%;
          position: relative;
          z-index: 2;
        }

        .section-icon-wrapper {
          width: 90px; /* Wider for larger icons */
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .icon-container {
          display: flex;
          justify-content: center;
          align-items: center;
          transition: all 0.3s ease;
        }

        .section-row.active .icon-container,
        .section-row.expanded .icon-container {
          transform: scale(1.1);
        }

        .section-text {
          flex: 1;
          padding-right: 1.5rem;
        }

        .section-title {
          font-size: 1.4rem; /* Larger titles */
          margin-bottom: 0.3rem;
          color: #bfad7f;
          font-weight: 300;
          letter-spacing: 0.05em;
          transition: all 0.3s ease;
        }

        .section-row.active .section-title,
        .section-row.expanded .section-title {
          transform: translateY(-2px);
        }

        .section-description {
          font-size: 0.9rem; /* Slightly larger descriptions */
          color: rgba(224, 224, 224, 0.6);
          line-height: 1.5;
          max-width: 90%;
          font-family: 'Garamond', 'Adobe Caslon Pro', serif;
          font-weight: 300;
          letter-spacing: 0.02em;
        }

        .expand-indicator {
          display: flex;
          justify-content: center;
          align-items: center;
          margin-right: 1rem;
          transition: transform 0.3s ease;
          opacity: 0.7;
        }

        .expand-indicator.expanded {
          transform: rotate(180deg);
          opacity: 1;
        }

        .section-row.active .expand-indicator {
          opacity: 1;
        }

        .expand-indicator.small {
          position: absolute;
          right: 0;
          top: 0.75rem;
          transform: scale(0.8);
        }

        .expand-indicator.small.expanded {
          transform: scale(0.8) rotate(180deg);
        }

        .diagonal-cut {
          position: absolute;
          top: 0;
          right: 0;
          width: 40px;
          height: 100%;
          overflow: hidden;
          z-index: 1;
        }

        .diagonal-cut::before {
          content: '';
          position: absolute;
          top: 0;
          right: 0;
          width: 80px;
          height: 150%;
          background-color: #080808;
          transform: rotate(15deg) translateX(20px);
        }

        .diagonal-cut::after {
          content: '';
          position: absolute;
          top: 0;
          right: 0;
          width: 1px;
          height: 150%;
          background-color: rgba(160, 142, 97, 0.2);
          transform: rotate(15deg) translateX(20px);
        }

        /* Subsection Styles */
        .subsections-container {
          margin-top: 0.75rem;
          margin-left: 3rem; /* More indentation */
          animation: fadeDown 0.3s ease forwards;
          position: relative;
          z-index: 1;
          will-change: transform, opacity;
          transition: transform 0.4s ease, opacity 0.4s ease;
        }

        .subsections-container.collapse {
          animation: fadeUp 0.3s ease forwards;
          z-index: 0;
        }

        .subsection-wrapper {
          position: relative;
          width: 100%;
          transition: height 0.4s ease, transform 0.4s ease, margin 0.4s ease;
        }

        /* Add transitions for expanded subsection */
        .subsection-wrapper.expanded-subsection {
          z-index: 5; /* Ensure expanded subsection is on top of others */
        }

        .subsection-row {
          background-color: rgba(15, 15, 15, 0.4);
          border: 1px solid rgba(160, 142, 97, 0.15);
          border-radius: 3px;
          padding: 0.75rem 1rem;
          margin-bottom: 0.5rem;
          cursor: pointer;
          transition: all 0.3s ease;
          will-change: transform, background-color, border-color;
        }

        .subsection-row.active,
        .subsection-row:hover {
          background-color: rgba(20, 20, 20, 0.6);
          border-color: rgba(160, 142, 97, 0.3);
          transform: translateX(5px);
        }

        .subsection-content {
          display: flex;
          flex-direction: column; /* Changed from row to column */
          align-items: flex-start; /* Align items to the start of the flex container */
          position: relative; /* Added for absolute positioning of the expand indicator */
          width: 100%; /* Ensure full width */
          transition: transform 0.3s ease, opacity 0.3s ease;
        }

        .subsection-title {
          font-size: 1rem;
          color: #bfad7f;
          font-weight: 300;
          margin: 0 0 0.5rem 0; /* Added bottom margin */
          width: 100%; /* Take full width */
          transition: transform 0.3s ease, opacity 0.3s ease;
        }

        .subsection-description {
          font-size: 0.8rem;
          color: rgba(224, 224, 224, 0.6);
          margin: 0;
          width: 100%; /* Take full width */
          display: block; /* Always display the description */
          transition: transform 0.3s ease, opacity 0.3s ease;
        }

        /* Nested Subsection Styles */
        .nested-subsections-container {
          margin-top: 0.5rem;
          margin-left: 1.5rem;
          animation: fadeDown 0.3s ease forwards;
          position: relative;
          z-index: 1;
          will-change: transform, opacity;
          transition: transform 0.4s ease, opacity 0.4s ease;
        }

        .nested-subsections-container.collapse {
          animation: fadeUp 0.3s ease forwards;
          z-index: 0;
        }

        .nested-subsection-row {
          background-color: rgba(15, 15, 15, 0.3);
          border: 1px solid rgba(160, 142, 97, 0.1);
          border-radius: 3px;
          padding: 0.6rem 0.75rem;
          margin-bottom: 0.4rem;
          cursor: pointer;
          transition: all 0.3s ease;
          will-change: transform, background-color, border-color;
        }

        .nested-subsection-row:hover {
          background-color: rgba(20, 20, 20, 0.5);
          border-color: rgba(160, 142, 97, 0.25);
          transform: translateX(3px);
        }

        .nested-subsection-content {
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          width: 100%;
          transition: transform 0.3s ease, opacity 0.3s ease;
        }

        .nested-subsection-title {
          font-size: 0.9rem;
          color: rgba(224, 224, 224, 0.9);
          font-weight: 300;
          margin: 0 0 0.4rem 0; /* Added bottom margin */
          width: 100%;
          transition: transform 0.3s ease, opacity 0.3s ease;
        }

        .nested-subsection-description {
          font-size: 0.75rem;
          color: rgba(224, 224, 224, 0.6);
          margin: 0;
          width: 100%;
          display: block; /* Always display the description */
          transition: transform 0.3s ease, opacity 0.3s ease;
        }

        /* Footer Moon Section */
        .footer-section {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 2rem 0 4rem;
          position: relative;
        }

        .footer-moon {
          margin-bottom: 2rem;
        }

        .brand-mark {
          display: flex;
          flex-direction: column;
          align-items: center;
          opacity: 0.4;
          transition: opacity 0.3s ease;
        }

        .brand-mark:hover {
          opacity: 0.7;
        }

        .symbol {
          font-size: 1.2rem;
          color: #a08e61;
          margin-bottom: 0.25rem;
        }

        .year {
          font-size: 0.65rem;
          color: rgba(224, 224, 224, 0.4);
          letter-spacing: 0.2em;
          font-weight: 300;
        }

        /* Animations */
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateX(-15px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes fadeDown {
          from {
            opacity: 0;
            transform: translateY(-10px);
            pointer-events: none;
          }
          to {
            opacity: 1;
            transform: translateY(0);
            pointer-events: auto;
          }
        }

        @keyframes fadeUp {
          from {
            opacity: 1;
            transform: translateY(0);
            pointer-events: none;
          }
          to {
            opacity: 0;
            transform: translateY(-10px);
            pointer-events: none;
          }
        }

        /* Apply to all children in collapsing containers */
        .subsections-container.collapse *,
        .nested-subsections-container.collapse * {
          transition: all 0.3s ease;
        }

        @keyframes pulse {
          0% {
            opacity: 0.15;
          }
          50% {
            opacity: 0.4;
          }
          100% {
            opacity: 0.15;
          }
        }

        /* Responsive Adjustments */
        @media (max-width: 768px) {
          .main-title {
            font-size: 2.5rem;
            letter-spacing: 0.2em;
          }

          .section-row {
            height: 5.5rem;
          }

          .section-icon-wrapper {
            width: 70px;
          }

          .icon-container svg {
            transform: scale(0.9);
          }

          .section-title {
            font-size: 1.2rem;
          }

          .section-description {
            font-size: 0.85rem;
          }

          .quote-text {
            font-size: 1rem;
          }

          .subsections-container {
            margin-left: 2rem;
          }

          .nested-subsections-container {
            margin-left: 1rem;
          }
        }

        @media (max-width: 480px) {
          .content-container {
            padding: 2rem 1rem 1rem;
          }

          .main-title {
            font-size: 2rem;
            letter-spacing: 0.15em;
          }

          .subtitle {
            font-size: 0.85rem;
          }

          .section-row {
            height: 5rem;
          }

          .section-icon-wrapper {
            width: 60px;
          }

          .icon-container svg {
            transform: scale(0.8);
          }

          .section-title {
            font-size: 1.1rem;
          }

          .quote-text {
            font-size: 0.9rem;
            line-height: 1.6;
          }

          .quote-author {
            font-size: 0.8rem;
          }

          .subsections-container {
            margin-left: 1rem;
          }

          .nested-subsections-container {
            margin-left: 0.5rem;
          }
        }
