import React, { useState, useEffect, useRef } from 'react';

const RefinedLandingPage = () => {
  // Add the new state variable here with all your other state variables at the top
  const [activeSection, setActiveSection] = useState(null);
  const [expandedSection, setExpandedSection] = useState(null);
  const [expandedSubsection, setExpandedSubsection] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [showIntro, setShowIntro] = useState(false);
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [moonDrawn, setMoonDrawn] = useState(0); // 0: not started, 1: drawing, 2: complete
  const [animatingLayout, setAnimatingLayout] = useState(false);
  const [disableHoverEffects, setDisableHoverEffects] = useState(false); // Add this new state variable

  // Your existing refs remain the same
  const canvasRef = useRef(null);
  const moonCanvasRef = useRef(null);
  const footerMoonRef = useRef(null);
  const particlesRef = useRef([]);
  const requestRef = useRef(null);
  const moonAnimationRef = useRef(null);
  const sectionsContainerRef = useRef(null);

  // Collection of poetic quotes by women
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend â€”\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ];

  // Main sections - reduced to 3
  const mainSections = [
    {
      id: "cycles",
      title: "Cycles",
      description: "Endless cycles of creation and destruction.",
      icon: "circles",
      path: "/cycles",
      expandable: true,
      subsections: [
        {
          id: "new-world",
          title: "New Cycle",
          description: "Paint a new world onto an empty canvas",
          path: "/cycles/new-world"
        },
        {
          id: "revisit",
          title: "Load Cycle",
          description: "Revisit a fading echo",
          path: "/cycles/revisit"
        }
      ]
    },
    {
      id: "codex",
      title: "Codex",
      description: "Repository of knowledge and rules",
      icon: "triangle",
      path: "/codex",
      expandable: true,
      subsections: [
        {
          id: "creator",
          title: "Creators",
          description: "The minds behind the mysteries.",
          path: "/codex/creator"
        },
        {
          id: "visualizations",
          title: "Visualizations",
          description: "Complex systems revealed through dynamic visual representations.",
          path: "/codex/visualizations",
          expandable: true,
          subsections: [
            {
              id: "creature-traits",
              title: "Creature Trait Visualization",
              description: "Explore how creatures adapt and evolve in response to environmental pressures.",
              path: "/codex/visualizations/creature-traits"
            },
            {
              id: "environmental",
              title: "Environmental Visualization",
              description: "Witness the formation and transformation of worlds and their ecosystems.",
              path: "/codex/visualizations/environmental"
            }
          ]
        },
        {
          id: "basics",
          title: "Basics",
          description: "Fundamental concepts and essential knowledge for beginners.",
          path: "/codex/basics"
        },
        {
          id: "advanced",
          title: "Advanced",
          description: "Complex theories and elaborate mechanics for seasoned explorers.",
          path: "/codex/advanced"
        }
      ]
    },
    {
      id: "characters",
      title: "Characters",
      description: "Beings shaped by their environments, each with a unique story to tell.",
      icon: "targetCircle",
      path: "/characters"
    }
  ];

  // Check if user has visited before
  useEffect(() => {
    const hasVisited = localStorage.getItem('hasVisitedCrescent');
    const shouldShowIntro = !hasVisited;

    setShowIntro(shouldShowIntro);

    if (shouldShowIntro) {
      // Select a random quote
      const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
      setCurrentQuote(randomQuote);

      // Fast 3-second intro sequence
      setTimeout(() => setMoonDrawn(1), 200); // Start drawing moon quickly
      setTimeout(() => setQuoteVisible(true), 400); // Show quote almost immediately
      setTimeout(() => setMoonDrawn(2), 2500); // Complete moon animation at 2.5s
      setTimeout(() => {
        setQuoteVisible(false);
        // Start fade out of intro
        const introElement = document.querySelector('.intro-sequence');
        if (introElement) introElement.classList.add('fade-out');
      }, 2600);
      setTimeout(() => {
        setShowIntro(false);
        setIsLoaded(true);
      }, 3000); // Complete entire sequence at 3s

      // Save that user has visited
      localStorage.setItem('hasVisitedCrescent', 'true');
    } else {
      // Skip intro for returning visitors
      setIsLoaded(true);
    }
  }, [quotes]); // Added quotes to dependency array

  // Initialize particles
  useEffect(() => {
    particlesRef.current = Array(40).fill().map(() => ({
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 1.5 + 0.5, // Smaller particles
      speedX: (Math.random() - 0.5) * 0.15,
      speedY: (Math.random() - 0.5) * 0.15,
      opacity: Math.random() * 0.3 + 0.05 // More subtle
    }));

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, []);

  // Vector moon animation for intro
  useEffect(() => {
    if (!moonCanvasRef.current || moonDrawn === 0) return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 70;
    let angle = 0;
    let direction = 1; // 1 for drawing, -1 for undoing
    let lastTimestamp = 0;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const drawMoonAnimation = (timestamp) => {
      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Speed control - faster animation to complete in ~2.3 seconds
      const speed = 1.3 * deltaTime / 16.67; // Significantly faster

      angle += speed * direction;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw moon outline
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, angle * Math.PI / 180, false);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = 1.5;
      ctx.stroke();

      // Add subtle glow
      ctx.shadowColor = 'rgba(191, 173, 127, 0.3)';
      ctx.shadowBlur = 8;

      // Reverse direction when full circle is drawn
      if (angle >= 360 && direction === 1) {
        direction = -1; // Start undoing
      }

      // Stop when animation is complete or when we reach moonDrawn state 2
      if ((angle <= 0 && direction === -1) || moonDrawn === 2) {
        cancelAnimationFrame(moonAnimationRef.current);
        return;
      }

      moonAnimationRef.current = requestAnimationFrame(drawMoonAnimation);
    };

    if (moonDrawn === 1) {
      moonAnimationRef.current = requestAnimationFrame(drawMoonAnimation);
    }

    return () => {
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, [moonDrawn]);

  // Static vector moon for footer
  useEffect(() => {
    if (!footerMoonRef.current) return;

    const canvas = footerMoonRef.current;
    const ctx = canvas.getContext('2d');

    const drawFooterMoon = () => {
      const centerX = canvas.width / 2;
      const centerY = canvas.height / 2;
      const radius = 70;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw thin circle
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = 1.5;

      // Add subtle glow
      ctx.shadowColor = 'rgba(191, 173, 127, 0.3)';
      ctx.shadowBlur = 10;

      ctx.stroke();

      // Draw a few thin lines across for texture (very subtle)
      ctx.beginPath();
      ctx.moveTo(centerX - radius * 0.7, centerY - radius * 0.3);
      ctx.lineTo(centerX + radius * 0.7, centerY + radius * 0.3);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.2)';
      ctx.lineWidth = 0.5;
      ctx.stroke();

      ctx.beginPath();
      ctx.moveTo(centerX - radius * 0.5, centerY + radius * 0.5);
      ctx.lineTo(centerX + radius * 0.5, centerY - radius * 0.5);
      ctx.stroke();
    };

    drawFooterMoon();

    window.addEventListener('resize', drawFooterMoon);
    return () => {
      window.removeEventListener('resize', drawFooterMoon);
    };
  }, []);

  // Canvas animation for background particles
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles
      particlesRef.current.forEach(particle => {
        // Update position
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // Wrap around screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${particle.opacity})`;
        ctx.fill();
      });

      // Draw cursor influence - very subtle glow
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 80
        );
        gradient.addColorStop(0, 'rgba(160, 142, 97, 0.03)');
        gradient.addColorStop(1, 'rgba(160, 142, 97, 0)');

        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 80, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [cursorPosition]);

  // Track mouse movement
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Skip intro
  const handleSkipIntro = () => {
    setShowIntro(false);
    setIsLoaded(true);
  };

  // Navigate to section
  const handleNavigate = (path, event, isExpandable) => {
    if (isExpandable) {
      event.preventDefault();
      event.stopPropagation();
      return;
    }

    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Handle section hover
  const handleSectionHover = (sectionId) => {
    if (!disableHoverEffects && !animatingLayout) {
      setActiveSection(sectionId);
    }
  };

  // Handle section click - for expanding sections
  const handleSectionClick = (sectionId, event) => {
    if (mainSections.find(section => section.id === sectionId && section.expandable)) {
      event.stopPropagation();

      // Disable hover effects during transition
      setDisableHoverEffects(true);
      setAnimatingLayout(true);

      // If currently expanded, collapse it
      if (expandedSection === sectionId) {
        const wrapper = document.querySelector(`.section-wrapper[data-id="${sectionId}"]`);
        const sectionRow = wrapper.querySelector('.section-row');

        // Save the current scroll position
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

        // Fix the height to current value
        wrapper.style.height = `${wrapper.offsetHeight}px`;

        // Force reflow
        void wrapper.offsetHeight;

        // Set target height
        wrapper.style.height = `${sectionRow.offsetHeight}px`;

        // Remove active state immediately to prevent transform conflicts
        setActiveSection(null);

        setTimeout(() => {
          setExpandedSection(null);

          // Restore scroll position if it changed
          window.scrollTo({
            top: scrollTop,
            behavior: 'instant'
          });

          setTimeout(() => {
            wrapper.style.height = 'auto';
            setAnimatingLayout(false);
            setDisableHoverEffects(false);
          }, 50);
        }, 400);
      } else {
        // If another section is already expanded, collapse it first
        if (expandedSection) {
          const previousWrapper = document.querySelector(`.section-wrapper[data-id="${expandedSection}"]`);
          const previousRow = previousWrapper?.querySelector('.section-row');

          if (previousWrapper && previousRow) {
            // Save scroll position
            const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

            // Set fixed height for transition
            previousWrapper.style.height = `${previousWrapper.offsetHeight}px`;

            // Force reflow
            void previousWrapper.offsetHeight;

            // Set height to just the row
            previousWrapper.style.height = `${previousRow.offsetHeight}px`;

            // After animation, expand the new section
            setTimeout(() => {
              previousWrapper.style.height = 'auto';

              // Restore scroll position
              window.scrollTo({
                top: scrollTop,
                behavior: 'instant'
              });

              expandNewSection(sectionId);
            }, 400);
          } else {
            expandNewSection(sectionId);
          }
        } else {
          expandNewSection(sectionId);
        }
      }
    }
  };

  // Helper function to expand a section
  const expandNewSection = (sectionId) => {
    // Set this section as active immediately
    setActiveSection(sectionId);

    const sectionWrapper = document.querySelector(`.section-wrapper[data-id="${sectionId}"]`);
    const sectionRow = sectionWrapper.querySelector('.section-row');

    // Save initial scroll position
    const initialScrollTop = window.pageYOffset || document.documentElement.scrollTop;

    // Set initial height to row height
    sectionWrapper.style.height = `${sectionRow.offsetHeight}px`;

    // Update the state to show the subsections
    setExpandedSection(sectionId);

    // Use requestAnimationFrame for more reliable timing
    requestAnimationFrame(() => {
      requestAnimationFrame(() => {
        // Measure the new height after content is rendered
        const subsectionsContainer = sectionWrapper.querySelector('.subsections-container');
        if (!subsectionsContainer) {
          setAnimatingLayout(false);
          setDisableHoverEffects(false);
          return;
        }

        const totalHeight = sectionRow.offsetHeight + subsectionsContainer.offsetHeight;

        // Set transition target height
        sectionWrapper.style.height = `${totalHeight}px`;

        // Restore original scroll position if it changed
        window.scrollTo({
          top: initialScrollTop,
          behavior: 'instant'
        });

        // Set a single timeout to switch to auto after transition completes
        setTimeout(() => {
          sectionWrapper.style.height = 'auto';
          setAnimatingLayout(false);
          setDisableHoverEffects(false);
        }, 410);
      });
    });
  };

  // Handle subsection hover/click - auto-expand
  const handleSubsectionHover = (sectionId, event) => {
    // Make sure we have a valid event object
    if (event) {
      event.stopPropagation();
    }

    const isClickEvent = event.type === 'click';

    if (isClickEvent) {
      setAnimatingLayout(true);
    }

    // If currently expanded and clicking the same subsection, add collapse animation first
    if (expandedSubsection === sectionId && isClickEvent) {
      const container = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"] .nested-subsections-container`);
      if (container) {
        // Add the collapse class to trigger the animation
        container.classList.add('collapse');

        // Calculate the current height of the subsection wrapper for smooth transition
        const subsectionWrapper = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"]`);
        const subsectionRow = subsectionWrapper.querySelector('.subsection-row');
        const currentHeight = subsectionWrapper.offsetHeight;
        const rowHeight = subsectionRow.offsetHeight;

        // Set explicit height to allow for transition
        subsectionWrapper.style.height = `${currentHeight}px`;

        // Force a reflow to ensure the browser recognizes the style change
        void subsectionWrapper.offsetHeight;

        // Set the target height (just the row)
        subsectionWrapper.style.height = `${rowHeight}px`;

        // After animation completes, collapse the subsection and reset height to auto
        setTimeout(() => {
          setExpandedSubsection(null);
          setTimeout(() => {
            subsectionWrapper.style.height = 'auto';

            if (isClickEvent) {
              setAnimatingLayout(false);
            }
          }, 50);
        }, 300);
      } else {
        setExpandedSubsection(null);
        if (isClickEvent) {
          setAnimatingLayout(false);
        }
      }
    } else {
      // For hover or first click, expand the subsection
      if (sectionId !== expandedSubsection) {
        // If another subsection is expanded, collapse it first
        if (expandedSubsection && isClickEvent) {
          const previousWrapper = document.querySelector(`.subsection-wrapper[data-id="${expandedSubsection}"]`);
          const previousRow = previousWrapper?.querySelector('.subsection-row');
          const previousContainer = previousWrapper?.querySelector('.nested-subsections-container');

          if (previousWrapper && previousRow && previousContainer) {
            // Set fixed height for transition
            previousWrapper.style.height = `${previousWrapper.offsetHeight}px`;

            // Force reflow
            void previousWrapper.offsetHeight;

            // Add collapse animation
            previousContainer.classList.add('collapse');

            // Set height to just the row
            previousWrapper.style.height = `${previousRow.offsetHeight}px`;

            // After animation, prepare to expand the new subsection
            setTimeout(() => {
              // Reset previous subsection
              previousWrapper.style.height = 'auto';
              previousContainer.classList.remove('collapse');

              // Expand new subsection
              expandNewSubsection(sectionId, isClickEvent);
            }, 300);
          } else {
            // Previous subsection doesn't have the expected structure,
            // just expand the new subsection
            expandNewSubsection(sectionId, isClickEvent);
          }
        } else {
          // No subsection is expanded, just expand the new one
          expandNewSubsection(sectionId, isClickEvent);
        }
      }
    }
  };

  // Helper function to expand a subsection
  const expandNewSubsection = (sectionId, isClickEvent) => {
    const subsectionWrapper = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"]`);

    if (subsectionWrapper && isClickEvent) {
      const subsectionRow = subsectionWrapper.querySelector('.subsection-row');

      // Set initial height to just the row height
      const rowHeight = subsectionRow.offsetHeight;
      subsectionWrapper.style.height = `${rowHeight}px`;
    }

    // Update the state to show the nested subsections
    setExpandedSubsection(sectionId);

    // After the DOM updates with the expanded content, transition to the full height
    if (isClickEvent) {
      setTimeout(() => {
        const subsectionWrapper = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"]`);
        if (subsectionWrapper) {
          const subsectionRow = subsectionWrapper.querySelector('.subsection-row');
          const newHeight = subsectionRow.offsetHeight + 
            (subsectionWrapper.querySelector('.nested-subsections-container')?.offsetHeight || 0);
          subsectionWrapper.style.height = `${newHeight}px`;

          // Reset to auto after transition completes
          setTimeout(() => {
            subsectionWrapper.style.height = 'auto';
            setAnimatingLayout(false);
          }, 400);
        }
      }, 50);
    }
  };

