import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useInView } from 'framer-motion';
import Background from '.././core/Background';
import RevealText from '../core/effects/RevealText';
import ProjectCard from '../core/effects/ProjectCard';
import './creatorsPage.css';

/**
 * CreatorsPage Component
 * 
 * Main component for the Creator profile page.
 * Implements:
 * - Scroll-based title animation that stops at a defined position
 * - Background with mouse-based parallax
 * - Animated sections that reveal on scroll
 */
const CreatorsPage = () => {
  // State management
  const [isLoaded, setIsLoaded] = useState(false);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  // References for elements and animations
  const creatorProfileRef = useRef(null);
  const titleRef = useRef(null);
  const headerRef = useRef(null);

  // Project data array
  const projects = [
    {
      id: "crescent",
      title: "Crescent TTRPG",
      description: "A dark fantasy tabletop roleplaying game centered around cycles of creation, evolution, and destruction.",
      image: "/images/projects/crescent.jpg",
      tags: ["Game Design", "World Building", "System Design"]
    },
    {
      id: "crescent-website",
      title: "Crucible Platform",
      description: "A web platform for managing and visualizing game worlds, character creation, and rule systems.",
      image: "/images/projects/crucible.jpg",
      tags: ["React", "UI/UX", "Interactive Design", "D3 visualization"]
    },
    {
      id: "crucible",
      title: "Evolution Simulator",
      description: "A sophisticated simulation system modeling environment formation and creature adaptation within dark fantasy settings.",
      image: "/images/projects/evolution.jpg",
      tags: ["C++", "Procedural Generation", "Complex Systems"]
    }
  ];

  // ---------- Scroll Animation Setup ----------

  // Access window scroll position using Framer Motion
  const { scrollY } = useScroll();

  // Define the threshold where title should stop scrolling (pixels from top)
  const TITLE_STOP_POSITION = 300;

  // Add additional space at the top for testing scroll effect
  const INITIAL_SPACE = 100; // Initial position before scrolling begins

  /**
   * Title Position Animation Logic
   * 
   * How this works:
   * 1. The title starts above its final position (negative INITIAL_SPACE)
   * 2. As the user scrolls down, the title moves DOWN (with scroll direction)
   * 3. When the user has scrolled TITLE_STOP_POSITION pixels, the title reaches its final position
   * 4. The title's final position will be at the top of its section (0px position)
   * 
   * When we map the scroll values:
   * - When scrollY is 0, titleY is -INITIAL_SPACE (title is above its final position)
   * - When scrollY is TITLE_STOP_POSITION, titleY is 0 (title is at its final position)
   * - For scrollY values beyond TITLE_STOP_POSITION, the title stays fixed at 0
   */
  const titleY = useTransform(
    scrollY,
    [0, TITLE_STOP_POSITION],
    //Use this as a shorthand method to alter the position of the title, the second number this takes in).
    [-INITIAL_SPACE, 185],
    { clamp: true } // Prevents going beyond defined limits
  );

  // Adjust opacity based on scroll position 
  const titleOpacity = useTransform(
    scrollY,
    [0, TITLE_STOP_POSITION, TITLE_STOP_POSITION + 100],
    [1, 0.95, 0.9]
  );

  // ---------- Animation Triggers ----------

  // Determine when the creator profile enters viewport
  const profileInView = useInView(creatorProfileRef, { 
    once: true, 
    threshold: 0.2 
  });

  // ---------- Event Handlers ----------

  // Track mouse position for parallax background effect
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // ---------- Lifecycle Management ----------

  // Initialize loaded state after a short delay
  useEffect(() => {
    const loadTimer = setTimeout(() => {
      setIsLoaded(true);
    }, 300);

    // Clean up timer on component unmount
    return () => clearTimeout(loadTimer);
  }, []);

  return (
    <div 
      className="crescent-creators-page"
      onMouseMove={handleMouseMove}
    >
      {/* Background component with interactive stars */}
      <Background cursorPosition={cursorPosition} setCursorPosition={setCursorPosition} />

