import React, { useRef, useState, useCallback, useMemo, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';
import CometBorderEffect from '../cosmiceffects/CometBorderEffect';

// Enhanced styles with scroll-triggered fixed sidebar
const useStyles = createUseStyles({
  container: {
    display: 'flex',
    flexDirection: 'row',
    width: '100%',
    maxWidth: '1300px',
    margin: '0 auto',
    minHeight: '70vh',
    position: 'relative',
    '@media (max-width: 768px)': {
      flexDirection: 'column',
    }
  },

  // Left sidebar wrapper maintains correct dimensions
  sidebarWrapper: {
    width: '40%',
    position: 'relative',
    '@media (max-width: 768px)': {
      width: '100%',
    }
  },

  // The sidebar that will change positioning based on scroll
  profileSidebar: {
    padding: '3rem 2rem 2rem 0',
    display: 'flex',
    flexDirection: 'column',
    position: 'relative', // Starts as relative
    width: 'inherit',
    maxWidth: 'inherit',
    transition: 'transform 0.3s ease', // Smooth transition when position changes
    '@media (max-width: 768px)': {
      padding: '2rem 1rem',
      alignItems: 'center',
      textAlign: 'center',
      width: '100%'
    }
  },

  // Applied when sidebar should be fixed
  sidebarFixed: {
    position: 'fixed',
    top: '2rem',
  },

  // Content section
  contentSection: {
    width: '60%',
    padding: '3rem 0 2rem 2rem',
    '@media (max-width: 768px)': {
      width: '100%',
      padding: '2rem 1rem',
    }
  },

  // Profile image container with circular shape
  profileImageContainer: {
    position: 'relative',
    width: '240px',
    height: '240px',
    borderRadius: '50%',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    transition: 'box-shadow 0.3s ease',
    overflow: 'hidden',
    boxSizing: 'border-box',
    marginBottom: '2rem',
    border: '1px solid rgba(191, 173, 127, 0.2)',
    '&:hover': {
      boxShadow: '0 5px 20px rgba(191, 173, 127, 0.4)'
    },
    '@media (max-width: 768px)': {
      width: '200px',
      height: '200px',
    },
  },

  profileImage: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    borderRadius: '50%',
    overflow: 'hidden',
    '&::after': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0.3)',
      borderRadius: '50%',
      pointerEvents: 'none'
    }
  },

  imageWrapper: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    overflow: 'hidden',
    borderRadius: '50%'
  },

  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transition: 'transform 0.6s ease, object-position 0.6s ease',
    display: 'block'
  },

  // Profile information styling
  profileInfo: {
    marginBottom: '2rem',
    paddingLeft: '5px',
  },

  profileName: {
    fontSize: '2.8rem',
    fontWeight: '300',
    color: '#bfad7f',
    letterSpacing: '0.05em',
    marginBottom: '0.5rem',
    '@media (max-width: 768px)': {
      fontSize: '2.2rem'
    }
  },

  profileRole: {
    fontSize: '1.2rem',
    fontWeight: '300',
    marginBottom: '2rem',
    color: 'rgba(191, 173, 127, 0.7)',
    letterSpacing: '0.05em',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontStyle: 'italic',
  },

  profileTagline: {
    fontSize: '1.1rem',
    lineHeight: '1.6',
    marginBottom: '3rem',
    maxWidth: '90%',
    color: 'rgba(224, 224, 224, 0.7)',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: '300',
    '@media (max-width: 768px)': {
      maxWidth: '100%',
      textAlign: 'center'
    }
  },

  // Content sections
  section: {
    marginBottom: '3rem',
    scrollMarginTop: '2rem',
  },

  sectionContent: {
    fontSize: '1rem',
    lineHeight: '1.8',
    marginBottom: '1.5rem',
    color: 'rgba(224, 224, 224, 0.7)',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: '300',
  },

  // Stats section
  statsContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
    marginTop: '3rem',
    width: '100%',
    '@media (max-width: 768px)': {
      justifyContent: 'center',
      gap: '2rem'
    },
    '@media (max-width: 480px)': {
      flexDirection: 'column',
      alignItems: 'center',
      gap: '1.5rem'
    }
  },

  stat: {
    flex: '1',
    textAlign: 'center',
    padding: '0 1rem',
    minWidth: '100px',
    '@media (max-width: 480px)': {
      width: '100%',
      maxWidth: '180px'
    }
  },

  statValue: {
    fontSize: '2.5rem',
    fontWeight: '100',
    color: 'rgba(191, 173, 127, 0.9)',
    marginBottom: '0.5rem'
  },

  statLabel: {
    fontSize: '0.85rem',
    color: 'rgba(224, 224, 224, 0.5)',
    textTransform: 'uppercase',
    letterSpacing: '0.1em'
  },

  // Nav links with line indicators that extend on hover
  navLinks: {
    marginTop: '2rem',
    paddingLeft: '10px',
  },

  navLink: {
    position: 'relative',
    display: 'block',
    padding: '0.5rem 0',
    marginBottom: '1rem',
    fontSize: '0.85rem',
    letterSpacing: '0.1em',
    transition: 'color 0.3s, transform 0.3s',
    textDecoration: 'none',
    color: 'rgba(224, 224, 224, 0.7)',
    paddingLeft: '15px',
    cursor: 'pointer',
    '&::before': {
      content: '""',
      position: 'absolute',
      left: '0',
      top: '50%',
      transform: 'translateY(-50%)',
      width: '0', // Starts at 0 width
      height: '2px', // Line thickness
      backgroundColor: 'rgba(191, 173, 127, 0.7)',
      transition: 'width 0.3s ease, background-color 0.3s ease'
    },
    '&:hover': {
      color: '#bfad7f',
      transform: 'translateX(3px)',
      '&::before': {
        width: '20px', // Extends to 20px on hover
        backgroundColor: 'rgba(191, 173, 127, 0.9)' // Brighter color on hover
      }
    },
    '&$activeLink': {
      color: '#bfad7f',
      '&::before': {
        width: '20px', // Always extended for active link
        backgroundColor: 'rgba(191, 173, 127, 0.9)'
      }
    },
    '@media (max-width: 768px)': {
      display: 'inline-block',
      margin: '0 1rem',
    }
  },

  activeLink: {}
});

/**
 * PersonProfileCard Component with dynamic sidebar positioning
 * 
 * A highly configurable profile card that fixes the sidebar in place
 * only while scrolling through the component content, then releases it
 * when scrolled past.
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string} props.person.tagline - Short tagline about the person
 * @param {string[]} props.person.bio - Array of bio paragraphs for content area
 * @param {Object[]} props.person.stats - Array of statistic objects
 * @param {Object[]} props.person.experience - Array of experience objects
 * @param {Object[]} props.person.projects - Array of project objects
 * @param {Object} props.animationConfig - Animation configuration
 * @param {Object} props.cometConfig - Comet animation configuration
 * @param {Object} props.imagePosition - Image position configuration
 * @param {Array} props.additionalSections - Additional content sections
 * @param {Function} props.onSectionChange - Callback when active section changes
 * @param {number} props.fixedTriggerOffset - Offset from top when sidebar becomes fixed (default: 200px)
 */
const PersonProfileCard = ({ 
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  cometConfig = {
    size: 1.5,
    trailLength: 100,
    speed: 0.001,
    targetFPS: 30,
    respectReducedMotion: true
  },
  imagePosition = {
    x: 50,
    y: 50,
    scale: 1
  },
  additionalSections = [],
  onSectionChange = null,
  fixedTriggerOffset = 200 // Default scroll offset when sidebar becomes fixed
}) => {
  const classes = useStyles();
  const [isHovered, setIsHovered] = useState(false);
  const [activeSection, setActiveSection] = useState('about');
  const [sidebarIsFixed, setSidebarIsFixed] = useState(false);
  const [sidebarInitialTop, setSidebarInitialTop] = useState(0);
  const [containerHeight, setContainerHeight] = useState(0);

  // Refs for sections to enable scrolling
  const aboutRef = useRef(null);
  const experienceRef = useRef(null);
  const projectsRef = useRef(null);
  const containerRef = useRef(null);
  const sidebarRef = useRef(null);
  const wrapperRef = useRef(null);

  const sectionRefs = {
    about: aboutRef,
    experience: experienceRef,
    projects: projectsRef
  };

  // Set up ref and inView detection
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Handle mouse events
  const handleMouseEnter = useCallback(() => setIsHovered(true), []);
  const handleMouseLeave = useCallback(() => setIsHovered(false), []);

  // Precompute image style
  const imageStyle = useMemo(() => ({
    objectPosition: `${imagePosition?.x ?? 50}% ${imagePosition?.y ?? 50}%`,
    transform: `scale(${imagePosition?.scale ?? 1})`
  }), [imagePosition?.x, imagePosition?.y, imagePosition?.scale]);

  // Get initial measurements once the component is mounted
  useEffect(() => {
    if (!containerRef.current || !wrapperRef.current || !sidebarRef.current) return;

    const updateMeasurements = () => {
      const containerRect = containerRef.current.getBoundingClientRect();
      const wrapperRect = wrapperRef.current.getBoundingClientRect();
      const sidebarRect = sidebarRef.current.getBoundingClientRect();

      // Store initial top position of sidebar relative to the viewport
      setSidebarInitialTop(wrapperRect.top + window.scrollY);

      // Store the height of the container to determine when to un-fix the sidebar
      setContainerHeight(containerRect.height);
    };

    // Initial measurement
    updateMeasurements();

    // Re-measure on window resize
    window.addEventListener('resize', updateMeasurements);

    return () => {
      window.removeEventListener('resize', updateMeasurements);
    };
  }, []);

  // Handle dynamic positioning of the sidebar based on scroll
  useEffect(() => {
    if (!sidebarRef.current || !containerRef.current || sidebarInitialTop === 0) return;

    // Skip on mobile devices
    if (window.innerWidth <= 768) {
      setSidebarIsFixed(false);
      return;
    }

    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const containerBottom = sidebarInitialTop + containerHeight - sidebarRef.current.offsetHeight - fixedTriggerOffset;

      // Make sidebar fixed when scrolled to the component
      if (scrollPosition >= sidebarInitialTop - fixedTriggerOffset) {
        // But only if we haven't scrolled past the container
        if (scrollPosition < containerBottom) {
          if (!sidebarIsFixed) {
            setSidebarIsFixed(true);
          }
        } else {
          // Unfix when we scroll past the container
          if (sidebarIsFixed) {
            setSidebarIsFixed(false);

            // Apply absolute positioning to keep it at the bottom of the container
            if (sidebarRef.current) {
              sidebarRef.current.style.position = 'absolute';
              sidebarRef.current.style.top = `${containerHeight - sidebarRef.current.offsetHeight - fixedTriggerOffset}px`;
            }
          }
        }
      } else {
        // Reset to normal positioning when scrolled above the component
        if (sidebarIsFixed) {
          setSidebarIsFixed(false);

          // Reset any absolute positioning
          if (sidebarRef.current) {
            sidebarRef.current.style.position = '';
            sidebarRef.current.style.top = '';
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Initial check
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [sidebarIsFixed, sidebarInitialTop, containerHeight, fixedTriggerOffset]);

  // Scroll to section function
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      sectionRef.current.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  }, [onSectionChange, sectionRefs]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    if (!aboutRef.current || !experienceRef.current || !projectsRef.current) return;

    const options = {
      root: null,
      rootMargin: '-10% 0px -70% 0px', // Consider element in view when in the top 30% of viewport
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            // Notify parent component if callback provided
            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    if (aboutRef.current) observer.observe(aboutRef.current);
    if (experienceRef.current) observer.observe(experienceRef.current);
    if (projectsRef.current) observer.observe(projectsRef.current);

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange]);

  return (
    <motion.div 
      ref={containerRef}
      className={classes.container}
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
    >
      {/* Left sidebar wrapper maintains space */}
      <div ref={wrapperRef} className={classes.sidebarWrapper}>
        {/* Sidebar with dynamic positioning */}
        <div 
          ref={sidebarRef}
          className={`${classes.profileSidebar} ${sidebarIsFixed ? classes.sidebarFixed : ''}`}
        >
          {/* Profile image with comet animation */}
          <div 
            className={classes.profileImageContainer}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            {/* Comet animation around the border using standalone component */}
            <CometBorderEffect 
              isHovered={isHovered}
              size={cometConfig.size}
              trailLength={cometConfig.trailLength}
              speed={cometConfig.speed}
              hoverSpeedMultiplier={cometConfig.hoverSpeedMultiplier || 2}
              trailSegments={cometConfig.trailSegments || 25}
              glowIntensity={cometConfig.glowIntensity || 0.8}
              targetFPS={cometConfig.targetFPS || 30}
              respectReducedMotion={cometConfig.respectReducedMotion || true}
              paddingTop={20}
              paddingRight={20}
              paddingBottom={20}
              paddingLeft={20}
            />

            <div className={classes.profileImage}>
              <div className={classes.imageWrapper}>
                <img 
                  src={person.image} 
                  alt={person.name} 
                  className={classes.image}
                  style={imageStyle}
                  loading="lazy"
                />
              </div>
            </div>
          </div>

          {/* Profile information - name, role, and short tagline */}
          <div className={classes.profileInfo}>
            <h1 className={classes.profileName}>{person.name}</h1>
            <h2 className={classes.profileRole}>{person.role}</h2>

            {/* Short tagline */}
            {person.tagline && (
              <p className={classes.profileTagline}>{person.tagline}</p>
            )}
          </div>

          {/* Navigation links with line indicators that extend on hover */}
          <div className={classes.navLinks}>
            <a 
              className={`${classes.navLink} ${activeSection === 'about' ? classes.activeLink : ''}`}
              onClick={() => scrollToSection('about')}
            >
              ABOUT
            </a>
            <a 
              className={`${classes.navLink} ${activeSection === 'experience' ? classes.activeLink : ''}`}
              onClick={() => scrollToSection('experience')}
            >
              EXPERIENCE
            </a>
            <a 
              className={`${classes.navLink} ${activeSection === 'projects' ? classes.activeLink : ''}`}
              onClick={() => scrollToSection('projects')}
            >
              PROJECTS
            </a>
          </div>
        </div>
      </div>

      {/* Right content section - scrollable with sections */}
      <div className={classes.contentSection}>
        {/* About section */}
        <div 
          ref={aboutRef} 
          data-section="about"
          className={classes.section}
          id="about"
        >
          {person.bio && person.bio.map((paragraph, index) => (
            <p key={index} className={classes.sectionContent}>
              {paragraph}
            </p>
          ))}

          {/* Stats display */}
          {person.stats && person.stats.length > 0 && (
            <div className={classes.statsContainer}>
              {person.stats.map((stat, index) => (
                <div key={index} className={classes.stat}>
                  <div className={classes.statValue}>{stat.value}</div>
                  <div className={classes.statLabel}>{stat.label}</div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Experience section */}
        <div 
          ref={experienceRef}
          data-section="experience"
          className={classes.section}
          id="experience"
        >
          {person.experience ? (
            person.experience.map((item, index) => (
              <p key={index} className={classes.sectionContent}>
                {item.content}
              </p>
            ))
          ) : (
            <p className={classes.sectionContent}>
              Throughout my career, I've specialized in developing software systems that seamlessly 
              integrate front-end experiences with robust back-end architectures. My experience spans 
              various domains, from interactive media to data visualization systems.
            </p>
          )}
        </div>

        {/* Projects section */}
        <div 
          ref={projectsRef}
          data-section="projects"
          className={classes.section}
          id="projects"
        >
          {person.projects ? (
            person.projects.map((item, index) => (
              <p key={index} className={classes.sectionContent}>
                {item.content}
              </p>
            ))
          ) : (
            <p className={classes.sectionContent}>
              My personal projects merge the scientific principles of evolution and adaptation with 
              mythological themes of rebirth and renewal. I create 'things' that are both mechanically 
              sound and narratively compelling, allowing users to explore complex themes through 
              immersive experiences.
            </p>
          )}
        </div>

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div key={index} className={classes.section}>
              {section.content && (
                typeof section.content === 'string' 
                  ? <p className={classes.sectionContent}>{section.content}</p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
