import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Background from '../../../../core/Background';
import MeteorShower from '../../../../core/effects/cosmiceffects/MeteorShower';
import RevealText from '../../../../core/effects/RevealText';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
  // Main container and layout
  evolutionSimulator: {
    position: 'relative',
    width: '100%',
    height: '100vh',
    overflow: 'hidden',
    color: '#e0e0e0',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    display: 'flex',
    flexDirection: 'column',
    background: '#0a0a0f'
  },

  // Enhanced Navbar
  navbar: {
    position: 'relative',
    width: '100%',
    backgroundColor: 'rgba(15, 15, 20, 0.85)',
    borderBottom: '1px solid rgba(191, 173, 127, 0.4)',
    padding: '0.5rem 1rem',
    zIndex: 100,
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)'
  },
  title: {
    color: '#bfad7f',
    fontSize: '1.5rem',
    margin: 0,
    fontWeight: 300,
    letterSpacing: '0.05em',
    textShadow: '0 2px 5px rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem'
  },
  logoIcon: {
    width: '24px',
    height: '24px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #bfad7f 0%, #7D6B9E 100%)',
    display: 'inline-block'
  },

  // Navigation Elements
  navControls: {
    display: 'flex',
    gap: '1rem',
    alignItems: 'center',
    flexWrap: 'wrap'
  },
  dropdown: {
    position: 'relative',
    marginRight: '0.5rem'
  },
  dropdownButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.4rem 0.8rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)'
    }
  },
  dropdownContent: {
    position: 'absolute',
    top: '100%',
    left: 0,
    backgroundColor: 'rgba(20, 20, 25, 0.95)',
    minWidth: '200px',
    maxHeight: '70vh',
    overflowY: 'auto',
    borderRadius: '4px',
    padding: '0.5rem',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)',
    zIndex: 101,
    border: '1px solid rgba(191, 173, 127, 0.3)'
  },
  menuItem: {
    padding: '0.5rem 0.75rem',
    cursor: 'pointer',
    borderRadius: '3px',
    transition: 'all 0.15s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)'
    }
  },
  menuItemActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    fontWeight: 'bold'
  },
  menuItemTitle: {
    fontSize: '0.95rem',
    color: '#bfad7f'
  },
  menuItemDescription: {
    fontSize: '0.8rem',
    color: 'rgba(224, 224, 224, 0.7)',
    marginTop: '0.25rem'
  },

  // Main content area
  contentContainer: {
    display: 'flex',
    flexDirection: 'column',
    padding: '1.5rem',
    flex: 1,
    overflow: 'auto',
    gap: '1.5rem'
  },

  // Section Headings
  sectionHeading: {
    color: '#b54b4b', // Dark reddish color as shown in the image
    fontSize: '1.2rem',
    marginBottom: '1rem',
    fontWeight: 'normal'
  },

  // Network Visualization Controls
  visualizationControls: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem'
  },
  controlButton: {
    backgroundColor: 'rgba(20, 20, 25, 0.7)',
    border: '1px solid rgba(181, 75, 75, 0.5)',
    borderRadius: '4px',
    color: '#e0e0e0',
    padding: '0.6rem 1.5rem',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(181, 75, 75, 0.2)',
      borderColor: 'rgba(181, 75, 75, 0.7)'
    }
  },
  activeControlButton: {
    backgroundColor: 'rgba(181, 75, 75, 0.3)',
    borderColor: 'rgba(181, 75, 75, 0.8)'
  },

  // Trait probability controls
  traitProbabilitiesContainer: {
    marginBottom: '1.5rem'
  },
  emergenceProbabilitiesButton: {
    backgroundColor: 'rgba(20, 20, 25, 0.7)',
    border: '1px solid rgba(181, 75, 75, 0.5)',
    borderRadius: '4px',
    color: '#e0e0e0',
    padding: '0.6rem 1.5rem',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(181, 75, 75, 0.2)',
      borderColor: 'rgba(181, 75, 75, 0.7)'
    }
  },

  // Environmental conditions
  environmentalConditions: {
    marginBottom: '1.5rem'
  },
  environmentalSlidersContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '1rem'
  },
  environmentSlider: {
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    gap: '0.35rem'
  },
  sliderLabel: {
    fontSize: '0.9rem',
    display: 'flex',
    justifyContent: 'space-between'
  },
  sliderValue: {
    color: '#bfad7f'
  },
  sliderTrack: {
    width: '100%',
    height: '4px',
    backgroundColor: '#333',
    borderRadius: '2px',
    position: 'relative'
  },
  sliderThumb: {
    width: '14px',
    height: '14px',
    borderRadius: '50%',
    backgroundColor: '#b54b4b',
    position: 'absolute',
    top: '50%',
    transform: 'translate(-50%, -50%)',
    cursor: 'pointer'
  },

  // Network visualization area
  visualizationContainer: {
    position: 'relative',
    height: '500px',
    backgroundColor: 'rgba(20, 20, 25, 0.4)',
    borderRadius: '6px',
    overflow: 'hidden',
    border: '1px solid rgba(181, 75, 75, 0.3)',
    marginBottom: '1.5rem',
    cursor: 'grab',
    '&:active': {
      cursor: 'grabbing'
    }
  },
  networkCanvas: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%'
  },

  // Tabs for different controls
  navTabs: {
    display: 'flex',
    gap: '0.25rem',
    marginLeft: 'auto'
  },
  navTab: {
    padding: '0.4rem 0.8rem',
    backgroundColor: 'transparent',
    border: 'none',
    borderBottom: '2px solid transparent',
    color: 'rgba(224, 224, 224, 0.7)',
    cursor: 'pointer',
    fontSize: '0.9rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      color: '#bfad7f',
      borderBottomColor: 'rgba(191, 173, 127, 0.3)'
    }
  },
  navTabActive: {
    color: '#bfad7f',
    borderBottomColor: '#bfad7f'
  },

  // View controls
  viewControls: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem'
  },
  viewButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    width: '32px',
    height: '32px',
    borderRadius: '4px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)'
    }
  },

  // Category filters
  categoryFilters: {
    position: 'absolute',
    bottom: '1rem',
    left: '50%',
    transform: 'translateX(-50%)',
    backgroundColor: 'rgba(15, 15, 20, 0.8)',
    borderRadius: '30px',
    padding: '0.5rem 1rem',
    display: 'flex',
    gap: '0.5rem',
    zIndex: 50,
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)',
    border: '1px solid rgba(191, 173, 127, 0.2)'
  },
  categoryButton: {
    background: 'transparent',
    border: 'none',
    color: 'rgba(224, 224, 224, 0.7)',
    padding: '0.35rem 0.75rem',
    borderRadius: '20px',
    fontSize: '0.85rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      color: '#e0e0e0',
      backgroundColor: 'rgba(191, 173, 127, 0.1)'
    }
  },
  categoryButtonActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0'
  },

  // Tooltip styling
  tooltip: {
    position: 'fixed',
    backgroundColor: 'rgba(20, 20, 25, 0.95)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    borderRadius: '4px',
    padding: '0.75rem',
    maxWidth: '300px',
    pointerEvents: 'none',
    zIndex: 200,
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)'
  },

  // Media queries for responsive design
  '@media (max-width: 768px)': {
    navbar: {
      flexDirection: 'column',
      alignItems: 'flex-start',
      padding: '0.75rem'
    },
    navControls: {
      marginTop: '0.75rem',
      width: '100%',
      justifyContent: 'space-between'
    },
    environmentalSlidersContainer: {
      gridTemplateColumns: '1fr'
    }
  }
});

// Enhanced Evolution Simulator Component
const EnhancedEvolutionSimulator = () => {
  const classes = useStyles();

  // State hooks
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [selectedBodyShape, setSelectedBodyShape] = useState('quadrupedal');
  const [showBodyShapeMenu, setShowBodyShapeMenu] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showCategoryMenu, setShowCategoryMenu] = useState(false);
  const [selectedTraits, setSelectedTraits] = useState({});
  const [showTraitMenus, setShowTraitMenus] = useState({});
  const [viewMode, setViewMode] = useState('all');
  const [showEnvironmentPanel, setShowEnvironmentPanel] = useState(false);
  const [activeTab, setActiveTab] = useState('traits');
  const [zoom, setZoom] = useState(1);
  const [panOffset, setPanOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [hoveredElement, setHoveredElement] = useState(null);
  const [visualizationType, setVisualizationType] = useState('direct');
  const [showEmergenceProbabilities, setShowEmergenceProbabilities] = useState(false);

  // Environment stressors state - modified to match the image
  const [environment, setEnvironment] = useState({
    eldritch_influence: 0.5,
    corruption_level: 0.5,
    resource_scarcity: 0.5,
    ambient_magic: 0.5,
    darkness: 0.5
  });

  // Simulation metrics state
  const [totalEffectiveStress, setTotalEffectiveStress] = useState(0);
  const [adaptationProbability, setAdaptationProbability] = useState(0);

  // Refs
  const canvasRef = useRef(null);
  const containerRef = useRef(null);
  const networkRef = useRef(null);
  const animationFrameRef = useRef(null);

  // Define body shapes data
  const bodyShapes = [
    { id: 'bipedal', name: 'Bipedal', description: 'Upright forms with manipulative upper limbs' },
    { id: 'quadrupedal', name: 'Quadrupedal', description: 'Four-limbed forms optimized for stability and movement' },
    { id: 'serpentine', name: 'Serpentine', description: 'Elongated, limbless forms specialized for flexibility' },
    { id: 'amorphous', name: 'Amorphous', description: 'Malleable forms without fixed structure' },
    { id: 'insectoid', name: 'Multi-limbed', description: 'Multi-limbed, segmented forms' },
    { id: 'avian', name: 'Avian', description: 'Forms optimized for flight and aerial movement' },
    { id: 'aquatic', name: 'Aquatic', description: 'Forms specialized for water environments' }
  ];

  // Trait categories
  const traitCategories = [
    { id: 'locomotion', name: 'Locomotion', color: '#7D6B9E' },
    { id: 'metabolism', name: 'Metabolism', color: '#5C9D8B' },
    { id: 'sensory', name: 'Sensory Acuity', color: '#C99846' },
    { id: 'etheric', name: 'Etheric Adaptation', color: '#B54B4B' },
    { id: 'thermal', name: 'Thermal Regulation', color: '#A67C52' }
  ];

  // Network Nodes - Central trait node and connected traits
  const networkNodes = [
    { id: 'multiple_eyes', name: 'Multiple Eyes', type: 'central', color: '#b54b4b', x: 0, y: 0 },
    { id: 'necrotic_tissue', name: 'Necrotic Tissue', type: 'major', color: '#5C8B4B', x: -120, y: 120 },
    { id: 'void_resonance', name: 'Void Resonance', type: 'major', color: '#7D6B9E', x: 120, y: 120 },
