    { id: 'reality_perception', name: 'Reality Perception', type: 'minor', color: '#b54b4b', x: -60, y: -100 },
    { id: 'mesmerizing_gaze', name: 'Mesmerizing Gaze', type: 'minor', color: '#b54b4b', x: 60, y: -100 },
    { id: 'reality_distortion', name: 'Reality Distortion', type: 'minor', color: '#7D6B9E', x: 130, y: -60 },
    { id: 'void_echo', name: 'Void Echo', type: 'minor', color: '#7D6B9E', x: 180, y: 120 },
