            terrain_harshness: 0,
            resource_scarcity: -0.2,
            pressure_gravity: 0.1
          },
          description: 'Biological structures capable of directing and manipulating etheric energies.',
          compatible: ['all']
        }
      ],
      thermal: [
        {
          id: 'thick_blubber',
          name: 'Thick Blubber',
          tier: 1,
          affinities: {
            temperature: -0.8,
            pressure_gravity: 0.3,
            resource_scarcity: -0.4,
            terrain_harshness: 0.1,
            light_conditions: 0,
            ether_density: 0
          },
          description: 'Dense fatty tissue layer providing insulation in cold environments.',
          compatible: ['all']
        },
        {
          id: 'reflective_scales',
          name: 'Reflective Scales',
          tier: 1,
          affinities: {
            temperature: 0.7,
            light_conditions: 0.6,
            terrain_harshness: 0.3,
            resource_scarcity: -0.2,
            pressure_gravity: 0,
            ether_density: 0.1
          },
          description: 'Light-reflecting scales that help regulate heat absorption.',
          compatible: ['all']
        }
      ]
    };

    // Add additional traits based on selected body shape
    // This would be expanded with more traits specific to each body shape

    return baseTraits;
  };

  // Initialize selected traits on body shape change
  useEffect(() => {
    const initialTraits = {};
    traitCategories.forEach(category => {
      const traits = getTraitsByCategory(category.id);
      const compatibleTraits = traits.filter(trait => 
        trait.compatible.includes('all') || trait.compatible.includes(selectedBodyShape)
      );
      if (compatibleTraits.length > 0) {
        initialTraits[category.id] = compatibleTraits[0].id;
      }
    });
    setSelectedTraits(initialTraits);
  }, [selectedBodyShape]);

  // Setup the main visualization canvas
  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    const canvas = canvasRef.current;
    const container = containerRef.current;
    const ctx = canvas.getContext('2d');

    // Set up canvas dimensions
    const updateCanvasDimensions = () => {
      const rect = container.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;

      networkRef.current = {
        width: rect.width,
        height: rect.height,
        centerX: rect.width / 2,
        centerY: rect.height / 2
      };
    };

    updateCanvasDimensions();
    window.addEventListener('resize', updateCanvasDimensions);

    // Animation function for the network visualization
    const animate = () => {
      if (!networkRef.current) return;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Apply zoom and pan transformations
      ctx.save();
      ctx.translate(panOffset.x, panOffset.y);
      ctx.scale(zoom, zoom);

      const { centerX, centerY } = networkRef.current;

      // Function to calculate node positions relative to canvas center
      const getNodePosition = (node) => {
        return {
          x: centerX + node.x,
          y: centerY + node.y
        };
      };

      // Draw connections between nodes
      ctx.lineWidth = 1;
      networkConnections.forEach(connection => {
        const sourceNode = networkNodes.find(n => n.id === connection.source);
        const targetNode = networkNodes.find(n => n.id === connection.target);

        if (sourceNode && targetNode) {
          const sourcePos = getNodePosition(sourceNode);
          const targetPos = getNodePosition(targetNode);

          // Define gradient based on node colors
          const gradient = ctx.createLinearGradient(
            sourcePos.x, sourcePos.y, targetPos.x, targetPos.y
          );
          gradient.addColorStop(0, sourceNode.color);
          gradient.addColorStop(1, targetNode.color);

          // Draw connection line
          ctx.beginPath();
          ctx.moveTo(sourcePos.x, sourcePos.y);

          // Create curved connections for better visibility
          const midX = (sourcePos.x + targetPos.x) / 2;
          const midY = (sourcePos.y + targetPos.y) / 2;
          const curveOffset = 20;

          // Add slight curve to connections for aesthetics
          const dx = targetPos.x - sourcePos.x;
          const dy = targetPos.y - sourcePos.y;
          const normalX = -dy / Math.sqrt(dx*dx + dy*dy);
          const normalY = dx / Math.sqrt(dx*dx + dy*dy);

          const controlX = midX + normalX * curveOffset;
          const controlY = midY + normalY * curveOffset;

          ctx.quadraticCurveTo(controlX, controlY, targetPos.x, targetPos.y);

          ctx.strokeStyle = gradient;
          ctx.globalAlpha = 0.7;
          ctx.stroke();
          ctx.globalAlpha = 1;
        }
      });

      // Draw nodes
      networkNodes.forEach(node => {
        const pos = getNodePosition(node);
        const radius = node.type === 'central' ? 40 : node.type === 'major' ? 30 : 20;

        // Draw node background
        ctx.beginPath();
        ctx.arc(pos.x, pos.y, radius, 0, Math.PI * 2);
        ctx.fillStyle = node.color;
        ctx.fill();

        // Draw node border
        ctx.lineWidth = 2;
        ctx.strokeStyle = '#000';
        ctx.stroke();

        // Add inner circle for central and major nodes
        if (node.type === 'central' || node.type === 'major') {
          ctx.beginPath();
          ctx.arc(pos.x, pos.y, radius * 0.7, 0, Math.PI * 2);
          ctx.lineWidth = 1;
          ctx.strokeStyle = '#000';
          ctx.stroke();
        }

        // Add node labels (not directly on the nodes, but next to them)
        ctx.font = node.type === 'central' ? 'bold 14px Arial' : '12px Arial';
        ctx.fillStyle = '#fff';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';

        // Position labels based on node position relative to center
        let textX = pos.x;
        let textY = pos.y;

        // Adjust label position based on node position
        if (node.y < -50) { // Top nodes
          textY = pos.y - radius - 10;
        } else if (node.y > 50) { // Bottom nodes
          textY = pos.y + radius + 10;
        } else if (node.x < 0) { // Left nodes
          textX = pos.x - radius - 10;
          ctx.textAlign = 'right';
        } else if (node.x > 0) { // Right nodes
          textX = pos.x + radius + 10;
          ctx.textAlign = 'left';
        }

        // Add text stroke for better visibility
        ctx.strokeStyle = 'rgba(0, 0, 0, 0.8)';
        ctx.lineWidth = 3;
        ctx.strokeText(node.name, textX, textY);
        ctx.fillText(node.name, textX, textY);
      });

      ctx.restore();

      // Continue animation
      animationFrameRef.current = requestAnimationFrame(animate);
    };

    // Start animation loop
    animate();

    // Set loaded after a delay
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 1000);

    // Cleanup on unmount
    return () => {
      window.removeEventListener('resize', updateCanvasDimensions);
      clearTimeout(timer);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [visualizationType, zoom, panOffset, showEmergenceProbabilities]);

  // Calculate stress and adaptation probability when environment changes
  useEffect(() => {
    // Using simplified stress calculation
    const stressFromEnvironment = Object.values(environment).reduce((sum, value) => sum + Math.abs(value), 0) / 5;
    setTotalEffectiveStress(stressFromEnvironment);

    // Simplified adaptation probability calculation
    const probability = 1 / (1 + Math.exp(-10 * (stressFromEnvironment - 0.5)));
    setAdaptationProbability(probability);
  }, [environment, selectedBodyShape, selectedTraits]);

  // Handle mouse move for background effect
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });

    // Handle dragging
    if (isDragging) {
      const dx = e.clientX - dragStart.x;
      const dy = e.clientY - dragStart.y;
      setPanOffset({
        x: panOffset.x + dx,
        y: panOffset.y + dy
      });
      setDragStart({
        x: e.clientX,
        y: e.clientY
      });
    }
  };

  // Handle mouse down for drag start
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({
      x: e.clientX,
      y: e.clientY
    });

    // Change cursor style
    if (containerRef.current) {
      containerRef.current.style.cursor = 'grabbing';
    }
  };

  // Handle mouse up for drag end
  const handleMouseUp = () => {
    setIsDragging(false);

    // Reset cursor style
    if (containerRef.current) {
      containerRef.current.style.cursor = 'grab';
    }
  };

  // Handle mouse wheel for zoom
  const handleWheel = (e) => {
    e.preventDefault();
    const zoomFactor = 0.1;
    const newZoom = e.deltaY < 0 
      ? Math.min(zoom + zoomFactor, 3) 
      : Math.max(zoom - zoomFactor, 0.5);

    setZoom(newZoom);
  };

  // Update environmental stressor
  const handleEnvironmentChange = (stressor, value) => {
    setEnvironment(prev => ({
      ...prev,
      [stressor]: parseFloat(value)
    }));
  };

  // Handle body shape selection
  const handleBodyShapeSelect = (shapeId) => {
    setSelectedBodyShape(shapeId);
    setShowBodyShapeMenu(false);
  };

  // Handle trait selection
  const handleTraitSelect = (categoryId, traitId) => {
    setSelectedTraits(prev => ({
      ...prev,
      [categoryId]: traitId
    }));
    setShowTraitMenus(prev => ({
      ...prev,
      [categoryId]: false
    }));
  };

  // Toggle trait menu visibility
  const handleTraitMenuToggle = (categoryId) => {
    setShowTraitMenus(prev => ({
      ...prev,
      [categoryId]: !prev[categoryId]
    }));

    // Close other menus
    setShowBodyShapeMenu(false);
    setShowCategoryMenu(false);
    Object.keys(showTraitMenus).forEach(key => {
      if (key !== categoryId) {
        setShowTraitMenus(prev => ({
          ...prev,
          [key]: false
        }));
      }
    });
  };

  // Reset view
  const handleResetView = () => {
    setZoom(1);
    setPanOffset({ x: 0, y: 0 });
  };

  // Get selected trait name
  const getSelectedTraitName = (categoryId) => {
    const traitId = selectedTraits[categoryId];
    if (!traitId) return 'Select...';

    const traits = getTraitsByCategory(categoryId);
    const trait = traits.find(t => t.id === traitId);
    return trait ? trait.name : 'Select...';
  };

  // Render dropdown menu
  const renderDropdownMenu = (isOpen, title, items, selectedId, onSelect, renderItem) => {
    if (!isOpen) return null;

    return (
      <motion.div 
        className={classes.dropdownContent}
        initial={{ opacity: 0, y: -10 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -10 }}
        transition={{ duration: 0.2 }}
      >
        {items.map(item => (
          <div 
            key={item.id}
            className={`
              ${classes.menuItem} 
              ${item.id === selectedId ? classes.menuItemActive : ''}
            `}
            onClick={() => onSelect(item.id)}
          >
            {renderItem ? renderItem(item) : (
              <>
                <div className={classes.menuItemTitle}>{item.name}</div>
                {item.description && (
                  <div className={classes.menuItemDescription}>{item.description}</div>
                )}
              </>
            )}
          </div>
        ))}
      </motion.div>
    );
  };

  // Render environmental slider
  const renderEnvironmentalSlider = (stressor, label) => {
    const value = environment[stressor];
    const percentage = value * 100;

    return (
      <div className={classes.environmentSlider} key={stressor}>
        <div className={classes.sliderLabel}>
          <span>{label}</span>
          <span className={classes.sliderValue}>{value.toFixed(1)}</span>
        </div>
        <div className={classes.sliderTrack}>
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={value}
            onChange={(e) => handleEnvironmentChange(stressor, e.target.value)}
            style={{ 
              width: '100%', 
              position: 'absolute',
              opacity: 0,
              height: '100%',
              cursor: 'pointer'
            }}
          />
          <div 
            className={classes.sliderThumb}
            style={{ left: `${percentage}%` }}
          ></div>
        </div>
      </div>
    );
  };

  return (
    <div 
      className={classes.evolutionSimulator}
      onMouseMove={handleMouseMove}
    >
      {/* Background effects */}
      <Background cursorPosition={cursorPosition} setCursorPosition={setCursorPosition} />

      <MeteorShower 
        height="100vh"
        zIndex={2}
        trailColor="rgba(191, 173, 127, 0.8)"
        glowColor="rgba(207, 185, 130, 0.6)"
        coreColor="rgba(255, 248, 220, 1)"
        meteorDensity={15}
        meteorMinSize={1}
        meteorMaxSize={2}
        meteorSpeed={0.08}
        trailLength={300}
        trailSegments={80}
        enableParallax={true}
        parallaxIntensity={0.15}
        mode="arc"
        staggered={true}
      />

      {/* Enhanced Navbar */}
      <div className={classes.navbar}>
        <h1 className={classes.title}>
          <span className={classes.logoIcon}></span>
          Crescent Evolution System
        </h1>

        <div className={classes.navControls}>
          {/* Body Shape Dropdown */}
          <div className={classes.dropdown}>
            <button 
              className={classes.dropdownButton}
              onClick={() => {
                setShowBodyShapeMenu(!showBodyShapeMenu);
                setShowCategoryMenu(false);
                setShowTraitMenus({});
              }}
            >
              <span>Body Shape: </span>
              <span style={{ fontWeight: 'bold' }}>
                {bodyShapes.find(shape => shape.id === selectedBodyShape)?.name || 'Select...'}
              </span>
              <span style={{ fontSize: '0.8rem', marginLeft: '4px' }}></span>
            </button>

            <AnimatePresence>
              {renderDropdownMenu(
                showBodyShapeMenu,
                'Select Body Shape',
                bodyShapes,
                selectedBodyShape,
                handleBodyShapeSelect
              )}
            </AnimatePresence>
          </div>

          {/* Filter by Category Dropdown */}
          <div className={classes.dropdown}>
            <button 
              className={classes.dropdownButton}
              onClick={() => {
                setShowCategoryMenu(!showCategoryMenu);
                setShowBodyShapeMenu(false);
                setShowTraitMenus({});
              }}
            >
              <span>Filter: </span>
              <span style={{ fontWeight: 'bold' }}>
                {selectedCategory 
                  ? traitCategories.find(cat => cat.id === selectedCategory)?.name 
                  : 'All Categories'}
              </span>
              <span style={{ fontSize: '0.8rem', marginLeft: '4px' }}></span>
            </button>

            <AnimatePresence>
              {renderDropdownMenu(
                showCategoryMenu,
                'Filter by Category',
                [{ id: null, name: 'All Categories' }, ...traitCategories],
                selectedCategory,
                (categoryId) => {
                  setSelectedCategory(categoryId);
                  setShowCategoryMenu(false);
                }
              )}
            </AnimatePresence>
          </div>

          {/* Trait Selection Dropdowns */}
          {activeTab === 'traits' && traitCategories.map(category => (
            <div className={classes.dropdown} key={category.id}>
              <button 
                className={classes.dropdownButton}
                style={{ borderColor: `${category.color}50` }}
                onClick={() => handleTraitMenuToggle(category.id)}
              >
                <span>{category.name}: </span>
                <span style={{ fontWeight: 'bold' }}>
                  {getSelectedTraitName(category.id)}
                </span>
                <span style={{ fontSize: '0.8rem', marginLeft: '4px' }}></span>
              </button>

              <AnimatePresence>
                {renderDropdownMenu(
                  showTraitMenus[category.id],
                  `Select ${category.name} Trait`,
                  getTraitsByCategory(category.id).filter(trait => 
                    trait.compatible.includes('all') || trait.compatible.includes(selectedBodyShape)
                  ),
                  selectedTraits[category.id],
                  (traitId) => handleTraitSelect(category.id, traitId),
                  (trait) => (
                    <>
                      <div className={classes.menuItemTitle}>{trait.name}</div>
                      <div className={classes.menuItemDescription}>{trait.description}</div>
                    </>
                  )
                )}
              </AnimatePresence>
            </div>
          ))}
        </div>

        {/* Navigation Tabs */}
        <div className={classes.navTabs}>
          <button 
            className={`${classes.navTab} ${activeTab === 'traits' ? classes.navTabActive : ''}`}
            onClick={() => setActiveTab('traits')}
          >
            Traits
          </button>
          <button 
            className={`${classes.navTab} ${activeTab === 'environment' ? classes.navTabActive : ''}`}
            onClick={() => setActiveTab('environment')}
          >
            Environment
          </button>
          <button 
            className={`${classes.navTab} ${activeTab === 'synthesis' ? classes.navTabActive : ''}`}
            onClick={() => setActiveTab('synthesis')}
          >
            Synthesis
          </button>
        </div>

        {/* View Controls */}
        <div className={classes.viewControls}>
          <button 
            className={classes.viewButton}
            onClick={() => setZoom(Math.min(zoom + 0.1, 3))}
            title="Zoom In"
          >
            +
          </button>
          <button 
            className={classes.viewButton}
            onClick={() => setZoom(Math.max(zoom - 0.1, 0.5))}
            title="Zoom Out"
          >
            -
          </button>
          <button 
            className={classes.viewButton}
            onClick={handleResetView}
            title="Reset View"
          >

          </button>
        </div>
      </div>

      {/* Main Content Area */}
      <div className={classes.contentContainer}>
        {/* Network Visualization Section */}
        <div>
          <h2 className={classes.sectionHeading}>Network Visualization</h2>

          <div className={classes.visualizationControls}>
            <button 
              className={`${classes.controlButton} ${visualizationType === 'direct' ? classes.activeControlButton : ''}`}
              onClick={() => setVisualizationType('direct')}
            >
              Direct Connections
            </button>
            <button 
              className={`${classes.controlButton} ${visualizationType === 'evolutionary' ? classes.activeControlButton : ''}`}
              onClick={() => setVisualizationType('evolutionary')}
            >
              Evolutionary Tree
            </button>
            <button 
              className={`${classes.controlButton} ${visualizationType === 'category' ? classes.activeControlButton : ''}`}
              onClick={() => setVisualizationType('category')}
            >
              Trait Category
            </button>
          </div>

          {/* Network visualization canvas */}
          <div 
            ref={containerRef}
            className={classes.visualizationContainer}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
            onWheel={handleWheel}
          >
            <canvas 
              ref={canvasRef}
              className={classes.networkCanvas}
            />
          </div>
        </div>

        {/* Trait Probabilities Section */}
        <div className={classes.traitProbabilitiesContainer}>
          <h2 className={classes.sectionHeading}>Trait Probabilities</h2>

          <button 
            className={classes.emergenceProbabilitiesButton}
            onClick={() => setShowEmergenceProbabilities(!showEmergenceProbabilities)}
          >
            Show Emergence Probabilities
          </button>
        </div>

        {/* Environmental Conditions Section */}
        <div className={classes.environmentalConditions}>
          <h2 className={classes.sectionHeading}>Environmental Conditions</h2>

          <div className={classes.environmentalSlidersContainer}>
            {renderEnvironmentalSlider('eldritch_influence', 'Eldritch Influence')}
            {renderEnvironmentalSlider('corruption_level', 'Corruption Level')}
            {renderEnvironmentalSlider('resource_scarcity', 'Resource Scarcity')}
            {renderEnvironmentalSlider('ambient_magic', 'Ambient Magic')}
            {renderEnvironmentalSlider('darkness', 'Darkness')}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EnhancedEvolutionSimulator;
