import React, { useState, useEffect } from 'react';
import Background from '../../../layout/Background';
import RevealText from '../../../core/effects/texteffects/RevealText';
import ProjectCard from '../../../core/effects/holdereffects/ProjectCard';
import PersonProfileCard from '../../../core/effects/holdereffects/PersonProfileCard';
import ScrollingTextAnimation from '../../../core/effects/texteffects/ScrollingTextAnimation';
import MeteorShower from '../../../core/effects/cosmiceffects/MeteorShower';
import { createUseStyles } from 'react-jss';

// Styles for the CreatorsPage component
const useStyles = createUseStyles({
  creatorsPage: {
    minHeight: '100vh',
    color: '#fff',
    overflowX: 'hidden',
  },

  content: {
    position: 'relative',
    zIndex: 2,
    paddingTop: '60vh', // Space for header and title
    '@media (max-width: 768px)': {
      paddingTop: '30vh',
    }
  },

  header: {
    position: 'relative',
    marginBottom: '2rem',
    textAlign: 'center',
    height: '200px',
    overflow: 'visible',
  },

  mainTitle: {
    fontSize: '3.5rem',
    fontWeight: 300,
    color: '#bfad7f',
    textTransform: 'uppercase',
    letterSpacing: '0.25em',
    margin: 0,
    textShadow: '0 0 10px rgba(191, 173, 127, 0.3)',
    '@media (max-width: 768px)': {
      fontSize: '2.2rem',
      letterSpacing: '0.2em',
    }
  },

  scrollableContent: {
    position: 'relative',
    zIndex: 5,
    opacity: 0,
    transition: 'opacity 0.5s ease',
  },

  loaded: {
    opacity: 1,
  },

  section: {
    padding: '5rem 2rem',
    maxWidth: '1200px',
    margin: '0 auto',
    position: 'relative',
    '@media (max-width: 768px)': {
      padding: '3rem 1.5rem',
    }
  },

  profileSection: {
    padding: '2rem',
    '@media (max-width: 768px)': {
      padding: '1rem',
    }
  },

  intro: {
    textAlign: 'center',
    paddingTop: '2rem',
  },

  introQuote: {
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontSize: '1.3rem',
    lineHeight: 1.7,
    color: 'rgba(191, 173, 127, 0.8)',
    marginBottom: '0.5rem',
    fontWeight: 300,
    fontStyle: 'italic',
    '@media (max-width: 768px)': {
      fontSize: '1.1rem',
    }
  },

  quoteAuthor: {
    fontSize: '1rem',
    color: 'rgba(224, 224, 224, 0.6)',
    textAlign: 'right',
    maxWidth: '600px',
    margin: '0 auto 3rem',
    fontWeight: 300,
    marginBottom: '2rem',
  },

  subtitle: {
    fontSize: '1.1rem',
    color: 'rgba(224, 224, 224, 0.7)',
    maxWidth: '700px',
    margin: '0 auto',
    lineHeight: 1.6,
    marginBottom: '20rem',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300,
    '@media (max-width: 768px)': {
      fontSize: '0.9rem',
    }
  },

  projectsSection: {
    paddingTop: '23rem',
    paddingBottom: '6rem',
  },

  sectionTitle: {
    fontSize: '2.2rem',
    textAlign: 'center',
    color: '#bfad7f',
    fontWeight: 300,
    letterSpacing: '0.1em',
    marginBottom: '0.5rem',
    '@media (max-width: 768px)': {
      fontSize: '1.8rem',
    }
  },

  titleDivider: {
    width: '150px',
    height: '1px',
    margin: '1rem auto',
  },

  projectsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
    gap: '2rem',
    marginTop: '3rem',
    '@media (max-width: 768px)': {
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    }
  },

  finalSection: {
    textAlign: 'center',
    padding: '6rem 2rem',
  },

  finalText: {
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontSize: '1.3rem',
    lineHeight: 1.8,
    color: 'rgba(191, 173, 127, 0.7)',
    fontWeight: 300,
    fontStyle: 'italic',
    maxWidth: '800px',
    margin: '0 auto',
    '@media (max-width: 768px)': {
      fontSize: '1.1rem',
    }
  }
});

/**
 * CreatorsPage Component
 * 
 * Main component for the Creator profile page featuring:
 * - Enhanced split layout profile card
 * - Animated sections that reveal on scroll
 * - Meteor shower effect in the header area
 */

const CreatorsPage = () => {
  // Hooks
  const classes = useStyles();
  const [isLoaded, setIsLoaded] = useState(false);
  const [showMeteors] = useState(true);

  // Creator profile data
  const creatorData = {
    name: "Rosalia Fialkova",
    role: "Systems Design Engineer",
    tagline: 
      <>
        I craft immersive systems<br />
        and experiences.
      </>,
    image: "/assets/images/creators/rosalia.png",
    bio: [
      "I strive to create accessible and well-structured systems that balance technical excellence with user experience. My personal projects are born from my fascination with cycles of rebirth and destruction—the eternal dance between light and darkness. I weave mathematical modeling, narrative structure, and visual aesthetics to craft immersive tools and simulations.",
      "I'm in my element when I'm crafting high-quality, reusable (I hate seeing a good program not find multiple homes!) systems across frontend interfaces and backend architectures. Combining mathematical modeling with aesthetic intuition and developing modular components is fun!"
    ],
    stats: [
      { value: "Stellar", label: "Motivation to Design" },
      { 
        value: <span className="math-formula">tanh(σ(i))</span>, 
        label: "Favorite Function" 
      },
      { value: "∞", label: "Cycles of Debugging" }
    ],
    experience: [
      {
        content: "Throughout my career, I've focused on developing software systems that seamlessly integrate front-end interfaces with robust back-end architectures. I've specialized in creating accessible, modular components that can be reused across multiple projects while maintaining high levels of quality and performance."
      },
      {
        content: "My technical expertise includes system architecture design, mathematical modeling for interactive simulations, and the development of intuitive user interfaces. I've successfully applied these skills across various domains, adapting my approach to best serve each project's unique requirements."
      }
    ],
    projects: [
      {
        content: "For the Crescent TTRPG, I developed a tension-based character progression system where players must balance competing motivations. This mechanical framework reinforces the game's themes of sacrifice and transformation while providing meaningful player choices."
      },
      {
        content: "The Crucible Platform translates complex evolutionary algorithms into interactive visualizations, making abstract concepts tangible. By applying hyperbolic tangent normalization to multi-trait affinities, I created a system that marries biological plausibility with computational efficiency."
      }
    ]
  };

  // Updated project data array with more detailed descriptions
  const projects = [
