    flexDirection: 'column',
    position: 'relative',
    // Slower, more deliberate transition properties
    transition: 'transform 0.6s cubic-bezier(0.25, 0.1, 0.25, 1.0), box-shadow 0.6s cubic-bezier(0.25, 0.1, 0.25, 1.0)',
    // Optimized GPU acceleration
    transform: 'translateZ(0)',
    // Only use will-change when needed (applied via className)
    boxShadow: '0 8px 25px rgba(0, 0, 0, 0.25)',
    height: '100%'
  },
  projectCardHover: {
    transform: 'translateZ(0) scale(1.02) translateY(-5px)',
    boxShadow: '0 16px 35px rgba(0, 0, 0, 0.3), 0 0 25px rgba(191, 173, 127, 0.15)'
  },
  cardBorderGlow: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    borderRadius: '8px',
    pointerEvents: 'none',
    opacity: 0,
    transition: 'opacity 0.8s ease',
    boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0), 0 0 30px rgba(191, 173, 127, 0)',
    zIndex: 0
  },
  cardBorderGlowActive: {
    opacity: 1,
    boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0.2), 0 0 30px rgba(191, 173, 127, 0.15)'
  },
  imageContainer: {
    width: '100%',
    height: '220px',
    position: 'relative',
    overflow: 'hidden',
    borderTopLeftRadius: '8px',
    borderTopRightRadius: '8px'
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    // Slower, more elegant transition
    transition: 'transform 0.9s cubic-bezier(0.2, 0.1, 0.3, 1.0)',
    transform: 'scale(1)',
    backfaceVisibility: 'hidden'
  },
  imageHover: {
    transform: 'scale(1.05)'
  },
  imageOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'linear-gradient(to bottom, rgba(15, 15, 15, 0.1), rgba(15, 15, 15, 0.3))',
    transition: 'opacity 0.7s ease',
    opacity: 0.5
  },
  imageOverlayHover: {
    opacity: 0.2
  },
  contentContainer: {
    padding: '1.5rem 1.8rem',
    display: 'flex',
    flexDirection: 'column',
    flex: 1
  },
  titleArea: {
    marginBottom: '0.75rem'
  },
  title: {
    fontSize: '1.6rem',
    color: '#bfad7f',
    letterSpacing: '0.04em',
    margin: 0,
    fontWeight: 300,
    lineHeight: 1.2,
  },
  category: {
    fontSize: '0.95rem',
    color: 'rgba(191, 173, 127, 0.7)',
    letterSpacing: '0.05em',
    fontWeight: 300,
    fontStyle: 'italic',
    margin: '0.4rem 0 0 0'
  },
  description: {
    fontSize: '0.90rem',
    color: 'rgba(224, 224, 224, 0.9)',
    lineHeight: 1.6,
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300,
    margin: '0.5rem 0 1.5rem 0',
    flex: 1
  },
  tagsContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.5rem',
    marginTop: 'auto',
    paddingTop: '1rem',
    borderTop: '1px solid rgba(160, 142, 97, 0.15)'
  },
  tag: {
    display: 'inline-flex',
    alignItems: 'center',
    padding: '0.3rem 0.7rem',
    borderRadius: '4px',
    backgroundColor: 'rgba(191, 173, 127, 0.1)',
    border: '1px solid rgba(191, 173, 127, 0.2)',
    color: 'rgba(191, 173, 127, 0.9)',
    fontSize: '0.75rem',
    fontWeight: 400,
    letterSpacing: '0.02em',
    transition: 'all 0.5s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.15)',
      borderColor: 'rgba(191, 173, 127, 0.3)'
    }
  },
  shimmerEffect: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundImage: 'linear-gradient(90deg, rgba(255,255,255,0) 0%, rgba(255,255,255,0.05) 50%, rgba(255,255,255,0) 100%)',
    backgroundSize: '200% 100%',
    opacity: 0,
    pointerEvents: 'none',
    zIndex: 1
  },
  shimmerEffectActive: {
    opacity: 1,
    animation: '$shimmer 2.6s infinite'
  },
  '@keyframes shimmer': {
    '0%': { backgroundPosition: '200% 0' },
    '100%': { backgroundPosition: '-200% 0' }
  },
  cardAnimation: {
    display: 'block',
    transform: 'translateZ(0)',
    backfaceVisibility: 'hidden'
  },
  willChange: {
    willChange: 'transform, box-shadow'
  }
});

/**
 * ProjectCard Component - Optimized Version
 * 
 * This component has been optimized for performance with:
 * - Efficient render cycles
 * - Reduced layout thrashing
 * - Better GPU acceleration
 * - Responsive design considerations
 * - Support for reduced motion preferences
 *
 * @param {Object} props - Component props
 * @param {Object} props.project - Project data
 * @param {Object} [props.animationConfig] - Optional animation configuration
 * @param {Function} [props.onClick] - Optional click handler
 */
const ProjectCard = ({
  project,
  animationConfig = {},
  onClick
}) => {
  // Initialize styles
  const classes = useStyles();

  // Reference for card element
  const cardRef = useRef(null);

  // Detect if user prefers reduced motion
  const prefersReducedMotion = useReducedMotion();

  // Detect if card is in view - used for animations
  const inView = useInView(cardRef, {
    once: animationConfig.once !== undefined ? animationConfig.once : true,
    threshold: animationConfig.threshold || 0.15,
    margin: "0px 0px -100px 0px" 
  });

  // State with single update - using lazy initialization
  const [state, setState] = useState({
    isHovered: false,
    isVisible: false
  });

  // Memoize config to prevent recreations
  const config = useMemo(() => ({
    delay: animationConfig.delay || 0,
    duration: animationConfig.duration || 1.2, // Increased from 0.7 to 1.2 for slower animation
    distance: prefersReducedMotion ? 0 : (animationConfig.distance || 50),
    threshold: animationConfig.threshold || 0.15,
    once: animationConfig.once !== undefined ? animationConfig.once : true,
    ease: animationConfig.ease || [0.2, 0.05, 0.2, 1.0], // Adjusted easing for smoother start/end
  }), [animationConfig, prefersReducedMotion]);

  // Set isVisible when inView changes - optimized with callback ref
  const isFirstViewRef = useRef(true);

  // Handle mouse events with minimal updates
  const handleMouseEnter = useCallback(() => {
    // Apply willChange only on hover to minimize memory usage
    if (cardRef.current) {
      cardRef.current.classList.add(classes.willChange);
    }
    setState(prev => ({ ...prev, isHovered: true }));
  }, [classes.willChange]);

  const handleMouseLeave = useCallback(() => {
    setState(prev => ({ ...prev, isHovered: false }));
    // Remove willChange after animation completes to free up resources
    setTimeout(() => {
      if (cardRef.current) {
        cardRef.current.classList.remove(classes.willChange);
      }
    }, 600); // Match new slower transition duration
  }, [classes.willChange]);

  // Optimize click handler
  const handleClick = useCallback(() => {
    if (onClick) onClick(project);
  }, [onClick, project]);

  // Prepare animation variants - only recompute when dependencies change
  const cardVariants = useMemo(() => ({
    hidden: {
      opacity: 0,
      y: config.distance,
      transition: {
        duration: 0
      }
    },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: config.duration,
        ease: config.ease,
        delay: config.delay / 1000,
      }
    }
  }), [config]);

  // Update visibility state when inView changes - do this check on render instead of useEffect
  if (inView && isFirstViewRef.current) {
    isFirstViewRef.current = false;
    // Update state when component is in view
    if (!state.isVisible) {
      setState(prev => ({ ...prev, isVisible: true }));
    }
  }

  // Determine visibility for animation
  const isVisible = inView || state.isVisible;

  // Compute class names conditionally
  const containerClassName = `${classes.projectCard} ${state.isHovered ? classes.projectCardHover : ''}`;
  const imageClassName = `${classes.image} ${state.isHovered ? classes.imageHover : ''}`;
  const imageOverlayClassName = `${classes.imageOverlay} ${state.isHovered ? classes.imageOverlayHover : ''}`;
  const borderGlowClassName = `${classes.cardBorderGlow} ${state.isHovered ? classes.cardBorderGlowActive : ''}`;
  const shimmerClassName = `${classes.shimmerEffect} ${state.isHovered ? classes.shimmerEffectActive : ''}`;

  // Determine if we should use simplified animations
  const useSimplifiedAnimations = prefersReducedMotion || 
                                 (typeof window !== 'undefined' && window.innerWidth <= 768);

  // Generate animation settings based on preferences
  const animationSettings = useSimplifiedAnimations 
    ? { 
        initial: false, 
        animate: { opacity: 1, y: 0 },
        transition: { duration: 0.5 }
      }
    : {
        initial: "hidden",
        animate: isVisible ? "visible" : "hidden",
        variants: cardVariants
      };

  return (
    <motion.div
      ref={cardRef}
      className={classes.cardAnimation}
      {...animationSettings}
      layout={!prefersReducedMotion}
    >
      <div
        className={containerClassName}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onClick={handleClick}
        style={{ cursor: onClick ? 'pointer' : 'default' }}
      >
        {/* Border glow effect */}
        <div className={borderGlowClassName} />

        {/* Project Image */}
        <div className={classes.imageContainer}>
          <img
            src={project.image}
            alt={project.title}
            className={imageClassName}
            loading="lazy"
            decoding="async"
          />
          <div className={imageOverlayClassName} />
          <div className={shimmerClassName} />
        </div>

        {/* Content Section */}
        <div className={classes.contentContainer}>
          {/* Title and Category */}
          <div className={classes.titleArea}>
            <h2 className={classes.title}>{project.title}</h2>
            <p className={classes.category}>{project.category}</p>
          </div>

          {/* Description */}
          <p className={classes.description}>{project.description}</p>

          {/* Tags */}
          {project.tags && project.tags.length > 0 && (
            <div className={classes.tagsContainer}>
              {project.tags.map((tag, index) => (
                <span key={index} className={classes.tag}>
                  {tag}
                </span>
              ))}
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
};

// Add display name for better debugging
ProjectCard.displayName = 'ProjectCard';

export default React.memo(ProjectCard);
