import React, { useRef, useEffect, useState } from 'react';
import './landingNavigation.css';

const LandingNavigation = ({ 
  mainSections, 
  activeSection, 
  expandedSection, 
  expandedSubsection,
  animatingLayout,
  setActiveSection, 
  setExpandedSection, 
  setExpandedSubsection,
  setAnimatingLayout
}) => {
  const sectionsContainerRef = useRef(null);
  const sectionRefs = useRef({});
  const subsectionRefs = useRef({});

  // State for tracking animation states
  const [animationStates, setAnimationStates] = useState({
    expanding: false,
    collapsing: false,
    targetSection: null,
    previousSection: null
  });

  // Effect for handling section visibility once animation completes
  useEffect(() => {
    if (expandedSection) {
      // Show the subsections container with a class
      const container = document.querySelector(`.section-wrapper[data-id="${expandedSection}"] .subsections-container`);
      if (container) {
        // Small delay to ensure DOM is ready
        setTimeout(() => {
          container.classList.add('visible');
        }, 50);
      }
    }
  }, [expandedSection]);

  // Effect for handling subsection visibility once animation completes
  useEffect(() => {
    if (expandedSubsection) {
      // Show the nested subsections container with a class
      const container = document.querySelector(`.subsection-wrapper[data-id="${expandedSubsection}"] .nested-subsections-container`);
      if (container) {
        // Small delay to ensure DOM is ready
        setTimeout(() => {
          container.classList.add('visible');
        }, 50);
      }
    }
  }, [expandedSubsection]);

  // Effect to handle animation completion
  useEffect(() => {
    if (animatingLayout) {
      const timer = setTimeout(() => {
        setAnimatingLayout(false);
        setAnimationStates(prev => ({
          ...prev,
          expanding: false,
          collapsing: false
        }));
      }, 400); // Match the transition duration

      return () => clearTimeout(timer);
    }
  }, [animatingLayout, setAnimatingLayout]);

  // Navigate to section
  const handleNavigate = (path, event, isExpandable) => {
    if (isExpandable) {
      event.preventDefault();
      event.stopPropagation();
      return;
    }

    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Handle section hover
  const handleSectionHover = (sectionId) => {
    if (!animatingLayout) {
      setActiveSection(sectionId);
    }
  };

  // NEW: Simplified section click handler
  const handleSectionClick = (sectionId, event) => {
    const section = mainSections.find(section => section.id === sectionId);

    if (!section || !section.expandable) return;

    event.stopPropagation();

    // If already animating, don't allow new animations
    if (animatingLayout) return;

    setAnimatingLayout(true);

    // If this section is already expanded, collapse it
    if (expandedSection === sectionId) {
      // 1. Hide the subsections visually
      const container = document.querySelector(`.section-wrapper[data-id="${sectionId}"] .subsections-container`);
      if (container) {
        container.classList.remove('visible');
      }

      setAnimationStates({
        expanding: false,
        collapsing: true,
        targetSection: null,
        previousSection: sectionId
      });

      // 2. With a small delay to allow animation to start, update the state
      setTimeout(() => {
        setExpandedSection(null);
      }, 100);
    } else {
      // We're expanding a new section

      // If there's a currently expanded section, collapse it first
      if (expandedSection) {
        // 1. Hide the current subsections visually
        const container = document.querySelector(`.section-wrapper[data-id="${expandedSection}"] .subsections-container`);
        if (container) {
          container.classList.remove('visible');
        }

        setAnimationStates({
          expanding: true,
          collapsing: true,
          targetSection: sectionId,
          previousSection: expandedSection
        });

        // 2. Update states with a small delay
        setTimeout(() => {
          setExpandedSection(sectionId);
          setExpandedSubsection(null);
        }, 100);
      } else {
        // No section is expanded, so we can just expand the new one
        setAnimationStates({
          expanding: true,
          collapsing: false,
          targetSection: sectionId,
          previousSection: null
        });

        // Update state immediately
        setExpandedSection(sectionId);
      }
    }
  };

  // NEW: Simplified subsection hover/click handler
  const handleSubsectionHover = (subsectionId, event) => {
    // Prevent event propagation
    if (event) {
      event.stopPropagation();
    }

    const isClickEvent = event.type === 'click';

    // If not a click event or already animating, don't proceed
    if (!isClickEvent || animatingLayout) return;

    setAnimatingLayout(true);

    // Subsection is already expanded, collapse it
    if (expandedSubsection === subsectionId) {
      // 1. Hide the nested subsections visually
      const container = document.querySelector(`.subsection-wrapper[data-id="${subsectionId}"] .nested-subsections-container`);
      if (container) {
        container.classList.remove('visible');
      }

      // 2. Update state with small delay
      setTimeout(() => {
        setExpandedSubsection(null);
      }, 100);
    } else {
      // We're expanding a new subsection

      // If there's already an expanded subsection, hide it first
      if (expandedSubsection) {
        const container = document.querySelector(`.subsection-wrapper[data-id="${expandedSubsection}"] .nested-subsections-container`);
        if (container) {
          container.classList.remove('visible');
        }

        // Update state with small delay
        setTimeout(() => {
          setExpandedSubsection(subsectionId);
        }, 100);
      } else {
        // No subsection is expanded, so expand this one directly
        setExpandedSubsection(subsectionId);
      }
    }
  };

  // Handle section leave
  const handleSectionLeave = () => {
    if (!animatingLayout) {
      setActiveSection(null);
    }
  };

  // Render SVG icons for main sections
  const renderIcon = (iconType) => {
    switch(iconType) {
      case 'circles':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="16" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
              <circle cx="26" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
            </svg>
          </div>
        );
      case 'triangle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M21 7L35 32H7L21 7Z" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
            </svg>
          </div>
        );
      case 'targetCircle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="21" cy="21" r="5" fill="#A08E61" opacity="0.7" />
            </svg>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div 
      ref={sectionsContainerRef}
      className={`sections-container ${animatingLayout ? 'animating' : ''}`}
    >
      {mainSections.map((section, index) => (
        <div 
          key={section.id} 
          className={`section-wrapper ${expandedSection === section.id ? 'expanded-section' : ''}`} 
          data-id={section.id}
          ref={el => sectionRefs.current[section.id] = el}
        >
          <div 
            className={`section-row ${activeSection === section.id ? 'active' : ''} ${expandedSection === section.id ? 'expanded' : ''}`}
            onMouseEnter={() => handleSectionHover(section.id)}
            onMouseLeave={handleSectionLeave}
            onClick={(e) => {
              if (section.expandable) {
                handleSectionClick(section.id, e);
              } else {
                handleNavigate(section.path, e);
              }
            }}
            style={{ animationDelay: `${index * 0.15 + 0.3}s` }}
          >
            <div className="section-content">
              <div className="section-icon-wrapper">
                {renderIcon(section.icon)}
              </div>
              <div className="section-text">
                <h2 className="section-title">{section.title}</h2>
                <p className="section-description">{section.description}</p>
              </div>
              {section.expandable && (
                <div className={`expand-indicator ${expandedSection === section.id ? 'expanded' : ''}`}>
                  <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
                  </svg>
                </div>
              )}
            </div>
            <div className="diagonal-cut" />
          </div>

          {/* Expandable Subsections - Now using visibility class instead of conditional rendering */}
          {section.expandable && (
            <div className={`subsections-container ${expandedSection === section.id ? 'visible' : ''}`}>
              {section.subsections.map((subsection) => (
                <div 
                  key={subsection.id} 
                  className={`subsection-wrapper ${expandedSubsection === subsection.id ? 'expanded-subsection' : ''}`} 
                  data-id={subsection.id}
                  ref={el => subsectionRefs.current[subsection.id] = el}
                >
                  <div 
                    className={`subsection-row ${expandedSubsection === subsection.id ? 'active' : ''}`}
                    onMouseEnter={(e) => handleSubsectionHover(subsection.id, e)}
                    onClick={(e) => subsection.expandable ? handleSubsectionHover(subsection.id, e) : handleNavigate(subsection.path, e)}
                  >
                    <div className="subsection-content">
                      <h3 className="subsection-title">{subsection.title}</h3>
                      <p className="subsection-description">{subsection.description}</p>
                      {subsection.expandable && (
                        <div className={`expand-indicator small ${expandedSubsection === subsection.id ? 'expanded' : ''}`}>
                          <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
                          </svg>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Nested Subsections - Using visibility class */}
                  {subsection.expandable && (
                    <div className={`nested-subsections-container ${expandedSubsection === subsection.id ? 'visible' : ''}`}>
                      {subsection.subsections.map((nestedSubsection) => (
                        <div 
                          key={nestedSubsection.id}
                          className="nested-subsection-row"
                          onClick={(e) => handleNavigate(nestedSubsection.path, e)}
                        >
                          <div className="nested-subsection-content">
                            <h4 className="nested-subsection-title">{nestedSubsection.title}</h4>
                            <p className="nested-subsection-description">{nestedSubsection.description}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default LandingNavigation;
