import React, { useRef, useState } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
  profile: {
    backgroundColor: 'rgba(15, 15, 15, 0.7)',
    border: '1px solid rgba(160, 142, 97, 0.2)',
    borderRadius: '3px',
    padding: '3rem',
    margin: '4rem auto',
    maxWidth: '1000px',
    display: 'grid',
    gridTemplateColumns: '1fr 2fr',
    gap: '3rem',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
    position: 'relative',
    '@media (max-width: 768px)': {
      gridTemplateColumns: '1fr',
      padding: '2rem'
    }
  },
  profileBorder: {
    '&::before': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      width: 0,
      height: '1px',
      background: 'linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0))',
      transition: 'width 1s ease 0.5s'
    },
    '&$inView::before': {
      width: '100%'
    }
  },
  inView: {},
  profileImageContainer: {
    position: 'relative',
    width: '100%',
    aspectRatio: '1/1',
    borderRadius: '3px',
    overflow: 'hidden',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    transition: 'box-shadow 0.3s ease',
    '&:hover': {
      boxShadow: '0 5px 20px rgba(191, 173, 127, 0.4)',
      '& $twinkleStar': {
        animation: '$twinkleIntense 1s infinite'
      },
      '& $vertexTwinkle': {
        opacity: 0.9,
        transform: 'scale(1.5)'
      }
    },
    '@media (max-width: 768px)': {
      maxWidth: '250px',
      margin: '0 auto'
    }
  },
  profileImage: {
    position: 'relative',
    width: '100%',
    height: '100%',
    overflow: 'hidden',
    '&::after': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      boxShadow: 'inset 0 0 10px rgba(191, 173, 127, 0.3)',
      borderRadius: '3px',
      pointerEvents: 'none'
    }
  },
  imageWrapper: {
    position: 'relative',
    width: '100%',
    height: '100%',
    overflow: 'hidden'
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transition: 'transform 0.6s ease, object-position 0.6s ease'
  },
  twinkleStar: {
    position: 'absolute',
    width: '3px',
    height: '3px',
    borderRadius: '50%',
    backgroundColor: 'rgba(191, 173, 127, 0.7)',
    animation: '$twinkle 3s infinite',
    boxShadow: '0 0 3px rgba(191, 173, 127, 0.5)',
    zIndex: 2
  },
  vertexTwinkle: {
    position: 'absolute',
    width: '6px',
    height: '6px',
    borderRadius: '50%',
    backgroundColor: 'rgba(191, 173, 127, 0.7)',
    boxShadow: '0 0 5px rgba(191, 173, 127, 0.7)',
    zIndex: 3,
    opacity: 0.6,
    transition: 'transform 0.3s ease, opacity 0.3s ease'
  },
  '@keyframes twinkle': {
    '0%': { opacity: 0.2, transform: 'scale(0.8)' },
    '50%': { opacity: 0.8, transform: 'scale(1.2)' },
    '100%': { opacity: 0.2, transform: 'scale(0.8)' }
  },
  '@keyframes twinkleIntense': {
    '0%': { opacity: 0.4, transform: 'scale(0.8)' },
    '50%': { opacity: 1, transform: 'scale(1.5)' },
    '100%': { opacity: 0.4, transform: 'scale(0.8)' }
  },
  profileDetails: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    '@media (max-width: 768px)': {
      textAlign: 'center'
    }
  },
  profileName: {
    fontSize: '2.2rem',
    color: '#bfad7f',
    fontWeight: 300,
    letterSpacing: '0.1em',
    marginBottom: '0.5rem',
    '@media (max-width: 480px)': {
      fontSize: '1.8rem'
    }
  },
  profileRole: {
    fontSize: '1.1rem',
    color: 'rgba(191, 173, 127, 0.7)',
    marginBottom: '2rem',
    letterSpacing: '0.05em',
    fontWeight: 300,
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontStyle: 'italic'
  },
  profileBio: {
    fontSize: '1rem',
    color: 'rgba(224, 224, 224, 0.7)',
    lineHeight: 1.8,
    marginBottom: '2rem',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300
  },
  profileStats: {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
