// Helper to generate random positions for stars along the border
const generateStarPositions = (count) => {
  const stars = [];
  for (let i = 0; i < count; i++) {
    // Decide which side of the border
    const side = Math.floor(Math.random() * 4); // 0: top, 1: right, 2: bottom, 3: left
    let position = {};

    switch(side) {
      case 0: // top
        position = {
          top: 0,
          left: `${Math.random() * 100}%`,
          animationDelay: `${Math.random() * 3}s`
        };
        break;
      case 1: // right
        position = {
          top: `${Math.random() * 100}%`,
          right: 0,
          animationDelay: `${Math.random() * 3}s`
        };
        break;
      case 2: // bottom
        position = {
          bottom: 0,
          left: `${Math.random() * 100}%`,
          animationDelay: `${Math.random() * 3}s`
        };
        break;
      case 3: // left
        position = {
          top: `${Math.random() * 100}%`,
          left: 0,
          animationDelay: `${Math.random() * 3}s`
        };
        break;
      default:
        break;
    }

    stars.push(position);
  }
  return stars;
};

/**
 * Enhanced PersonProfileCard Component
 * 
 * A reusable component for displaying a person's profile with image, bio, and statistics.
 * Features scroll-based animation, responsive design, and a twinkling border effect.
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string[]} props.person.bio - Array of bio paragraphs
 * @param {Object[]} props.person.stats - Array of statistic objects
 * @param {string|number} props.person.stats[].value - The statistic value
 * @param {string} props.person.stats[].label - The statistic label
 * @param {Object} [props.animationConfig] - Optional animation configuration
 */
const PersonProfileCard = ({ 
  person, 
  animationConfig = {
    useInternalRef: true,
    threshold: 0.2,
    once: true,
    initialY: 50,
    duration: 0.8,
    ease: "easeOut"
  }
}) => {
  const classes = useStyles();

  // Set up ref and inView detection
  const internalRef = useRef(null);

  // Image positioning state
  const [imagePosition, setImagePosition] = useState({
    x: 50, // 0-100, represents position in percentage
    y: 50,
    scale: 1, // 1-1.5
  });

  // Generate random star positions
  const starPositions = useRef(generateStarPositions(15)).current;

  // Always call useInView (to follow React hook rules)
  const internalInView = useInView(
    internalRef, 
    { 
      once: animationConfig.once, 
      threshold: animationConfig.threshold 
    }
  );

  // Use either internal or external ref/inView based on config
  const ref = animationConfig.useInternalRef ? internalRef : animationConfig.ref;
  const isInView = animationConfig.useInternalRef ? internalInView : animationConfig.isInView;

  // Reset image position
  const resetImagePosition = () => {
    setImagePosition({
      x: 50,
      y: 50,
      scale: 1
    });
  };

  return (
    <motion.div 
      ref={ref}
      className={`${classes.profile} ${classes.profileBorder} ${isInView ? classes.inView : ''}`}
      initial={{ opacity: 0, y: animationConfig.initialY }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig.initialY }}
      transition={{ 
        duration: animationConfig.duration, 
        ease: animationConfig.ease 
      }}
    >
      <div className={classes.profileImageContainer}>
        <div className={classes.profileImage}>
          <div className={classes.imageWrapper}>
            <img 
              src={person.image} 
              alt={person.name} 
              className={classes.image}
              style={{
                objectPosition: `${imagePosition.x}% ${imagePosition.y}%`,
                transform: `scale(${imagePosition.scale})`
              }}
            />
          </div>

          {/* Twinkling stars along the border */}
          {starPositions.map((position, index) => (
            <div 
              key={`star-${index}`}
              className={classes.twinkleStar} 
              style={position}
            />
          ))}

          {/* Vertex twinkles at the corners */}
          <div className={classes.vertexTwinkle} style={{ top: 0, left: 0 }} />
          <div className={classes.vertexTwinkle} style={{ top: 0, right: 0 }} />
          <div className={classes.vertexTwinkle} style={{ bottom: 0, left: 0 }} />
          <div className={classes.vertexTwinkle} style={{ bottom: 0, right: 0 }} />
        </div>

        {/* Image position controls */}
        <div className={classes.imageControls}>
          <div className={classes.controlGroup}>
            <span className={classes.controlLabel}>Horizontal</span>
            <input 
              type="range" 
              min="0" 
              max="100" 
              value={imagePosition.x}
              onChange={(e) => setImagePosition({...imagePosition, x: Number(e.target.value)})}
              className={classes.controlSlider}
            />
          </div>

          <div className={classes.controlGroup}>
            <span className={classes.controlLabel}>Vertical</span>
            <input 
              type="range" 
              min="0" 
              max="100" 
              value={imagePosition.y}
              onChange={(e) => setImagePosition({...imagePosition, y: Number(e.target.value)})}
              className={classes.controlSlider}
            />
          </div>

          <div className={classes.controlGroup}>
            <span className={classes.controlLabel}>Zoom</span>
            <input 
              type="range" 
              min="100" 
              max="150" 
              value={imagePosition.scale * 100}
              onChange={(e) => setImagePosition({...imagePosition, scale: Number(e.target.value) / 100})}
              className={classes.controlSlider}
            />
          </div>

          <button 
            onClick={resetImagePosition} 
            className={classes.controlButton}
          >
            Reset
          </button>
        </div>
      </div>

      <div className={classes.profileDetails}>
        <h2 className={classes.profileName}>{person.name}</h2>
        <p className={classes.profileRole}>{person.role}</p>

        {person.bio.map((paragraph, index) => (
          <p key={index} className={classes.profileBio}>{paragraph}</p>
        ))}

        {person.stats && person.stats.length > 0 && (
          <div className={classes.profileStats}>
            {person.stats.map((stat, index) => (
              <div key={index} className={classes.stat}>
                <div className={classes.statValue}>{stat.value}</div>
                <div className={classes.statLabel}>{stat.label}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
