import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { createUseStyles } from 'react-jss';

// Styles for the simplified navigation bar matching the screenshot
const useStyles = createUseStyles({
  // Main container - much simpler and focused on minimalism
  navContainer: {
    width: '100%',
    backgroundColor: 'rgba(10, 10, 15, 0.8)',
    borderBottom: '1px solid rgba(191, 173, 127, 0.3)',
    display: 'flex',
    flexDirection: 'column',
    zIndex: 100,
  },

  // Upper bar with title, dropdowns and tab controls
  upperNav: {
    display: 'flex',
    padding: '0.75rem 1.5rem',
    alignItems: 'center',
    gap: '1rem',
  },

  // Title styling
  title: {
    color: '#bfad7f',
    fontSize: '1.25rem',
    margin: 0,
    fontWeight: 300,
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
  },
  logoIcon: {
    width: '20px',
    height: '20px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #bfad7f 0%, #7D6B9E 100%)',
  },

  // Dropdown selectors
  dropdown: {
    position: 'relative',
    marginLeft: '1rem',
  },
  dropdownButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.7)',
    border: '1px solid rgba(191, 173, 127, 0.4)',
    color: '#e0e0e0',
    padding: '0.55rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    minWidth: '220px',
    justifyContent: 'space-between',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(40, 40, 50, 0.7)',
      borderColor: 'rgba(191, 173, 127, 0.5)',
    },
  },
  dropdownMenu: {
    position: 'absolute',
    top: 'calc(100% + 4px)',
    left: 0,
    backgroundColor: 'rgba(55, 50, 35, 0.95)',
    minWidth: '100%',
    borderRadius: '4px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
    zIndex: 150,
    overflow: 'hidden',
    border: '1px solid rgba(191, 173, 127, 0.3)',
  },
  menuItem: {
    padding: '0.75rem 1rem',
    cursor: 'pointer',
    color: '#e0e0e0',
    transition: 'all 0.15s ease',
    borderBottom: '1px solid rgba(191, 173, 127, 0.1)',
    '&:last-child': {
      borderBottom: 'none',
    },
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  activeMenuItem: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
  },

  // Tab navigation
  tabNav: {
    display: 'flex',
    marginLeft: 'auto',
    borderBottom: 'none',
  },
  tab: {
    padding: '0.5rem 1.25rem',
    color: '#e0e0e0',
    opacity: 0.7,
    cursor: 'pointer',
    borderBottom: '3px solid transparent',
    fontSize: '0.95rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      opacity: 0.9,
      backgroundColor: 'rgba(191, 173, 127, 0.1)',
    },
  },
  activeTab: {
    color: '#bfad7f',
    opacity: 1,
    borderBottom: '3px solid #bfad7f',
  },

  // Zoom controls
  zoomControls: {
    display: 'flex',
    gap: '0.25rem',
    marginLeft: '1rem',
  },
  zoomButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.7)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    width: '32px',
    height: '32px',
    borderRadius: '4px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(40, 40, 50, 0.7)',
      borderColor: 'rgba(191, 173, 127, 0.5)',
    },
  },

  // Visualization styling - for the line network
  visualizationContainer: {
    display: 'flex',
    gap: '1rem',
    padding: '0 1.5rem 1rem',
    flexWrap: 'wrap',
  },
  visualizationControls: {
    display: 'flex',
    gap: '0.75rem',
  },
  visualButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.7)',
    border: '1px solid rgba(191, 173, 127, 0.4)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(40, 40, 50, 0.7)',
    },
  },
  activeVisualButton: {
    backgroundColor: 'rgba(191, 173, 127, 0.25)',
    borderColor: 'rgba(191, 173, 127, 0.6)',
  },

  // Probability indicator element
  probabilityIndicator: {
    backgroundColor: 'rgba(95, 75, 139, 0.6)',
    color: '#ffffff',
    padding: '0.5rem 0.75rem',
    borderRadius: '4px',
    position: 'absolute',
    bottom: '1rem',
    right: '1rem',
    fontWeight: 'bold',
    fontSize: '1.1rem',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.3)',
  },

  // Panel styling for each tab content
  tabPanel: {
    display: 'none',
    padding: '1rem 0',
  },
  activePanel: {
    display: 'block',
  },

  // Environment controls
  environmentControls: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '1.5rem',
    padding: '1rem 1.5rem',
  },
  sliderContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.5rem',
  },
  sliderLabel: {
    display: 'flex',
    justifyContent: 'space-between',
    color: '#e0e0e0',
    fontSize: '0.9rem',
  },
  sliderValue: {
    color: '#bfad7f',
    fontWeight: 'bold',
  },
  slider: {
    width: '100%',
    appearance: 'none',
    height: '6px',
    borderRadius: '3px',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    outline: 'none',
    opacity: 0.9,
    transition: 'opacity 0.2s',
    '&::-webkit-slider-thumb': {
      appearance: 'none',
      width: '16px',
      height: '16px',
      borderRadius: '50%',
      background: '#bfad7f',
      cursor: 'pointer',
      border: '2px solid rgba(10, 10, 15, 0.8)',
    },
    '&::-moz-range-thumb': {
      width: '16px',
      height: '16px',
      borderRadius: '50%',
      background: '#bfad7f',
      cursor: 'pointer',
      border: '2px solid rgba(10, 10, 15, 0.8)',
    },
    '&:hover': {
      opacity: 1,
    },
  },

  // Synthesis panel
  synthesisPanel: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1.5rem',
    padding: '1rem 1.5rem',
  },
  synthesisCard: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(125, 107, 158, 0.4)',
    borderRadius: '4px',
    padding: '1rem',
    position: 'relative',
  },
  synthesisHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '0.75rem',
  },
  synthesisTitle: {
    color: '#bfad7f',
    margin: 0,
    fontSize: '1.1rem',
  },
  synthesisProbability: {
    position: 'absolute',
    top: '1rem',
    right: '1rem',
    backgroundColor: 'rgba(95, 75, 139, 0.6)',
    color: '#ffffff',
    padding: '0.25rem 0.5rem',
    borderRadius: '4px',
    fontSize: '0.9rem',
  },
  synthesisDescription: {
    color: '#e0e0e0',
    fontSize: '0.9rem',
    lineHeight: 1.5,
  },

  // Media queries for responsiveness
  '@media (max-width: 900px)': {
    upperNav: {
      flexWrap: 'wrap',
      gap: '0.75rem',
    },
    tabNav: {
      marginLeft: 0,
      width: '100%',
      justifyContent: 'center',
      marginTop: '0.5rem',
    },
    visualizationControls: {
      width: '100%',
      justifyContent: 'center',
    },
  },
  '@media (max-width: 600px)': {
    dropdown: {
      marginLeft: 0,
      width: '100%',
    },
    dropdownButton: {
      width: '100%',
    },
  }
});

const EvoSimShowcaseBar = ({
  // Props with defaults
  bodyShape = 'amorphous',
  onBodyShapeChange,
  categoryFilter = null,
  onCategoryFilterChange,
  activeTab = 'synthesis',
  onTabChange,
  visualizationMode = 'direct',
  onVisualizationModeChange,
  environment = {},
  onEnvironmentChange,
  zoom = 1,
  onZoomChange,
  adaptationProbability = 0.37
}) => {
  const classes = useStyles();

  // Local state for dropdowns
  const [bodyShapeDropdownOpen, setBodyShapeDropdownOpen] = useState(false);
  const [categoryDropdownOpen, setCategoryDropdownOpen] = useState(false);

  // Refs for handling clicks outside dropdowns
  const bodyShapeRef = useRef(null);
  const categoryRef = useRef(null);

  // Body shapes configuration
  const bodyShapes = [
    { id: 'bipedal', name: 'Bipedal', description: 'Upright forms with manipulative upper limbs' },
    { id: 'quadrupedal', name: 'Quadrupedal', description: 'Four-limbed forms optimized for stability' },
    { id: 'serpentine', name: 'Serpentine', description: 'Elongated, limbless forms with flexibility' },
    { id: 'amorphous', name: 'Amorphous', description: 'Malleable forms without fixed structure' },
    { id: 'insectoid', name: 'Multi-limbed', description: 'Multi-limbed, segmented forms' },
    { id: 'avian', name: 'Avian', description: 'Forms optimized for flight and aerial movement' },
    { id: 'aquatic', name: 'Aquatic', description: 'Forms specialized for water environments' }
  ];

  // Categories configuration
  const categories = [
    { id: null, name: 'All Categories' },
    { id: 'locomotion', name: 'Locomotion', color: '#7D6B9E' },
    { id: 'metabolism', name: 'Metabolism', color: '#5C9D8B' },
    { id: 'sensory', name: 'Sensory Acuity', color: '#C99846' },
    { id: 'etheric', name: 'Etheric Adaptation', color: '#B54B4B' },
    { id: 'thermal', name: 'Thermal Regulation', color: '#A67C52' }
  ];

  // Tab options
  const tabs = [
    { id: 'traits', name: 'Traits' },
    { id: 'environment', name: 'Environment' },
    { id: 'synthesis', name: 'Synthesis' }
  ];

  // Visualization modes
  const visualizationModes = [
    { id: 'direct', name: 'Direct Connections' },
    { id: 'tree', name: 'Evolutionary Tree' },
    { id: 'category', name: 'Trait Category' }
  ];

  // Environmental factors
  const environmentalFactors = [
    { id: 'eldritch_influence', name: 'Eldritch Influence', default: 0.5 },
    { id: 'corruption_level', name: 'Corruption Level', default: 0.5 },
    { id: 'resource_scarcity', name: 'Resource Scarcity', default: 0.5 },
    { id: 'ambient_magic', name: 'Ambient Magic', default: 0.5 },
    { id: 'darkness', name: 'Darkness', default: 0.5 }
  ];

  // Sample synthesis options
  const synthesisOptions = [
    {
      id: 'dimensional_sight',
      name: 'Dimensional Sight',
      description: 'Advanced visual organs that can perceive multiple planes of reality simultaneously, revealing hidden truths and dangers.',
      probability: 0.37
    },
    {
      id: 'void_communion',
      name: 'Void Communion',
      description: 'Specialized neural structures that establish a symbiotic relationship with eldritch entities, granting access to forbidden knowledge.',
      probability: 0.19
    },
    {
      id: 'adaptive_metabolism',
      name: 'Adaptive Metabolism',
      description: 'Highly specialized digestive system that can process and derive energy from virtually any organic or inorganic substance.',
      probability: 0.28
    }
  ];

  // Handle click outside to close dropdowns
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (bodyShapeRef.current && !bodyShapeRef.current.contains(event.target)) {
        setBodyShapeDropdownOpen(false);
      }
      if (categoryRef.current && !categoryRef.current.contains(event.target)) {
        setCategoryDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle body shape selection
  const handleBodyShapeChange = (shape) => {
    onBodyShapeChange(shape);
    setBodyShapeDropdownOpen(false);
  };

  // Handle category filter change
  const handleCategoryChange = (category) => {
    onCategoryFilterChange(category);
    setCategoryDropdownOpen(false);
  };

  // Handle environmental factor change
  const handleEnvironmentChange = (factorId, value) => {
    onEnvironmentChange({
      ...environment,
      [factorId]: parseFloat(value)
    });
  };

  // Handle zoom level change
  const handleZoom = (direction) => {
    const newZoom = direction === 'in' 
      ? Math.min(zoom + 0.1, 3) 
      : Math.max(zoom - 0.1, 0.5);
    onZoomChange(newZoom);
  };

  return (
    <div className={classes.navContainer}>
      {/* Main navigation bar */}
      <div className={classes.upperNav}>
        {/* Title */}
        <h1 className={classes.title}>
          <span className={classes.logoIcon}></span>
          Crescent Evolution System
        </h1>

        {/* Body Shape Dropdown */}
        <div className={classes.dropdown} ref={bodyShapeRef}>
          <button 
            className={classes.dropdownButton}
            onClick={() => {
              setBodyShapeDropdownOpen(!bodyShapeDropdownOpen);
              setCategoryDropdownOpen(false);
            }}
          >
            <span>Body Shape:</span>
            <span style={{ fontWeight: 500 }}>
              {bodyShapes.find(shape => shape.id === bodyShape)?.name || 'Select'}
            </span>
            <span>▼</span>
          </button>

          <AnimatePresence>
            {bodyShapeDropdownOpen && (
              <motion.div 
                className={classes.dropdownMenu}
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
              >
                {bodyShapes.map(shape => (
                  <div 
                    key={shape.id}
                    className={`${classes.menuItem} ${bodyShape === shape.id ? classes.activeMenuItem : ''}`}
                    onClick={() => handleBodyShapeChange(shape.id)}
                  >
                    {shape.name}
                  </div>
                ))}
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Category Filter Dropdown */}
        <div className={classes.dropdown} ref={categoryRef}>
          <button 
            className={classes.dropdownButton}
            onClick={() => {
              setCategoryDropdownOpen(!categoryDropdownOpen);
              setBodyShapeDropdownOpen(false);
            }}
          >
            <span>Filter:</span>
            <span style={{ fontWeight: 500 }}>
              {categories.find(cat => cat.id === categoryFilter)?.name || 'All Categories'}
            </span>
            <span>▼</span>
          </button>

          <AnimatePresence>
            {categoryDropdownOpen && (
              <motion.div 
                className={classes.dropdownMenu}
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
              >
                {categories.map(category => (
                  <div 
                    key={category.id || 'all'}
                    className={`${classes.menuItem} ${categoryFilter === category.id ? classes.activeMenuItem : ''}`}
                    onClick={() => handleCategoryChange(category.id)}
                  >
                    {category.name}
                  </div>
                ))}
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Tab Navigation */}
        <div className={classes.tabNav}>
          {tabs.map(tab => (
            <div
              key={tab.id}
              className={`${classes.tab} ${activeTab === tab.id ? classes.activeTab : ''}`}
              onClick={() => onTabChange(tab.id)}
            >
              {tab.name}
            </div>
          ))}
        </div>

        {/* Zoom Controls */}
        <div className={classes.zoomControls}>
          <button 
            className={classes.zoomButton}
            onClick={() => handleZoom('in')}
            title="Zoom In - Increases the size of network nodes and connections"
          >
            +
          </button>
          <button 
            className={classes.zoomButton}
            onClick={() => handleZoom('out')}
            title="Zoom Out - Shows more of the network by reducing element size"
          >
            -
          </button>
        </div>
      </div>

      {/* Visualization Controls - Only shown when necessary */}
      {activeTab === 'traits' && (
        <div className={classes.visualizationContainer}>
          <div className={classes.visualizationControls}>
            {visualizationModes.map(mode => (
              <button
                key={mode.id}
                className={`${classes.visualButton} ${visualizationMode === mode.id ? classes.activeVisualButton : ''}`}
                onClick={() => onVisualizationModeChange(mode.id)}
              >
                {mode.name}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Environment Tab Panel */}
      <div className={`${classes.tabPanel} ${activeTab === 'environment' ? classes.activePanel : ''}`}>
        <div className={classes.environmentControls}>
          {environmentalFactors.map(factor => (
            <div className={classes.sliderContainer} key={factor.id}>
              <div className={classes.sliderLabel}>
                <span>{factor.name}</span>
                <span className={classes.sliderValue}>
                  {(environment[factor.id] || factor.default).toFixed(1)}
                </span>
              </div>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={environment[factor.id] || factor.default}
                onChange={(e) => handleEnvironmentChange(factor.id, e.target.value)}
                className={classes.slider}
              />
            </div>
          ))}
        </div>
      </div>

      {/* Synthesis Tab Panel */}
      <div className={`${classes.tabPanel} ${activeTab === 'synthesis' ? classes.activePanel : ''}`}>
        <div className={classes.synthesisPanel}>
          {synthesisOptions.map(option => (
            <div className={classes.synthesisCard} key={option.id}>
              <div className={classes.synthesisHeader}>
                <h3 className={classes.synthesisTitle}>{option.name}</h3>
              </div>
              <div className={classes.synthesisProbability}>
                {Math.round(option.probability * 100)}%
              </div>
              <p className={classes.synthesisDescription}>{option.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Adaptation probability indicator */}
      {(activeTab === 'traits' || activeTab === 'synthesis') && (
        <div className={classes.probabilityIndicator}>
          {Math.round(adaptationProbability * 100)}%
        </div>
      )}
    </div>
  );
};

export default EvoSimShowcaseBar;
