          // Draw zodiac vertex with warmer color
          ctx.beginPath();
          ctx.arc(vertex.x, vertex.y, vertex.size, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(207, 185, 130, ${vertex.opacity})`;
          ctx.fill();
        });
      });

      // Draw cursor influence - enhanced subtle glow with gradient
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 100
        );
        gradient.addColorStop(0, 'rgba(180, 162, 107, 0.04)');
        gradient.addColorStop(0.5, 'rgba(180, 162, 107, 0.02)');
        gradient.addColorStop(1, 'rgba(180, 162, 107, 0)');
        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 100, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      requestRef.current = requestAnimationFrame(animate);
    };

    animate(0);

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;

      // Reposition zodiac sign vertices on resize
      zodiacSignsRef.current = zodiacSignsRef.current.map(sign => {
        const vertices = sign.vertices.map((vertex, index) => {
          const originalVertex = ZODIAC_SIGNS.find(s => s.name === sign.name).vertices[index];
          return {
            ...vertex,
            x: originalVertex.x * window.innerWidth,
            y: originalVertex.y * window.innerHeight
          };
        });

        return {
          ...sign,
          vertices
        };
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [cursorPosition]);

  return <canvas ref={canvasRef} className="background-canvas" />;
};

export default Background;
