import React, { useRef } from 'react';
import './landingNavigation.css';

const LandingNavigation = ({ 
  mainSections, 
  activeSection, 
  expandedSection, 
  expandedSubsection,
  animatingLayout,
  setActiveSection, 
  setExpandedSection, 
  setExpandedSubsection,
  setAnimatingLayout
}) => {
  const sectionsContainerRef = useRef(null);

  // Navigate to section
  const handleNavigate = (path, event, isExpandable) => {
    if (isExpandable) {
      event.preventDefault();
      event.stopPropagation();
      return;
    }

    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Handle section hover
  const handleSectionHover = (sectionId) => {
    setActiveSection(sectionId);
  };

  // Handle section click - for expanding sections
  const handleSectionClick = (sectionId, event) => {
    if (mainSections.find(section => section.id === sectionId && section.expandable)) {
      event.stopPropagation();

      // Set animating flag to true
      setAnimatingLayout(true);

      // If currently expanded, add collapse animation class first
      if (expandedSection === sectionId) {
        const container = document.querySelector(`.section-wrapper[data-id="${sectionId}"] .subsections-container`);
        if (container) {
          // Add the collapse class to trigger the animation
          container.classList.add('collapse');

          // Calculate the current height of the section wrapper for smooth transition
          const sectionWrapper = document.querySelector(`.section-wrapper[data-id="${sectionId}"]`);
          const sectionRow = sectionWrapper.querySelector('.section-row');
          const currentHeight = sectionWrapper.offsetHeight;
          const rowHeight = sectionRow.offsetHeight;

          // Set explicit height to allow for transition
          sectionWrapper.style.height = `${currentHeight}px`;

          // Force a reflow to ensure the browser recognizes the style change
          void sectionWrapper.offsetHeight;

          // Set the target height (just the row)
          sectionWrapper.style.height = `${rowHeight}px`;

          // After animation completes, collapse the section and reset height to auto
          setTimeout(() => {
            setExpandedSection(null);

            // Wait a bit more to reset the height to auto
            setTimeout(() => {
              sectionWrapper.style.height = 'auto';

              // Reset animating flag
              setAnimatingLayout(false);
            }, 50);
          }, 300);
        } else {
          setExpandedSection(null);
          setAnimatingLayout(false);
        }
      } else {
        // If another section is already expanded, collapse it first
        if (expandedSection) {
          const previousWrapper = document.querySelector(`.section-wrapper[data-id="${expandedSection}"]`);
          const previousRow = previousWrapper?.querySelector('.section-row');
          const previousContainer = previousWrapper?.querySelector('.subsections-container');

          if (previousWrapper && previousRow && previousContainer) {
            // Set fixed height for transition
            previousWrapper.style.height = `${previousWrapper.offsetHeight}px`;

            // Force reflow
            void previousWrapper.offsetHeight;

            // Add collapse animation
            previousContainer.classList.add('collapse');

            // Set height to just the row
            previousWrapper.style.height = `${previousRow.offsetHeight}px`;

            // After animation, prepare to expand the new section
            setTimeout(() => {
              // Reset previous section
              previousWrapper.style.height = 'auto';
              previousContainer.classList.remove('collapse');

              // Setup and expand the new section
              expandNewSection(sectionId);
            }, 300);
          } else {
            // Previous section doesn't have the expected structure,
            // just expand the new section
            expandNewSection(sectionId);
          }
        } else {
          // No section is expanded, just expand the new one
          expandNewSection(sectionId);
        }
      }
    }
  };

  // Helper function to expand a section
  const expandNewSection = (sectionId) => {
    const sectionWrapper = document.querySelector(`.section-wrapper[data-id="${sectionId}"]`);
    const sectionRow = sectionWrapper.querySelector('.section-row');

    // Set initial height to just the row height
    const rowHeight = sectionRow.offsetHeight;
    sectionWrapper.style.height = `${rowHeight}px`;

    // Update the state to show the subsections
    setExpandedSection(sectionId);

    // After the DOM updates with the expanded content, transition to the full height
    setTimeout(() => {
      const newHeight = sectionRow.offsetHeight + 
        (sectionWrapper.querySelector('.subsections-container')?.offsetHeight || 0);
      sectionWrapper.style.height = `${newHeight}px`;

      // Reset to auto after transition completes
      setTimeout(() => {
        sectionWrapper.style.height = 'auto';

        // Reset animating flag
        setAnimatingLayout(false);
      }, 400);
    }, 50);
  };

  // Handle subsection hover/click - auto-expand
  const handleSubsectionHover = (sectionId, event) => {
    // Make sure we have a valid event object
    if (event) {
      event.stopPropagation();
    }

    const isClickEvent = event.type === 'click';

    if (isClickEvent) {
      setAnimatingLayout(true);
    }

    // If currently expanded and clicking the same subsection, add collapse animation first
    if (expandedSubsection === sectionId && isClickEvent) {
      const container = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"] .nested-subsections-container`);
      if (container) {
        // Add the collapse class to trigger the animation
        container.classList.add('collapse');

        // Calculate the current height of the subsection wrapper for smooth transition
        const subsectionWrapper = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"]`);
        const subsectionRow = subsectionWrapper.querySelector('.subsection-row');
        const currentHeight = subsectionWrapper.offsetHeight;
        const rowHeight = subsectionRow.offsetHeight;

        // Set explicit height to allow for transition
        subsectionWrapper.style.height = `${currentHeight}px`;

        // Force a reflow to ensure the browser recognizes the style change
        void subsectionWrapper.offsetHeight;

        // Set the target height (just the row)
        subsectionWrapper.style.height = `${rowHeight}px`;

        // After animation completes, collapse the subsection and reset height to auto
        setTimeout(() => {
          setExpandedSubsection(null);
          setTimeout(() => {
            subsectionWrapper.style.height = 'auto';

            if (isClickEvent) {
              setAnimatingLayout(false);
            }
          }, 50);
        }, 300);
      } else {
        setExpandedSubsection(null);
        if (isClickEvent) {
          setAnimatingLayout(false);
        }
      }
    } else {
      // For hover or first click, expand the subsection
      if (sectionId !== expandedSubsection) {
        // If another subsection is expanded, collapse it first
        if (expandedSubsection && isClickEvent) {
          const previousWrapper = document.querySelector(`.subsection-wrapper[data-id="${expandedSubsection}"]`);
          const previousRow = previousWrapper?.querySelector('.subsection-row');
          const previousContainer = previousWrapper?.querySelector('.nested-subsections-container');

          if (previousWrapper && previousRow && previousContainer) {
            // Set fixed height for transition
            previousWrapper.style.height = `${previousWrapper.offsetHeight}px`;

            // Force reflow
            void previousWrapper.offsetHeight;

            // Add collapse animation
            previousContainer.classList.add('collapse');

            // Set height to just the row
            previousWrapper.style.height = `${previousRow.offsetHeight}px`;

            // After animation, prepare to expand the new subsection
            setTimeout(() => {
              // Reset previous subsection
              previousWrapper.style.height = 'auto';
              previousContainer.classList.remove('collapse');

              // Expand new subsection
              expandNewSubsection(sectionId, isClickEvent);
            }, 300);
          } else {
            // Previous subsection doesn't have the expected structure,
            // just expand the new subsection
            expandNewSubsection(sectionId, isClickEvent);
          }
        } else {
          // No subsection is expanded, just expand the new one
          expandNewSubsection(sectionId, isClickEvent);
        }
      }
    }
  };

  // Helper function to expand a subsection
  const expandNewSubsection = (sectionId, isClickEvent) => {
    const subsectionWrapper = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"]`);

    if (subsectionWrapper && isClickEvent) {
      const subsectionRow = subsectionWrapper.querySelector('.subsection-row');

      // Set initial height to just the row height
      const rowHeight = subsectionRow.offsetHeight;
      subsectionWrapper.style.height = `${rowHeight}px`;
    }

    // Update the state to show the nested subsections
    setExpandedSubsection(sectionId);

    // After the DOM updates with the expanded content, transition to the full height
    if (isClickEvent) {
      setTimeout(() => {
        const subsectionWrapper = document.querySelector(`.subsection-wrapper[data-id="${sectionId}"]`);
        if (subsectionWrapper) {
          const subsectionRow = subsectionWrapper.querySelector('.subsection-row');
          const newHeight = subsectionRow.offsetHeight + 
            (subsectionWrapper.querySelector('.nested-subsections-container')?.offsetHeight || 0);
          subsectionWrapper.style.height = `${newHeight}px`;

          // Reset to auto after transition completes
          setTimeout(() => {
            subsectionWrapper.style.height = 'auto';
            setAnimatingLayout(false);
          }, 400);
        }
      }, 50);
    }
  };

  // Handle section leave
  const handleSectionLeave = () => {
    setActiveSection(null);
  };

  // Render SVG icons for main sections
  const renderIcon = (iconType) => {
    switch(iconType) {
      case 'circles':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="16" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
              <circle cx="26" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
            </svg>
          </div>
        );
      case 'triangle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M21 7L35 32H7L21 7Z" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
            </svg>
          </div>
        );
      case 'targetCircle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="21" cy="21" r="5" fill="#A08E61" opacity="0.7" />
            </svg>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div 
      ref={sectionsContainerRef}
      className={`sections-container ${animatingLayout ? 'animating' : ''}`}
    >
      {mainSections.map((section, index) => (
        <div 
          key={section.id} 
          className={`section-wrapper ${expandedSection === section.id ? 'expanded-section' : ''}`} 
          data-id={section.id}
        >
          <div 
            className={`section-row ${activeSection === section.id ? 'active' : ''} ${expandedSection === section.id ? 'expanded' : ''}`}
            onMouseEnter={() => handleSectionHover(section.id)}
            onMouseLeave={handleSectionLeave}
            onClick={(e) => {
              if (section.expandable) {
                handleSectionClick(section.id, e);
              } else {
                handleNavigate(section.path, e);
              }
            }}
            style={{ animationDelay: `${index * 0.15 + 0.3}s` }}
          >
            <div className="section-content">
              <div className="section-icon-wrapper">
                {renderIcon(section.icon)}
              </div>
              <div className="section-text">
                <h2 className="section-title">{section.title}</h2>
                <p className="section-description">{section.description}</p>
              </div>
              {section.expandable && (
                <div className={`expand-indicator ${expandedSection === section.id ? 'expanded' : ''}`}>
                  <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
                  </svg>
                </div>
              )}
            </div>
            <div className="diagonal-cut" />
          </div>

          {/* Expandable Subsections */}
          {section.expandable && expandedSection === section.id && (
            <div className="subsections-container">
              {section.subsections.map((subsection) => (
                <div 
                  key={subsection.id} 
                  className={`subsection-wrapper ${expandedSubsection === subsection.id ? 'expanded-subsection' : ''}`} 
                  data-id={subsection.id}
                >
                  <div 
                    className={`subsection-row ${expandedSubsection === subsection.id ? 'active' : ''}`}
                    onMouseEnter={(e) => handleSubsectionHover(subsection.id, e)}
                    onClick={(e) => subsection.expandable ? handleSubsectionHover(subsection.id, e) : handleNavigate(subsection.path, e)}
                  >
                    <div className="subsection-content">
                      <h3 className="subsection-title">{subsection.title}</h3>
                      <p className="subsection-description">{subsection.description}</p>
                      {subsection.expandable && (
                        <div className={`expand-indicator small ${expandedSubsection === subsection.id ? 'expanded' : ''}`}>
                          <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
                          </svg>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Nested Subsections - automatically expanded on hover */}
                  {subsection.expandable && expandedSubsection === subsection.id && (
                    <div className="nested-subsections-container">
                      {subsection.subsections.map((nestedSubsection) => (
                        <div 
                          key={nestedSubsection.id}
                          className="nested-subsection-row"
                          onClick={(e) => handleNavigate(nestedSubsection.path, e)}
                        >
                          <div className="nested-subsection-content">
                            <h4 className="nested-subsection-title">{nestedSubsection.title}</h4>
                            <p className="nested-subsection-description">{nestedSubsection.description}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default LandingNavigation;
