import React, { useState, useEffect, useRef } from 'react';

const EnhancedLandingPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [introComplete, setIntroComplete] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(0);
  const [introOpacity, setIntroOpacity] = useState(1);

  const canvasRef = useRef(null);
  const moonCanvasRef = useRef(null);
  const particlesRef = useRef([]);
  const requestRef = useRef(null);
  const moonAnimationRef = useRef(null);

  // Poetic quotes by women poets capturing the dark fantasy theme
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend —\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "Because I could not stop for Death –\nHe kindly stopped for me –\nThe Carriage held but just Ourselves –\nAnd Immortality.",
      author: "Emily Dickinson"
    }
  ];

  const sections = [
    {
      id: "creator",
      title: "Creator",
      description: "The mind behind the mysteries, shaping worlds from shadow and light.",
      icon: "◉", // Circle symbol
      path: "/creator"
    },
    {
      id: "creativity",
      title: "Creativity",
      description: "Visualizations of complex systems, revealing patterns beneath the chaos.",
      icon: "△", // Triangle symbol
      path: "/creativity"
    },
    {
      id: "cycles",
      title: "Cycles",
      description: "An intricate framework modeling the evolution of worlds through endless creation and destruction.",
      icon: "⌁", // Lightning symbol
      path: "/cycles"
    },
    {
      id: "codex",
      title: "Codex",
      description: "Repository of arcane knowledge, rules, and lore from across the multiverse.",
      icon: "⊕", // Circled plus symbol
      path: "/codex"
    },
    {
      id: "characters",
      title: "Characters",
      description: "Beings shaped by their environments, each with a unique story to tell.",
      icon: "✧", // Star symbol
      path: "/characters"
    }
  ];

  // Initialize the intro animation
  useEffect(() => {
    // Choose a random quote to display
    setCurrentQuote(Math.floor(Math.random() * quotes.length));

    // Automatically skip intro if user preference is set
    const hasSeenIntro = localStorage.getItem('hasSeenIntro');
    if (hasSeenIntro) {
      setIntroComplete(true);
      setIsLoaded(true);
      return;
    }

    // Start intro animation
    setTimeout(() => {
      // Fade out intro
      setIntroOpacity(0);

      // Mark intro as complete
      setTimeout(() => {
        setIntroComplete(true);
        setIsLoaded(true);
        localStorage.setItem('hasSeenIntro', 'true');
      }, 1000); // Fade duration
    }, 3500); // Intro duration

    // Initialize moon animation
    if (moonCanvasRef.current) {
      initMoonAnimation();
    }

    // Allow skipping with any key or click
    const skipIntro = () => {
      setIntroOpacity(0);
      setTimeout(() => {
        setIntroComplete(true);
        setIsLoaded(true);
        localStorage.setItem('hasSeenIntro', 'true');
      }, 500);
    };

    window.addEventListener('keydown', skipIntro);
    window.addEventListener('click', skipIntro);

    return () => {
      window.removeEventListener('keydown', skipIntro);
      window.removeEventListener('click', skipIntro);

      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, []);

  // Initialize particles
  useEffect(() => {
    if (introComplete) {
      particlesRef.current = Array(50).fill().map(() => ({
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        size: Math.random() * 2 + 1,
        speedX: (Math.random() - 0.5) * 0.3,
        speedY: (Math.random() - 0.5) * 0.3,
        opacity: Math.random() * 0.5 + 0.1
      }));
    }

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, [introComplete]);

  // Canvas animation for background
  useEffect(() => {
    if (!introComplete || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight * 2; // Double height for scrolling

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles
      particlesRef.current.forEach(particle => {
        // Update position
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // Wrap around screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${particle.opacity})`;
        ctx.fill();
      });

      // Draw cursor influence
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 150
        );
        gradient.addColorStop(0, 'rgba(160, 142, 97, 0.1)');
        gradient.addColorStop(1, 'rgba(160, 142, 97, 0)');

        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 150, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      // Draw large moon at the bottom
      const moonX = canvas.width / 2;
      const moonY = canvas.height - 300;
      const moonRadius = 150;

      // Moon glow
      const moonGlow = ctx.createRadialGradient(
        moonX, moonY, moonRadius * 0.9,
        moonX, moonY, moonRadius * 2.5
      );
      moonGlow.addColorStop(0, 'rgba(191, 173, 127, 0.2)');
      moonGlow.addColorStop(1, 'rgba(191, 173, 127, 0)');

      ctx.beginPath();
      ctx.arc(moonX, moonY, moonRadius * 2.5, 0, Math.PI * 2);
      ctx.fillStyle = moonGlow;
      ctx.fill();

      // Moon itself
      const moonGradient = ctx.createRadialGradient(
        moonX - moonRadius * 0.3, moonY - moonRadius * 0.3, 0,
        moonX, moonY, moonRadius
      );
      moonGradient.addColorStop(0, 'rgba(191, 173, 127, 0.9)');
      moonGradient.addColorStop(0.8, 'rgba(160, 142, 97, 0.7)');
      moonGradient.addColorStop(1, 'rgba(160, 142, 97, 0.6)');

      ctx.beginPath();
      ctx.arc(moonX, moonY, moonRadius, 0, Math.PI * 2);
      ctx.fillStyle = moonGradient;
      ctx.fill();

      // Moon craters
      const craters = [
        { x: moonX - 40, y: moonY - 50, r: 15 },
        { x: moonX + 60, y: moonY - 20, r: 20 },
        { x: moonX - 20, y: moonY + 60, r: 12 },
        { x: moonX + 30, y: moonY + 40, r: 10 }
      ];

      craters.forEach(crater => {
        ctx.beginPath();
        ctx.arc(crater.x, crater.y, crater.r, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(140, 122, 77, 0.6)';
        ctx.fill();
      });

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight * 2;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [introComplete, cursorPosition]);

  // Moon animation for intro
  const initMoonAnimation = () => {
    const canvas = moonCanvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    let phase = 0;

    const animateMoon = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      const centerX = canvas.width / 2;
      const centerY = canvas.height / 2;
      const radius = Math.min(canvas.width, canvas.height) * 0.15;

      // Moon glow
      const outerGlow = ctx.createRadialGradient(
        centerX, centerY, radius,
        centerX, centerY, radius * 3
      );
      outerGlow.addColorStop(0, 'rgba(191, 173, 127, 0.2)');
      outerGlow.addColorStop(1, 'rgba(191, 173, 127, 0)');

      ctx.beginPath();
      ctx.arc(centerX, centerY, radius * 3, 0, Math.PI * 2);
      ctx.fillStyle = outerGlow;
      ctx.fill();

      // Moon gradient
      const moonGradient = ctx.createRadialGradient(
        centerX - radius * 0.3, centerY - radius * 0.3, 0,
        centerX, centerY, radius
      );
      moonGradient.addColorStop(0, 'rgba(191, 173, 127, 0.9)');
      moonGradient.addColorStop(0.8, 'rgba(160, 142, 97, 0.8)');
      moonGradient.addColorStop(1, 'rgba(160, 142, 97, 0.7)');

      // Draw moon with phase
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
      ctx.fillStyle = moonGradient;
      ctx.fill();

      // Create phase effect (waxing crescent)
      const phaseOffset = radius * Math.sin(phase);
      ctx.beginPath();
      ctx.arc(centerX + phaseOffset, centerY, radius, -Math.PI/2, Math.PI/2);
      ctx.arc(centerX, centerY, radius, Math.PI/2, -Math.PI/2);
      ctx.fillStyle = 'rgba(8, 8, 8, 0.9)';
      ctx.fill();

      // Update phase
      phase += 0.03;
      if (phase < Math.PI) {
        moonAnimationRef.current = requestAnimationFrame(animateMoon);
      }
    };

    animateMoon();
  };

  // Track mouse movement
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Navigate to section
  const handleNavigate = (path) => {
    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  return (
    <div 
      className="app-wrapper"
      onMouseMove={handleMouseMove}
    >
      {/* Intro animation */}
      {!introComplete && (
        <div 
          className="intro-container"
          style={{ opacity: introOpacity }}
        >
          <canvas ref={moonCanvasRef} className="moon-canvas" />

          <div className="quote-container">
            <p className="quote-text">
              {quotes[currentQuote].text.split('\n').map((line, i) => (
                <React.Fragment key={i}>
                  {line}
                  <br />
                </React.Fragment>
              ))}
            </p>
            <p className="quote-author">— {quotes[currentQuote].author}</p>
          </div>

          <div className="skip-text">Click or press any key to skip</div>
        </div>
      )}

      {/* Main content */}
      <div className="landing-container">
        <canvas ref={canvasRef} className="background-canvas" />

        <div className={`content-container ${isLoaded ? 'loaded' : ''}`}>
          <div className="header-section">
            <h1 className="main-title">CRESCENT</h1>
            <div className="title-divider" />
            <p className="subtitle">A Dark Fantasy Experience</p>
          </div>

          <div className="sections-container">
            {sections.map((section, index) => (
              <div 
                key={section.id}
                className={`section-row ${activeSection === section.id ? 'active' : ''}`}
                onMouseEnter={() => setActiveSection(section.id)}
                onMouseLeave={() => setActiveSection(null)}
                onClick={() => handleNavigate(section.path)}
                style={{ animationDelay: `${index * 0.15}s` }}
              >
                <div className="section-indicator" />

                <div className="section-content">
                  <div className="section-icon-wrapper">
                    <div className="section-icon">{section.icon}</div>
                  </div>
                  <div className="section-text">
                    <h2 className="section-title">{section.title}</h2>
                    <p className="section-description">{section.description}</p>
                  </div>
                </div>
                <div className="diagonal-cut" />
              </div>
            ))}
          </div>

          <div className="scroll-indicator">
            <div className="scroll-icon">▼</div>
            <div className="scroll-text">Scroll to discover</div>
          </div>
        </div>

        <div className="brand-mark">
          <span className="symbol">◊</span>
          <span className="year">2025</span>
        </div>
      </div>

      <style jsx>{`
