import React, { useRef, useState, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
  profile: {
    backgroundColor: 'rgba(15, 15, 15, 0.7)',
    border: '1px solid rgba(160, 142, 97, 0.2)',
    borderRadius: '3px',
    padding: '3rem',
    margin: '4rem auto',
    maxWidth: '1000px',
    display: 'grid',
    gridTemplateColumns: '1fr 2fr',
    gap: '3rem',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
    position: 'relative',
    '@media (max-width: 768px)': {
      gridTemplateColumns: '1fr',
      padding: '2rem'
    }
  },
  profileBorder: {
    '&::before': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      width: 0,
      height: '1px',
      background: 'linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0))',
      transition: 'width 1s ease 0.5s'
    },
    '&$inView::before': {
      width: '100%'
    }
  },
  inView: {},
  profileImageContainer: {
    position: 'relative',
    width: '100%',
    aspectRatio: '1/1',
    borderRadius: '3px',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    transition: 'box-shadow 0.3s ease',
    '&:hover': {
      boxShadow: '0 5px 20px rgba(191, 173, 127, 0.4)',
      '& $cometCanvas': {
        filter: 'brightness(1.5) contrast(1.2)'
      }
    },
    '@media (max-width: 768px)': {
      maxWidth: '250px',
      margin: '0 auto'
    }
  },
  cometCanvasContainer: {
    position: 'absolute',
    top: -20,
    left: -20,
    right: -20,
    bottom: -20,
    pointerEvents: 'none',
    zIndex: 10
  },
  cometCanvas: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    transition: 'filter 0.3s ease'
  },
  profileImage: {
    position: 'relative',
    width: '100%',
    height: '100%',
    borderRadius: '3px',
    overflow: 'hidden',
    '&::after': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0.3)',
      borderRadius: '3px',
      pointerEvents: 'none'
    }
  },
  imageWrapper: {
    position: 'relative',
    width: '100%',
    height: '100%',
    overflow: 'hidden',
    borderRadius: '3px'
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transition: 'transform 0.6s ease, object-position 0.6s ease'
  },
  profileDetails: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    '@media (max-width: 768px)': {
      textAlign: 'center'
    }
  },
  profileName: {
    fontSize: '2.2rem',
    color: '#bfad7f',
    fontWeight: 300,
    letterSpacing: '0.1em',
    marginBottom: '0.5rem',
    '@media (max-width: 480px)': {
      fontSize: '1.8rem'
    }
  },
  profileRole: {
    fontSize: '1.1rem',
    color: 'rgba(191, 173, 127, 0.7)',
    marginBottom: '2rem',
    letterSpacing: '0.05em',
    fontWeight: 300,
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontStyle: 'italic'
  },
  profileBio: {
    fontSize: '1rem',
    color: 'rgba(224, 224, 224, 0.7)',
    lineHeight: 1.8,
    marginBottom: '2rem',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300
  },
  profileStats: {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '1.5rem',
    marginTop: '2rem',
    borderTop: '1px solid rgba(160, 142, 97, 0.2)',
    paddingTop: '1.5rem',
    '@media (max-width: 480px)': {
      gridTemplateColumns: '1fr',
      gap: '1.5rem'
    }
  },
  stat: {
    textAlign: 'center'
  },
  statValue: {
    fontSize: '2.5rem',
    color: 'rgba(191, 173, 127, 0.9)',
    fontWeight: 300
  },
  statLabel: {
    fontSize: '0.85rem',
    color: 'rgba(224, 224, 224, 0.5)',
    textTransform: 'uppercase',
    letterSpacing: '0.1em',
    marginTop: '0.5rem'
  },
  imageControls: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.75rem',
    marginTop: '1rem',
    width: '100%'
  },
  controlGroup: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%'
  },
  controlLabel: {
    fontSize: '0.8rem',
    color: 'rgba(191, 173, 127, 0.7)',
    letterSpacing: '0.05em',
  },
  controlSlider: {
    flex: 1,
    maxWidth: '75%',
    height: '0.25rem',
    borderRadius: '1rem',
    appearance: 'none',
    backgroundColor: 'rgba(191, 173, 127, 0.2)',
    outline: 'none',
    '&::-webkit-slider-thumb': {
      appearance: 'none',
      width: '0.75rem',
      height: '0.75rem',
      borderRadius: '50%',
      backgroundColor: 'rgba(191, 173, 127, 0.8)',
      cursor: 'pointer'
    },
    '&::-moz-range-thumb': {
      width: '0.75rem',
      height: '0.75rem',
      borderRadius: '50%',
      backgroundColor: 'rgba(191, 173, 127, 0.8)',
      cursor: 'pointer',
      border: 'none'
    }
  },
  controlButton: {
    background: 'none',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: 'rgba(191, 173, 127, 0.7)',
    padding: '0.25rem 0.5rem',
    fontSize: '0.7rem',
    borderRadius: '2px',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    marginTop: '0.5rem',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.1)',
      color: 'rgba(191, 173, 127, 0.9)'
    }
  }
});

// Orbiting Comet Animation Component
const CometBorderAnimation = ({ isHovered = false, config = {} }) => {
  const canvasRef = useRef(null);
  const requestRef = useRef(null);
  const containerRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  // Initialize the animation parameters
  const cometPositionRef = useRef(0);
  const lastTimeRef = useRef(0);

  // Default configuration with prop overrides
  const {
    size = 6,
    trailLength = 140,
    speed = 0.08,
    hoverSpeedMultiplier = 2,
    trailSegments = 25,
    glowIntensity = 0.8
  } = config;

  // Set up dimensions on mount
  useEffect(() => {
    if (containerRef.current) {
      const updateDimensions = () => {
        const width = containerRef.current.offsetWidth;
        const height = containerRef.current.offsetHeight;
        setDimensions({ width, height });
      };

      updateDimensions();
      window.addEventListener('resize', updateDimensions);

      return () => {
        window.removeEventListener('resize', updateDimensions);
      };
    }
  }, []);

  // Run animation
  useEffect(() => {
    if (!canvasRef.current || dimensions.width === 0) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas dimensions
    canvas.width = dimensions.width;
    canvas.height = dimensions.height;

    const rectWidth = dimensions.width - 40;  // Leaving 20px padding on each side
    const rectHeight = dimensions.height - 40;

    // Calculate the perimeter of the rectangle
    const perimeter = 2 * (rectWidth + rectHeight);

    // Comet parameters with hover enhancement
    const cometSize = isHovered ? size * 1.2 : size;
    const actualTrailLength = isHovered ? trailLength * 1.2 : trailLength;
    const actualSpeed = isHovered ? speed * hoverSpeedMultiplier : speed;
    const actualGlowIntensity = isHovered ? glowIntensity * 1.5 : glowIntensity;

    const animateComet = (timestamp) => {
      if (!lastTimeRef.current) {
        lastTimeRef.current = timestamp;
      }

      const deltaTime = timestamp - lastTimeRef.current;
      lastTimeRef.current = timestamp;

      // Calculate speed and update position
      const speed = actualSpeed;
      cometPositionRef.current = (cometPositionRef.current + speed * deltaTime) % perimeter;

      // Clear the canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Function to calculate x,y from position along perimeter
      // Position the comet directly on top of the border
      const getPointOnRectangle = (pos) => {
        const normalizedPos = pos % perimeter;
        const offset = 0; // Position directly on the border

        // Top edge
        if (normalizedPos < rectWidth) {
          return { x: 20 + normalizedPos, y: 20 };
        }
        // Right edge
        else if (normalizedPos < rectWidth + rectHeight) {
          return { x: 20 + rectWidth, y: 20 + (normalizedPos - rectWidth) };
        }
        // Bottom edge
        else if (normalizedPos < 2 * rectWidth + rectHeight) {
          return { x: 20 + rectWidth - (normalizedPos - (rectWidth + rectHeight)), y: 20 + rectHeight };
        }
        // Left edge
        else {
          return { x: 20, y: 20 + rectHeight - (normalizedPos - (2 * rectWidth + rectHeight)) };
        }
      };

      // Draw trail segments
      for (let i = 0; i < trailSegments; i++) {
        const segmentPos = (cometPositionRef.current - (i * (actualTrailLength / trailSegments)) + perimeter) % perimeter;
        const nextSegmentPos = (cometPositionRef.current - ((i + 1) * (actualTrailLength / trailSegments)) + perimeter) % perimeter;

        const point = getPointOnRectangle(segmentPos);
        const nextPoint = getPointOnRectangle(nextSegmentPos);

        // Calculate opacity based on position in trail
        const baseOpacity = 0.9 * (1 - (i / trailSegments));

        // Draw trail segment
        ctx.beginPath();
        ctx.moveTo(point.x, point.y);
        ctx.lineTo(nextPoint.x, nextPoint.y);

        // Vary line width from head to tail
        const segmentWidth = 3 * (1 - (i / trailSegments)) + 0.5;

        // Set shadow/glow for trail
        ctx.shadowColor = `rgba(255, 253, 227, ${baseOpacity * actualGlowIntensity})`;
        ctx.shadowBlur = (12 * (1 - (i / trailSegments)) + 5) * actualGlowIntensity;

        // Set line style and draw
        ctx.strokeStyle = `rgba(191, 173, 127, ${baseOpacity})`;
        ctx.lineWidth = segmentWidth;
        ctx.lineCap = 'round';
        ctx.stroke();
      }

      // Draw comet head
      const headPoint = getPointOnRectangle(cometPositionRef.current);

      // Add additional stars along the path
      if (isHovered) {
        for (let i = 0; i < 8; i++) {
          const starPos = (cometPositionRef.current + (i * 70) + perimeter) % perimeter;
          const starPoint = getPointOnRectangle(starPos);

          // Draw twinkling star with animation
          const twinkle = (Math.sin(timestamp / 500 + i) + 1) / 2; // Value between 0 and 1

          ctx.beginPath();
          ctx.arc(starPoint.x, starPoint.y, 1.5 * twinkle, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(255, 255, 255, ${0.4 * twinkle})`;
          ctx.shadowColor = 'rgba(255, 253, 227, 0.8)';
          ctx.shadowBlur = 5 * twinkle;
          ctx.fill();
        }
      }

      // Larger outer glow for comet head
      ctx.beginPath();
      ctx.arc(headPoint.x, headPoint.y, cometSize * 1.5, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${0.4 * actualGlowIntensity})`;
      ctx.shadowColor = `rgba(255, 253, 227, ${0.6 * actualGlowIntensity})`;
      ctx.shadowBlur = 15 * actualGlowIntensity;
      ctx.fill();

      // Draw comet head
      ctx.beginPath();
      ctx.arc(headPoint.x, headPoint.y, cometSize, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${0.6 * actualGlowIntensity})`;
      ctx.shadowColor = `rgba(255, 253, 227, ${0.7 * actualGlowIntensity})`;
      ctx.shadowBlur = 12 * actualGlowIntensity;
      ctx.fill();

      // Brightest center point (smaller than before)
      ctx.beginPath();
      ctx.arc(headPoint.x, headPoint.y, cometSize * 0.4, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 255, 255, ${0.8 * actualGlowIntensity})`;
      ctx.shadowColor = `rgba(255, 255, 255, ${0.9 * actualGlowIntensity})`;
      ctx.shadowBlur = 8 * actualGlowIntensity;
      ctx.fill();

      // Draw subtle ambient star field if hovered
      if (isHovered) {
        for (let i = 0; i < 20; i++) {
          const x = 20 + Math.random() * rectWidth;
          const y = 20 + Math.random() * rectHeight;
          const size = Math.random() * 1;
          const opacity = Math.random() * 0.3;

          ctx.beginPath();
          ctx.arc(x, y, size, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(255, 255, 255, ${opacity})`;
          ctx.fill();
        }
      }

      requestRef.current = requestAnimationFrame(animateComet);
    };

    // Start animation
    requestRef.current = requestAnimationFrame(animateComet);

    // Cleanup
    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, [dimensions, isHovered]);

  return (
    <div ref={containerRef} className="comet-border-container" style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
      <canvas ref={canvasRef} className="comet-border-canvas" style={{ position: 'absolute', top: 0, left: 0 }} />
    </div>
  );
};

/**
 * Enhanced PersonProfileCard Component with Orbiting Comet Animation
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string[]} props.person.bio - Array of bio paragraphs
 * @param {Object[]} props.person.stats - Array of statistic objects
 * @param {Object} [props.animationConfig] - Optional animation configuration
 * @param {Object} [props.cometConfig] - Optional comet configuration
 * @param {number} [props.cometConfig.size=6] - Size of the comet head (px)
 * @param {number} [props.cometConfig.trailLength=140] - Length of the comet trail
 * @param {number} [props.cometConfig.speed=0.08] - Base speed of the comet
 * @param {boolean} [props.showImageControls=false] - Whether to show image positioning controls
 */
const PersonProfileCard = ({ 
  person,
  animationConfig = {
    useInternalRef: true,
    threshold: 0.2,
    once: true,
    initialY: 50,
    duration: 0.8,
    ease: "easeOut"
  },
  cometConfig = {
    size: 2,
    trailLength: 180,
    speed: 0.08
  },
  showImageControls = false
}) => {
  const classes = useStyles();
  const [isHovered, setIsHovered] = useState(false);

  // Set up ref and inView detection
  const internalRef = useRef(null);

  // Image positioning state
  const [imagePosition, setImagePosition] = useState({
    x: 50, // 0-100, represents position in percentage
    y: 50,
    scale: 1, // 1-1.5
  });

  // Always call useInView (to follow React hook rules)
  const internalInView = useInView(
    internalRef, 
    { 
      once: animationConfig.once, 
      threshold: animationConfig.threshold 
    }
  );

  // Use either internal or external ref/inView based on config
  const ref = animationConfig.useInternalRef ? internalRef : animationConfig.ref;
  const isInView = animationConfig.useInternalRef ? internalInView : animationConfig.isInView;

  // Reset image position
  const resetImagePosition = () => {
    setImagePosition({
      x: 50,
      y: 50,
      scale: 1
    });
  };

  return (
    <motion.div 
      ref={ref}
      className={`${classes.profile} ${classes.profileBorder} ${isInView ? classes.inView : ''}`}
      initial={{ opacity: 0, y: animationConfig.initialY }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig.initialY }}
      transition={{ 
        duration: animationConfig.duration, 
        ease: animationConfig.ease 
      }}
    >
      <div 
        className={classes.profileImageContainer}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        {/* Comet animation around the border */}
        <div className={classes.cometCanvasContainer}>
          <CometBorderAnimation 
            isHovered={isHovered} 
            config={cometConfig}
          />
        </div>

        <div className={classes.profileImage}>
          <div className={classes.imageWrapper}>
            <img 
              src={person.image} 
              alt={person.name} 
              className={classes.image}
              style={{
                objectPosition: `${imagePosition.x}% ${imagePosition.y}%`,
                transform: `scale(${imagePosition.scale})`
              }}
            />
          </div>
        </div>

        {/* Image position controls */}
        {showImageControls && (
          <div className={classes.imageControls}>
            <div className={classes.controlGroup}>
              <span className={classes.controlLabel}>Horizontal</span>
              <input 
                type="range" 
                min="0" 
                max="100" 
                value={imagePosition.x}
                onChange={(e) => setImagePosition({...imagePosition, x: Number(e.target.value)})}
                className={classes.controlSlider}
              />
            </div>

            <div className={classes.controlGroup}>
              <span className={classes.controlLabel}>Vertical</span>
              <input 
                type="range" 
                min="0" 
                max="100" 
                value={imagePosition.y}
                onChange={(e) => setImagePosition({...imagePosition, y: Number(e.target.value)})}
                className={classes.controlSlider}
              />
            </div>

            <div className={classes.controlGroup}>
              <span className={classes.controlLabel}>Zoom</span>
              <input 
                type="range" 
                min="100" 
                max="150" 
                value={imagePosition.scale * 100}
                onChange={(e) => setImagePosition({...imagePosition, scale: Number(e.target.value) / 100})}
                className={classes.controlSlider}
              />
            </div>

            <button 
              onClick={resetImagePosition} 
              className={classes.controlButton}
            >
              Reset
            </button>
          </div>
        )}
      </div>

      <div className={classes.profileDetails}>
        <h2 className={classes.profileName}>{person.name}</h2>
        <p className={classes.profileRole}>{person.role}</p>

        {person.bio.map((paragraph, index) => (
          <p key={index} className={classes.profileBio}>{paragraph}</p>
        ))}

        {person.stats && person.stats.length > 0 && (
          <div className={classes.profileStats}>
            {person.stats.map((stat, index) => (
              <div key={index} className={classes.stat}>
                <div className={classes.statValue}>{stat.value}</div>
                <div className={classes.statLabel}>{stat.label}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
